css:
.sheet-toolbar {
  position: relative;
  -webkit-app-region: drag;
  border-bottom: 1px solid #1E1E2E;
}
.column-MessageListSidebar {
  border-left: transparent;
}
.flexbox-handle-horizontal div {
  height: 100%;
  box-shadow: transparent;
}
#inbox-html-wrapper > :not(table) a,
#inbox-plain-wrapper > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a {
  color: #313244;
}
/* Badge with counter, secondary */
.item-count-box:not(.selected):not(.focused) {
  color: #1E1E2E;
  box-shadow: 0 0.5px 0 #181825, 0 -0.5px 0 #181825, 0.5px 0 0 #181825, -0.5px 0 0 #181825;
  background: #B4BEFE;
}
/* Badge with counter, primary, selected */
.outline-view .item .item-count-box.alt-count {
  color: #B4BEFE;
  background: #1E1E2E;
  box-shadow: none;
}
/* Badge with counter, primary, not selected */
.outline-view .item .item-count-box.alt-count:not(.selected):not(.focused) {
  color: #1E1E2E;
  background: #B4BEFE;
  box-shadow: none;
}
/* tracking icon, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, unopened, focused */
.list-item.focused .open-tracking-icon img.content-mask.unopened,
.list-item.selected .open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, opened, unfocused */
.open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, opened, focused */
.list-item.focused .open-tracking-icon img.content-mask.opened,
.list-item.selected .open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, message list, opened */
.open-tracking-message-status.opened img.content-mask {
  background-color: #CDD6F4;
}
/* tracking icon, mail list, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
.toolbar-activity .unread-count.active {
  background: #B4BEFE;
  color: #1E1E2E;
}
.open-tracking-message-status.opened {
  color: #CDD6F4;
}
.open-tracking-message-status.unopened {
  color: #7F849C;
}
.account-switcher img {
  zoom: 1 !important;
  max-width: 10px;
  max-height: 6px;
  transform: none;
  background-image: none;
  background-color: #7F849C;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="10" height="6" viewBox="0 0 10 6"><path fill="#FFF" d="M0 0h1l4 4 4-4h1v1L5 6 0 1z"/></svg>');
}
.toolbar-activity .activity-toolbar-icon {
  background: #A6ADC8;
}
.div.suggestions:hover {
  color: #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item,
.button-dropdown.btn-emphasis .secondary-picker,
.button-dropdown.btn-emphasis .only-item {
  position: relative;
  color: #1E1E2E;
  font-weight: 500;
  background: #B4BEFE;
  box-shadow: none;
  border: 0px solid #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item img.content-mask,
.button-dropdown.btn-emphasis .secondary-picker img.content-mask,
.button-dropdown.btn-emphasis .only-item img.content-mask {
  background-color: #1E1E2E;
}
.button-dropdown.btn-emphasis .primary-item:active,
.button-dropdown.btn-emphasis .secondary-picker:active,
.button-dropdown.btn-emphasis .only-item:active {
  background: #B4BEFE;
}
.tokenizing-field .token {
  background: linear-gradient(to bottom, #313244 0%, #45475A 100%);
}
.tokenizing-field .token.selected,
.tokenizing-field .token.dragging {
  color: #1E1E2E;
}
.tokenizing-field .token.selected .action img,
.tokenizing-field .token.dragging .action img {
  background-color: #1E1E2E;
}
.menu .item.selected,
.menu .item:active {
  background-color: #4e4947;
  color: #CDD6F4;
}
.div.button-dropdown.open.open-down {
  background-color: #CDD6F4;
  color: #CDD6F4;
}
.menu .item.selected,
.menu .item:active {
  background-color: #B4BEFE;
  color: #1E1E2E;
}
.menu .item.selected .primary,
.menu .item:active .primary {
  color: #1E1E2E;
}
.menu .item.selected .secondary,
.menu .item:active .secondary {
  color: #313244;
}
.button-dropdown .primary-item,
.button-dropdown .only-item {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown .secondary-picker {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown.open.open-down .secondary-items {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08), 0 0 3px #B4BEFE;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content.unfocused {
  background-color: #313244;
}
.unsubscribe-action {
  color: #B4BEFE;
  text-decoration: underline;
}
.undo-redo-toast .content {
  background: #B4BEFE;
}
.preferences-wrap .container-accounts .accounts-content .account-details {
  background-color: #1E1E2E;
}
.nylas-editable-list .items-wrapper {
  background-color: #313244;
}
.thread-injected-quick-actions .btn {
  box-shadow: none;
}
.thread-list .list-item .list-column-HoverActions .action.action-trash {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-archive {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url('../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .thread-icon.thread-icon-star,
.draft-list .thread-icon.thread-icon-star {
  filter: grayscale(100%);
}
#message-list .mail-label.removable {
  color: #1E1E2E !important;
  background: #B4BEFE !important;
  box-shadow: none !important;
}
.thread-list .thread-icon.thread-icon-unread,
.draft-list .thread-icon.thread-icon-unread {
  filter: grayscale(100%);
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(24, 24, 37, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(24, 24, 37, 0.8);
}
*:focus,
input:focus {
  outline: none;
}
.thread-list .list-container,
.draft-list .list-container,
.thread-list .scroll-region,
.draft-list .scroll-region {
  width: 100%;
  height: 100%;
  -webkit-font-smoothing: subpixel-antialiased;
}
.thread-list .swipe-backing,
.draft-list .swipe-backing {
  background-color: #0a0a0f;
}
.thread-list .swipe-backing::after,
.draft-list .swipe-backing::after {
  color: rgba(255, 255, 255, 0.9);
  padding-top: 45px;
  text-align: center;
  font-weight: 400;
  font-size: 13.02px;
  position: absolute;
  top: 0;
  transform: translateX(0%);
  width: 80px;
  bottom: 0;
  opacity: 0.8;
  transition: opacity linear 150ms;
  background-repeat: no-repeat;
  background-position: 50% 35%;
  background-size: 24px 24px;
}
.thread-list .swipe-backing.swipe-trash,
.draft-list .swipe-backing.swipe-trash {
  transition: background-color linear 150ms;
  background-color: #b92c44;
}
.thread-list .swipe-backing.swipe-trash::after,
.draft-list .swipe-backing.swipe-trash::after {
  transition: left linear 150ms, transform linear 150ms;
  content: 'Trash';
  left: 0;
  background-image: url(../static/images/swipe/icon-swipe-trash@2x.png);
}
.thread-list .swipe-backing.swipe-trash.confirmed,
.draft-list .swipe-backing.swipe-trash.confirmed {
  background-color: #ed304b;
}
.thread-list .swipe-backing.swipe-trash.confirmed::after,
.draft-list .swipe-backing.swipe-trash.confirmed::after {
  left: 100%;
  transform: translateX(-100%);
  opacity: 1;
}
.thread-list .swipe-backing.swipe-archive,
.draft-list .swipe-backing.swipe-archive,
.thread-list .swipe-backing.swipe-all,
.draft-list .swipe-backing.swipe-all {
  transition: background-color linear 150ms;
  background-color: #59a724;
}
.thread-list .swipe-backing.swipe-archive::after,
.draft-list .swipe-backing.swipe-archive::after,
.thread-list .swipe-backing.swipe-all::after,
.draft-list .swipe-backing.swipe-all::after {
  transition: left linear 150ms, transform linear 150ms;
  content: 'Archive';
  left: 0;
  background-image: url(../static/images/swipe/icon-swipe-archive@2x.png);
}
.thread-list .swipe-backing.swipe-archive.confirmed,
.draft-list .swipe-backing.swipe-archive.confirmed,
.thread-list .swipe-backing.swipe-all.confirmed,
.draft-list .swipe-backing.swipe-all.confirmed {
  background-color: #6cd420;
}
.thread-list .swipe-backing.swipe-archive.confirmed::after,
.draft-list .swipe-backing.swipe-archive.confirmed::after,
.thread-list .swipe-backing.swipe-all.confirmed::after,
.draft-list .swipe-backing.swipe-all.confirmed::after {
  left: 100%;
  transform: translateX(-100%);
  opacity: 1;
}
.thread-list .swipe-backing.swipe-snooze,
.draft-list .swipe-backing.swipe-snooze {
  transition: background-color linear 150ms;
  background-color: #7158b6;
}
.thread-list .swipe-backing.swipe-snooze::after,
.draft-list .swipe-backing.swipe-snooze::after {
  transition: right linear 150ms, transform linear 150ms;
  content: 'Snooze';
  right: 0;
  background-image: url(../static/images/swipe/icon-swipe-snooze@2x.png);
}
.thread-list .swipe-backing.swipe-snooze.confirmed,
.draft-list .swipe-backing.swipe-snooze.confirmed {
  background-color: #8d6be3;
}
.thread-list .swipe-backing.swipe-snooze.confirmed::after,
.draft-list .swipe-backing.swipe-snooze.confirmed::after {
  right: 100%;
  transform: translateX(100%);
  opacity: 1;
}
.thread-list .list-item,
.draft-list .list-item {
  background-color: #1a1a28;
  border-bottom: 1px solid rgba(49, 50, 68, 0.6);
  line-height: 36px;
}
.thread-list .mail-important-icon,
.draft-list .mail-important-icon {
  margin-left: 6px;
  padding: 12px;
  vertical-align: initial;
}
.thread-list .mail-important-icon:not(.active),
.draft-list .mail-important-icon:not(.active) {
  visibility: hidden;
}
.thread-list .message-count,
.draft-list .message-count {
  color: #CDD6F4;
  background: #313244;
  padding: 4px 6px 2px 6px;
  margin-left: 1em;
}
.thread-list .draft-icon,
.draft-list .draft-icon {
  margin-left: 10px;
  flex-shrink: 0;
  object-fit: contain;
}
.thread-list .participants,
.draft-list .participants {
  font-size: 13.02px;
  text-overflow: ellipsis;
  text-align: left;
  overflow: hidden;
}
.thread-list .participants.no-recipients,
.draft-list .participants.no-recipients {
  color: #7F849C;
}
.thread-list .details,
.draft-list .details {
  display: flex;
  align-items: center;
  overflow: hidden;
}
.thread-list .details .subject,
.draft-list .details .subject {
  font-size: 13.02px;
  font-weight: 400;
  padding-right: 12px;
  text-overflow: ellipsis;
  overflow: hidden;
  flex-shrink: 0.1;
}
.thread-list .details .snippet,
.draft-list .details .snippet {
  font-size: 13.02px;
  font-weight: 400;
  text-overflow: ellipsis;
  overflow: hidden;
  opacity: 0.62;
  flex: 1;
}
.thread-list .details .thread-icon,
.draft-list .details .thread-icon {
  margin-right: 12px;
  margin-left: 12px;
}
.thread-list .list-column-State,
.draft-list .list-column-State {
  display: flex;
  align-items: center;
}
.thread-list .list-column-Date,
.draft-list .list-column-Date {
  text-align: right;
}
.thread-list .timestamp,
.draft-list .timestamp {
  font-size: 13.02px;
  font-weight: 400;
  min-width: 70px;
  margin-right: 8px;
  opacity: 0.62;
}
.thread-list .unread:not(.focused):not(.selected),
.draft-list .unread:not(.focused):not(.selected) {
  background-color: #1E1E2E;
}
.thread-list .unread:not(.focused):not(.selected):hover,
.draft-list .unread:not(.focused):not(.selected):hover {
  background: #1a1a28;
}
.thread-list .unread:not(.focused):not(.selected) .snippet,
.draft-list .unread:not(.focused):not(.selected) .snippet {
  color: #A6ADC8;
}
.thread-list .unread:not(.focused):not(.selected):not(.next-is-selected),
.draft-list .unread:not(.focused):not(.selected):not(.next-is-selected) {
  border-bottom: 1px solid #313244;
}
.thread-list .unread:not(.focused) .subject,
.draft-list .unread:not(.focused) .subject {
  font-weight: 600;
}
.thread-list .unread:not(.focused) .subject .emoji,
.draft-list .unread:not(.focused) .subject .emoji {
  font-weight: 400;
}
.thread-list .unread:not(.focused) .participants .unread-true,
.draft-list .unread:not(.focused) .participants .unread-true {
  font-weight: 600;
}
.thread-list .focused,
.draft-list .focused {
  -webkit-font-smoothing: antialiased;
  color: #CDD6F4;
}
.thread-list .focused .participants .unread-true,
.draft-list .focused .participants .unread-true {
  font-weight: 400;
}
.thread-list .focused .subject,
.draft-list .focused .subject {
  font-weight: 400;
}
.thread-list .focused .thread-icon,
.draft-list .focused .thread-icon,
.thread-list .focused .draft-icon,
.draft-list .focused .draft-icon,
.thread-list .focused .mail-important-icon,
.draft-list .focused .mail-important-icon {
  filter: brightness(600%) grayscale(100%);
}
.thread-list .focused .mail-label,
.draft-list .focused .mail-label {
  background: rgba(205, 214, 244, 0.2) !important;
  box-shadow: none !important;
  filter: brightness(600%) grayscale(100%);
}
.thread-list .thread-injected-icons,
.draft-list .thread-injected-icons {
  vertical-align: top;
}
.thread-list .thread-injected-mail-labels,
.draft-list .thread-injected-mail-labels {
  margin-right: 6px;
}
.thread-list .thread-icon,
.draft-list .thread-icon {
  width: 25px;
  height: 24px;
  flex-shrink: 0;
  background-size: 15px;
  display: inline-block;
  background-repeat: no-repeat;
  background-position: center;
}
.thread-list .thread-icon.thread-icon-attachment,
.draft-list .thread-icon.thread-icon-attachment {
  background-image: url(../static/images/thread-list/icon-attachment-@2x.png);
  margin-right: 0;
