css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
@keyframes sending-progress-move {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 50px 50px;
  }
}
.draft-list .sending {
  background-color: #ffffff;
}
.draft-list .sending:hover {
  background-color: #ffffff;
}
.draft-list .sending-progress {
  display: block;
  height: 7px;
  align-self: center;
  background-color: #ffffff;
  border-bottom: 1px solid #dddddd;
  position: relative;
}
.draft-list .sending-progress .filled {
  display: block;
  background: #3187e1;
  height: 6px;
  width: 0;
  transition: width 1000ms linear;
}
.draft-list .sending-progress .indeterminate {
  display: block;
  background: #3187e1;
  height: 6px;
  width: 100%;
}
.draft-list .sending-progress .indeterminate:after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background-image: linear-gradient(-45deg, rgba(255, 255, 255, 0.2) 25%, transparent 25%, transparent 50%, rgba(255, 255, 255, 0.2) 50%, rgba(255, 255, 255, 0.2) 75%, transparent 75%, transparent);
  background-size: 50px 50px;
  animation: sending-progress-move 2s linear infinite;
}
