css:
.account-sidebar {
  background-color: #161320;
  border-right: 1px solid #161320;
}
.column-RootSidebar .flexbox-handle-horizontal div {
  box-shadow: none;
}
.nylas-outline-view .heading {
  font-size: 13px;
  text-transform: uppercase;
  letter-spacing: 2px;
  color: rgba(245, 224, 220, 0.5);
  margin-bottom: 6px;
  padding-top: 12px;
}
.nylas-outline-view .heading .add-item-button img.content-mask {
  background-color: rgba(245, 224, 220, 0.5);
  margin-top: 3px;
}
.nylas-outline-view .heading .add-item-button:hover img.content-mask {
  background-color: #F5E0DC;
}
.nylas-outline-view .heading .collapse-button:hover {
  color: #F5E0DC;
}
.account-switcher img.content-dark {
  background-image: none;
  background-color: #F5E0DC;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url(../static/images/sidebar/account-switcher-dropdown@2x.png);
  -webkit-mask-position: 0px 0px;
  width: 34px !important;
  height: 34px !important;
  margin-top: 8px;
}
.account-switcher:hover {
  cursor: pointer;
}
.account-switcher:hover img.content-dark {
  background-color: #161320;
}
.nylas-outline-view .item {
  font-size: 14px;
  font-weight: 500;
  color: #F5E0DC;
}
.nylas-outline-view .item .icon {
  margin-left: 8px;
}
.nylas-outline-view .item img.content-mask {
  background-color: #F5E0DC;
  margin-top: -1px;
  margin-bottom: 1px;
}
.nylas-outline-view .item:hover {
  background-color: #302D41;
}
.nylas-outline-view .item.selected {
  background-color: #C9CBFF;
  color: #161320;
}
.nylas-outline-view .item.selected img.content-mask {
  background-color: #161320;
}
.nylas-outline-view .item-container.dropping {
  background: transparent;
}
.nylas-outline-view .item-container.dropping .item {
  background-color: #C9CBFF;
  color: #161320;
}
.nylas-outline-view .item-container.dropping .item img.content-mask {
  background-color: #161320;
}
.nylas-outline-view .item .item-count-box,
.nylas-outline-view .item .item-count-box.alt-count {
  box-shadow: none;
  background-color: #C9CBFF;
  color: #161320;
}
.nylas-outline-view .item.selected .item-count-box,
.nylas-outline-view .item.selected .item-count-box.alt-count {
  background-color: #161320;
  color: #C9CBFF;
}
.disclosure-triangle.visible div {
  border-left-color: rgba(245, 224, 220, 0.5);
}
.disclosure-triangle.visible:hover div {
  border-left-color: #F5E0DC;
}
.sidebar-activity {
  background-color: #0f0d16;
  box-shadow: none;
  font-weight: 500;
  color: #F5E0DC;
}
.sidebar-activity .item .count {
  font-weight: 500;
  color: #F5E0DC;
}
.sidebar-activity .inner a {
  color: #C9CBFF;
  cursor: pointer;
}
.sidebar-activity .item h2 {
  font-weight: 600;
  color: rgba(245, 224, 220, 0.5);
  margin: 6px 0px 12px;
}
.sidebar-activity .item .sync-activity .account .folder-progress::before {
  background-color: #C9CBFF;
  border-color: #C9CBFF;
}
.sidebar-activity .item .sync-activity .account .folder-progress.busy::before {
  animation: border-pulse 3s ease-in infinite;
}
@keyframes border-pulse {
  0% {
    border-color: transparent;
  }
  100% {
    border-color: #C9CBFF;
  }
}
.sidebar-activity .item .sync-activity .account .folder-progress .progress-label {
  color: rgba(245, 224, 220, 0.5);
}
.notifications {
  background-color: #0b0a10;
  box-shadow: none;
}
.notification {
  background: #C9CBFF;
  border-radius: 3px;
  margin-top: 16px;
}
.notification .action {
  border: none !important;
}
.notification .action:hover {
  box-shadow: none !important;
  background: #fcfcff;
}
.notification .action:active {
  background: #969aff;
}
.notification.error {
  background: #F28FAD;
}
.notification.error .action:hover {
  background: #f7bdce;
}
.notification.error .action:active {
  background: #ed618c;
}
.notification.offline {
  background: #FAE3B0;
}
.notification.offline .action:hover {
  background: #fdf4e0;
}
.notification.offline .action:active {
  background: #f7d280;
}
.account-sidebar .scrollbar-track {
  background: transparent;
  border-left: 1px solid transparent;
  border-right: 1px solid transparent;
  width: 12px;
  padding: 3px 0px 3px 6px;
}
.account-sidebar .scrollbar-track .scrollbar-handle {
  background: #575268;
  border: none;
  border-radius: 3px;
  opacity: 0.4;
}
.account-sidebar .scrollbar-track:hover,
.account-sidebar .scrollbar-track.dragging {
  background: #302D41;
  border-left: 1px solid #161320;
  padding-left: 3px;
  padding-right: 2px;
}
.account-sidebar .scrollbar-track:hover .scrollbar-handle,
.account-sidebar .scrollbar-track.dragging .scrollbar-handle {
  opacity: 0.99;
}
.account-sidebar .scrollbar-track:hover:not(.dragging) .scrollbar-handle {
  background: #6E6C7E;
}
.account-sidebar .scrollbar-track:hover:not(.dragging) .scrollbar-handle:hover {
  background: #988BA2;
}
.account-sidebar .scrollbar-track.dragging .scrollbar-handle {
  background: #C9CBFF;
}
