css:
/* The variables declared here are used by the theme picker in Mailspring
to show an appropriate mini-preview of your theme. */
.column-RootSidebar,
.column-MessageListSidebar,
.preferences-sidebar,
.column-DraftList {
  padding: 5em 0 2em 2em;
  background: #ffffff;
}
.column-MessageList {
  padding: 4.5em 2em 0;
}
.toolbar-window-controls button {
  background-color: #899fa8;
  background-image: none !important;
  width: 12px;
  height: 12px;
  float: none;
  opacity: 0.5;
  transform: scaleY(0.5);
  border-radius: 0;
  transition-duration: 150ms;
  transition-property: border-radius, opacity, transform;
}
.toolbar-window-controls:hover button {
  opacity: 1;
  border-radius: 50%;
  transform: scaleY(1);
}
.toolbar-window-controls .close {
  background-color: #ff5f56;
}
.toolbar-window-controls .minimize {
  background-color: #fbd852;
}
.toolbar-window-controls .maximize {
  background-color: #8dd07d;
}
.sheet-toolbar .btn-toolbar {
  background: transparent;
  border: none;
  box-shadow: none;
}
.toolbar-MessageListSidebar,
.sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-color: #ffffff;
}
.toolbar-MessageListSidebar {
  border-left: 1px solid #dddddd;
  height: 40px;
  margin-left: -0.5px;
}
.sheet-toolbar-container [data-column='0'] .item-container {
  background-color: #ffffff;
}
.sheet-toolbar {
  border: none;
  height: 0;
  min-height: 0;
}
.sheet-toolbar .selection-bar .absolute {
  position: absolute;
  left: 0;
  right: 0;
  border-left: none;
  border-right: none;
}
.column-RootSidebar,
.account-sidebar {
  background-color: #ffffff;
}
.account-sidebar .item {
  color: #899fa8;
  font-size: 14px;
  font-weight: 400;
  padding-left: 20px;
  margin-bottom: 6px;
}
.account-sidebar .heading {
  font-size: 10px;
  text-transform: uppercase;
  letter-spacing: 2px;
  color: #899fa8;
  margin-bottom: 12px;
}
.account-sidebar .item img.content-mask,
.account-sidebar .add-item-button img {
  background-color: transparent;
  -webkit-mask-image: none;
}
.account-sidebar .item .name {
  padding-left: 0;
  margin-left: -10px;
}
.account-sidebar .item.selected {
  background: transparent;
  color: #566c75;
  font-weight: 600;
}
.disclosure-triangle {
  padding-top: 7px;
}
.disclosure-triangle div {
  border-left-color: #899fa8;
  border-top-width: 3px;
  border-bottom-width: 3px;
  border-left-width: 5px;
  transform-origin: 2px;
}
.outline-view .item .icon img {
  display: none;
}
.outline-view .heading .add-item-button img {
  background: #899fa8;
}
.outline-view .item .item-count-box.alt-count {
  background: #566c75;
  color: #ffffff;
}
.scrollbar-track {
  background-color: transparent;
  width: 10px;
  border-left: none;
}
.flexbox-handle-horizontal div {
  border-right: none;
  box-shadow: none;
}
#message-list .scrollbar-track {
  margin-right: -2em;
}
.column-ThreadList,
.list-container .list-item,
.list-container .list-item:hover {
  cursor: pointer !important;
  box-sizing: border-box;
  border: 0 !important;
  background-color: #ffffff;
  color: #566c75;
}
.column-ThreadList {
  padding: 5em 2em 1em;
}
body.is-blurred .list-container .list-item.focused,
body.is-blurred .list-container .list-item.selected,
body.is-blurred .thread-list.handler-split .list-item.selected {
  background-color: #fafafa;
  color: #000000;
  font-weight: bold;
}
.list-container .list-item.selected,
.list-container .list-item:hover {
  background: #fafafa;
  color: #566c75;
}
.thread-list .list-item.selected:hover .list-column-HoverActions .inner,
.thread-list .list-item:hover .list-column-HoverActions .inner {
  background-image: linear-gradient(to right, rgba(250, 250, 250, 0) 0%, #fafafa 40%, #fafafa 100%);
}
.thread-injected-quick-actions .btn {
  box-shadow: none;
}
.thread-list .list-item .list-column-HoverActions .action.action-trash {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png') center no-repeat, #fafafa;
}
.thread-list .list-item .list-column-HoverActions .action.action-archive {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png') center no-repeat, #fafafa;
}
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url('../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png') center no-repeat, #fafafa;
}
.thread-list .thread-icon.thread-icon-star,
.draft-list .thread-icon.thread-icon-star {
  filter: grayscale(100%);
}
#message-list {
  background-color: #ffffff;
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: none;
  border-radius: 0;
}
#message-list .footer-reply-area-wrap {
  box-shadow: none;
  border-radius: 0;
  border-top: none;
  background: #fafafa;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap,
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .composer-action-bar-wrap {
  background-color: #fafafa;
  box-shadow: none;
  border-radius: 0;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  border: 1px solid transparent;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .RichEditor-toolbar .inner {
  background-color: #fafafa;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  background-color: #ffffff;
  box-shadow: none;
  border: 1px solid #dddddd;
  border-radius: 0;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .RichEditor-toolbar .inner {
  background: #ffffff;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .composer-action-bar-wrap {
  background-color: #ffffff;
}
.column-MessageListSidebar {
  padding: 5em 1em;
}
.sidebar-participant-picker {
  padding-bottom: 50px;
}
.sidebar-section {
  border: none;
  border-radius: 0;
}
.sidebar-section h2 {
  font-size: 12px;
  text-transform: uppercase;
  letter-spacing: 3px;
  color: #899fa8;
  border-bottom: none;
}
.related-threads {
  background: transparent;
  border-top: none;
  border-radius: 0;
  overflow: visible;
}
.related-threads .related-thread {
  border-top: none;
  background-color: #fafafa;
  color: #566c75;
  margin-bottom: 8px;
  padding: 15px 10px;
}
.related-threads .toggle {
  border-top: none;
  color: #566c75;
}
.thread-list .swipe-backing.swipe-all,
.thread-list .swipe-backing.swipe-archive,
.draft-list .swipe-backing.swipe-all,
.draft-list .swipe-backing.swipe-archive {
  background: #8dd07d;
}
.thread-list .swipe-backing.swipe-all.confirmed,
.thread-list .swipe-backing.swipe-archive.confirmed,
.draft-list .swipe-backing.swipe-all.confirmed,
.draft-list .swipe-backing.swipe-archive.confirmed {
  background: #88d974;
}
.thread-list .swipe-backing.swipe-snooze,
.draft-list .swipe-backing.swipe-snooze {
  background: #fbd852;
}
.thread-list .swipe-backing.swipe-snooze.confirmed,
.draft-list .swipe-backing.swipe-snooze.confirmed {
  background: #ffda4e;
}
.thread-list .swipe-backing.swipe-trash,
.draft-list .swipe-backing.swipe-trash {
  background: #ff5f56;
}
.thread-list .swipe-backing.swipe-trash.confirmed,
.draft-list .swipe-backing.swipe-trash.confirmed {
  background: #ff5f56;
}
.preferences-wrap .container-preference-tabs .preferences-tabs {
  padding-top: 40px;
  background-color: #ffffff;
}
.preferences-wrap .preferences-content > .scroll-region-content {
  padding-bottom: 100px;
}
input[type='text'],
input[type='email'],
input[type='date'],
input[type='datetime'],
input[type='datetime-local'],
input[type='month'],
input[type='number'],
input[type='password'],
input[type='range'],
input[type='search'],
input[type='tel'],
input[type='time'],
input[type='url'] {
  border-radius: 0;
  border: none !important;
}
.search-bar {
  background-color: transparent;
  width: 400px;
  margin-right: 7.5px;
}
body.is-blurred .search-bar .menu .header-container input,
body.is-blurred .sheet-toolbar-container .btn.btn-toolbar,
.search-bar .menu .header-container input {
  box-shadow: none;
}
.notifications-sticky .notifications-sticky-item {
  background-color: #ff5f56;
  line-height: 50px;
  border: none;
}
.sidebar-activity {
  background: #ffffff;
  color: #566c75;
  box-shadow: none;
}
.sidebar-activity .item {
  border: none;
}
.sheet-toolbar-container,
body.is-blurred .sheet-toolbar-container {
  background-color: #ffffff;
  background-image: none;
  box-shadow: none;
}
.composer-full-window .composer-inner-wrap .composer-action-bar-wrap {
  background: #ffffff;
  border-top: none;
  box-shadow: none;
  padding-bottom: 0.8em;
}
.tokenizing-field .token.invalid {
  border: 1px solid #ffd8d6;
}
.tokenizing-field .token.selected,
.tokenizing-field .token.dragging {
  background: #566c75;
  box-shadow: none;
  border: none;
}
.tokenizing-field .token.invalid.selected,
.tokenizing-field .token.invalid.dragging {
  background: #ffd8d6;
}
.tokenizing-field .tokenizing-field-input input[type='text'],
.tokenizing-field .tokenizing-field-input input[type='text']:focus {
  border-bottom: none !important;
}
.button-dropdown .primary-item,
.button-dropdown .only-item {
  box-shadow: none;
}
.button-dropdown.btn-emphasis .primary-item,
.button-dropdown.btn-emphasis .secondary-picker,
.button-dropdown.btn-emphasis .only-item .button-dropdown.btn-emphasis .primary-item:active,
.button-dropdown.btn-emphasis .secondary-picker:active,
.button-dropdown.btn-emphasis .only-item:active,
.button-dropdown.bordered .primary-item,
.button-dropdown:hover .primary-item,
.button-dropdown.bordered .only-item,
.button-dropdown:hover .only-item,
.button-dropdown .secondary-picker,
.btn.btn-emphasis {
  background-color: #a6b7be;
  background: #a6b7be;
  box-shadow: none;
  border: 1px solid #98abb3;
}
.button-dropdown .primary-item img.content-mask,
.button-dropdown .only-item img.content-mask,
.button-dropdown .secondary-picker img.content-mask {
  background-color: #ffffff;
}
html,
body {
  margin: 0;
  height: 100%;
  width: 100%;
  overflow: hidden;
  -webkit-font-smoothing: antialiased;
}
.theme-option {
  position: absolute;
  top: 0;
  margin-top: 4px;
  margin-left: 5px;
  width: 100px;
  height: 60px;
  background-color: #ffffff;
  color: #566c75;
  border-radius: 5px;
  text-align: center;
  overflow: hidden;
}
.theme-option.active-true {
  border: 1px solid #3187e1;
  box-shadow: 0 0 4px #9ecaed;
}
.theme-option.active-false {
  border: 1px solid #dddddd;
}
.theme-option .theme-name {
  font-family: 'Nylas-Pro', 'Helvetica', sans-serif;
  font-size: 12px;
  font-weight: 600;
  margin-top: 7px;
  height: 18px;
  overflow: hidden;
}
.theme-option .swatches {
  padding-left: 27px;
  padding-right: 27px;
  display: flex;
  flex-direction: row;
}
.theme-option .swatches .swatch {
  flex: 1;
  height: 10px;
  width: 10px;
  margin: 4px 2px 4px 2px;
  border-radius: 2px;
  border: 1px solid rgba(0, 0, 0, 0.15);
  background-clip: border-box;
  background-origin: border-box;
}
.theme-option .swatches .swatch.font-color {
  background-color: #566c75;
}
.theme-option .swatches .swatch.active-color {
  background-color: #ffffff;
}
.theme-option .swatches .swatch.toolbar-color {
  background-color: #ffffff;
}
.theme-option .divider-black {
  position: absolute;
  bottom: 12px;
  height: 1px;
  width: 100%;
  background-color: black;
  opacity: 0.15;
}
.theme-option .divider-white {
  position: absolute;
  z-index: 10;
  bottom: 11px;
  height: 1px;
  width: 100%;
  background-color: white;
  opacity: 0.15;
}
.theme-option .strip {
  position: absolute;
  bottom: 0;
  height: 12px;
  width: 100%;
  background-color: #ffffff;
}
