css:
.sheet-toolbar {
  border-bottom: 1px solid #2c2c2c;
  box-shadow: none;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  filter: invert(100%);
}
img.content-dark {
  filter: invert(100%);
}
img.content-light {
  filter: invert(100%);
}
.mail-label {
  filter: contrast(110%) brightness(85%);
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: 0 0 0.5px rgba(255, 255, 255, 0.28), 0 1px 1.5px rgba(255, 255, 255, 0.08);
}
.thread-list .list-item:not(.unread):not(.selected):not(.focused) {
  color: #aaa;
}
.thread-list .list-item.thread-list-reminder-item {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread:not(.focused):not(.selected) {
  background-color: #312856;
}
.send-reminders-header {
  color: #7d5bef;
  background: #312856;
  border-bottom: 1px solid #4d3b8a;
}
.preferences-wrap .container-accounts {
  min-width: 420px;
  margin: 0 auto;
}
.preferences-wrap .container-accounts .accounts-content {
  display: flex;
  justify-content: center;
}
.preferences-wrap .container-accounts .accounts-content .account-list {
  display: flex;
  flex-direction: column;
  height: auto;
  width: 400px;
}
.preferences-wrap .container-accounts .accounts-content .account-list .items-wrapper {
  flex: 1;
}
.preferences-wrap .container-accounts .accounts-content .account-list .account {
  padding: 10px;
  border-bottom: 1px solid #2c2c2c;
}
.preferences-wrap .container-accounts .accounts-content .account-list .list-item:not(.selected) .sync-error {
  color: #d9534f;
}
.preferences-wrap .container-accounts .accounts-content .account-list .account-name {
  font-size: 15.96px;
  cursor: default;
  overflow: hidden;
  text-overflow: ellipsis;
  vertical-align: middle;
}
.preferences-wrap .container-accounts .accounts-content .account-list .account-subtext {
  font-size: 13.02px;
  cursor: default;
}
.preferences-wrap .container-accounts .accounts-content .account-list .btn-editable-list {
  height: 37px;
  width: 37px;
  line-height: 37px;
  font-size: 1em;
}
.preferences-wrap .container-accounts .accounts-content .account-details {
  width: 400px;
  padding: 20px;
  padding-left: 31.5px;
  padding-right: 31.5px;
  background-color: #131313;
  border-top: 1px solid #2c2c2c;
  border-right: 1px solid #2c2c2c;
  border-bottom: 1px solid #2c2c2c;
}
.preferences-wrap .container-accounts .accounts-content .account-details .key-commands-region {
  height: inherit;
}
.preferences-wrap .container-accounts .accounts-content .account-details .items-wrapper {
  height: 100px;
}
.preferences-wrap .container-accounts .accounts-content .account-details .account-error-detail {
  display: flex;
  flex-direction: column;
  background: linear-gradient(to top, #ca2541 0%, #d55268 100%);
}
.preferences-wrap .container-accounts .accounts-content .account-details .account-error-detail .action {
  flex-shrink: 0;
  background-color: rgba(0, 0, 0, 0.15);
  text-align: center;
  padding: 3px 12px;
  color: white;
  flex: 1;
  border-top: solid rgba(255, 255, 255, 0.5) 1px;
  border-right: solid rgba(255, 255, 255, 0.5) 1px;
}
.preferences-wrap .container-accounts .accounts-content .account-details .account-error-detail .action:last-child {
  border-right: 0;
}
.preferences-wrap .container-accounts .accounts-content .account-details .account-error-detail .action:hover {
  background-color: rgba(255, 255, 255, 0.15);
  text-decoration: none;
}
.preferences-wrap .container-accounts .accounts-content .account-details .account-error-detail .message {
  flex-grow: 1;
  padding: 3px 12px;
  color: white;
}
.preferences-wrap .container-accounts .accounts-content .account-details .newsletter {
  padding-top: 10px;
}
.preferences-wrap .container-accounts .accounts-content .account-details .newsletter input[type='checkbox'] {
  margin: 0;
  position: relative;
  top: 0;
}
.preferences-wrap .container-accounts .accounts-content .account-details > h6:first-child {
  margin-top: 0;
}
.preferences-wrap .container-accounts .accounts-content .account-details > input {
  font-size: 0.9em;
  width: 100%;
}
.preferences-wrap .container-accounts .accounts-content .account-details .default-alias-selector {
  padding-top: 15px;
  padding-bottom: 5px;
}
.preferences-wrap .container-accounts .accounts-content .account-details .default-alias-selector > select {
  font-size: 0.9em;
  margin-left: 0;
  width: 100%;
}
