css:
.sheet-toolbar {
  border-bottom: 1px solid #2c2c2c;
  box-shadow: none;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  filter: invert(100%);
}
img.content-dark {
  filter: invert(100%);
}
img.content-light {
  filter: invert(100%);
}
.mail-label {
  filter: contrast(110%) brightness(85%);
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: 0 0 0.5px rgba(255, 255, 255, 0.28), 0 1px 1.5px rgba(255, 255, 255, 0.08);
}
.thread-list .list-item:not(.unread):not(.selected):not(.focused) {
  color: #aaa;
}
.thread-list .list-item.thread-list-reminder-item {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread:not(.focused):not(.selected) {
  background-color: #312856;
}
.send-reminders-header {
  color: #7d5bef;
  background: #312856;
  border-bottom: 1px solid #4d3b8a;
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 200;
  src: url('mailspring://custom-fonts/fonts/Custom-Thin.otf');
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 300;
  src: url('mailspring://custom-fonts/fonts/Custom-Blond.otf');
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 400;
  src: url('mailspring://custom-fonts/fonts/Custom-Normal.otf');
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 500;
  src: url('mailspring://custom-fonts/fonts/Custom-Medium.otf');
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 600;
  src: url('mailspring://custom-fonts/fonts/Custom-SemiBold.otf');
}
@font-face {
  font-family: 'Nylas-Pro';
  font-style: normal;
  font-weight: 600;
  src: url('mailspring://custom-fonts/fonts/Custom-Normal.otf'), Helvetica, sans-serif;
  unicode-range: U+1f300-1f5ff, U+1f600-1f64f, U+1f680-1f6ff, U+2600-26ff;
}
.ignore-in-parent-frame html,
.ignore-in-parent-frame body {
  font-size: 14.5px;
  line-height: 1.5;
  color: #eee;
  background-color: transparent !important;
  border: 0;
  margin: 0;
  padding: 0;
  -webkit-text-size-adjust: auto;
  word-wrap: break-word;
  -webkit-nbsp-mode: space;
  -webkit-line-break: after-white-space;
}
.ignore-in-parent-frame html {
  overflow-x: auto;
  overflow-y: hidden;
}
.ignore-in-parent-frame #inbox-html-wrapper {
  font-family: 'Nylas-Pro', 'Helvetica', 'Lucidia Grande', sans-serif;
}
.ignore-in-parent-frame #inbox-html-wrapper.platform-win32 {
  font-family: system-ui;
}
.ignore-in-parent-frame #inbox-plain-wrapper {
  font-family: 'DejaVu Sans Mono', Menlo, Monaco, Consolas, 'Courier New', monospace;
  font-size: 14px;
  white-space: pre-line;
}
.ignore-in-parent-frame strong,
.ignore-in-parent-frame b,
.ignore-in-parent-frame .bold {
  font-weight: 600;
}
.ignore-in-parent-frame body {
  padding: 0;
  margin: auto;
  -webkit-font-smoothing: antialiased;
}
.ignore-in-parent-frame body.restrict-width {
  max-width: 840px;
}
.ignore-in-parent-frame a {
  word-break: break-word;
}
.ignore-in-parent-frame blockquote {
  border-left: 2px solid #eee;
  margin: 0;
  padding: 0 0 0 10px;
  -webkit-margin-after: initial !important;
  -webkit-margin-before: initial !important;
  -webkit-margin-start: initial !important;
  -webkit-margin-end: initial !important;
}
.ignore-in-parent-frame a {
  color: #58b660;
}
.ignore-in-parent-frame a:hover {
  color: #43984a;
}
.ignore-in-parent-frame a:visited {
  color: #43984a;
}
.ignore-in-parent-frame a img {
  border-bottom: 0;
}
.ignore-in-parent-frame div,
.ignore-in-parent-frame pre {
  max-width: 100%;
}
.ignore-in-parent-frame p {
  margin: 0;
  padding: 0;
  margin-bottom: 0;
}
.ignore-in-parent-frame pre.nylas-plaintext {
  font-family: 'Nylas-Pro', 'Helvetica', 'Lucidia Grande', sans-serif;
  white-space: pre-wrap;
  word-wrap: break-word;
}
.ignore-in-parent-frame img {
  border: 0;
}
.ignore-in-parent-frame search-match,
.ignore-in-parent-frame .search-match {
  background: #fff000;
  border-radius: 3px;
  box-shadow: 0 0.5px 0.5px rgba(0, 0, 0, 0.25);
}
.ignore-in-parent-frame search-match.current-match,
.ignore-in-parent-frame .search-match.current-match {
  background: #ff8b1a;
}
.ignore-in-parent-frame .inline-download-prompt {
  border: solid 1px rgba(0, 0, 0, 0.09);
  border-radius: 2px;
  color: #eee;
  background: #212121;
  margin: 4px 0;
  padding: 4px 8px;
  cursor: pointer;
  display: inline-block;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
}
.ignore-in-parent-frame .inline-download-prompt:hover {
  cursor: pointer;
  color: #eee;
  border: solid 1px rgba(0, 0, 0, 0.16);
}
