css:
.column-MessageList {
  background: #1E1E2E;
  border-left: 1px solid #161320;
}
#message-list {
  background: #1E1E2E;
}
#message-list .messages-wrap .scroll-region-content-inner {
  padding: 6px 20px;
}
#message-list .message-subject-wrap {
  padding-left: 2px;
}
#message-list .message-subject {
  color: #D9E0EE;
}
#message-list .message-icons-wrap {
  cursor: default;
}
#message-list .message-icons-wrap div {
  background: none;
  border: 1px solid transparent;
  border-radius: 3px;
  height: 26px;
  padding: 0px 13px;
}
#message-list .message-icons-wrap div img.content-mask {
  margin-top: -6px;
  background-color: #c9cbff;
}
#message-list .message-icons-wrap div:hover {
  background-color: #302d41;
  border-color: #1a1826;
}
#message-list .message-icons-wrap div:active {
  background-color: #C9CBFF;
  border-color: #C9CBFF;
}
#message-list .message-icons-wrap div:active img.content-mask {
  background-color: #1A1826;
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: none !important;
  background: #1A1826;
  border: 1px solid #161320;
  border-radius: 3px;
}
.message-participants .from-contact {
  font-size: 14px;
  color: #D9E0EE;
}
.message-participants.to-participants .collapsed-participants {
  margin-top: 3px;
}
.message-participants .from-label,
.message-participants .to-label,
.message-participants .reply-to-label,
.message-participants .cc-label,
.message-participants .bcc-label {
  color: rgba(217, 224, 238, 0.5);
  font-size: 14px;
}
.message-participants .to-contact,
.message-participants .reply-to-contact,
.message-participants .cc-contact,
.message-participants .bcc-contact,
.message-participants .to-everyone {
  color: rgba(217, 224, 238, 0.8);
  font-size: 14px;
}
.message-participants .expanded-participants .from-contact .participant-primary,
.message-participants .expanded-participants .from-contact .participant-secondary {
  color: #D9E0EE;
}
.message-participants .expanded-participants .participant-primary,
.message-participants .expanded-participants .participant-secondary {
  color: rgba(217, 224, 238, 0.8);
}
#message-list .message-header {
  margin-bottom: 12px;
}
#message-list .message-header .header-row {
  font-size: 14px;
}
#message-list .message-header .header-row .header-label {
  color: rgba(217, 224, 238, 0.5);
}
#message-list .message-header .header-row .header-name {
  color: rgba(217, 224, 238, 0.8);
}
#message-list .message-header .message-time,
#message-list .message-header .message-indicator {
  color: rgba(217, 224, 238, 0.5);
}
#message-list .message-header .message-header-right .open-tracking-message-status.opened img.content-mask {
  background-color: #C9CBFF;
}
#message-list .header-toggle-control img {
  background: rgba(217, 224, 238, 0.5);
}
#message-list .header-toggle-control img:hover {
  background: #D9E0EE;
}
#message-list .message-header .message-actions-wrap .button-dropdown.bordered .primary-item,
#message-list .message-header .message-actions-wrap .button-dropdown.bordered .secondary-picker {
  box-shadow: none;
  background: #302D41;
  border: 1px solid #161320;
}
#message-list .message-header .message-actions-wrap .button-dropdown.bordered .primary-item img.content-mask,
#message-list .message-header .message-actions-wrap .button-dropdown.bordered .secondary-picker img.content-mask {
  background-color: #C9CBFF;
}
#message-list .message-header .message-actions-wrap .button-dropdown.bordered .primary-item {
  border-radius: 3px 0 0 3px;
}
#message-list .message-header .message-actions-wrap .button-dropdown.bordered .secondary-picker {
  border-left-width: 0;
  border-radius: 0 3px 3px 0;
}
#message-list .message-header .message-actions-wrap .button-dropdown.bordered:hover .primary-item,
#message-list .message-header .message-actions-wrap .button-dropdown.bordered:hover .secondary-picker {
  background: #575268;
  border-color: #161320;
}
#message-list .message-header .message-actions-wrap .button-dropdown.bordered .primary-item:active,
#message-list .message-header .message-actions-wrap .button-dropdown.bordered .secondary-picker:active {
  background: #C9CBFF;
  border-color: #1A1826;
}
#message-list .message-header .message-actions-wrap .button-dropdown.bordered .primary-item:active img.content-mask,
#message-list .message-header .message-actions-wrap .button-dropdown.bordered .secondary-picker:active img.content-mask {
  background-color: #1A1826;
}
#message-list .footer-reply-area-wrap {
  box-shadow: none;
  background: #1A1826;
  border: 1px solid #161320;
  border-radius: 0 0 3px 3px;
}
#message-list .footer-reply-area-wrap .footer-reply-area {
  width: max-content;
  background: #302D41;
  border: 1px solid #161320;
  border-radius: 3px;
  margin-bottom: 9px;
  margin-top: 9px;
  color: #C9CBFF;
  font-size: 14px;
  height: 2.1em;
  line-height: 1.9em;
  padding: 0 0.8em;
}
#message-list .footer-reply-area-wrap .footer-reply-area img.content-mask {
  background-color: #C9CBFF;
}
#message-list .footer-reply-area-wrap .footer-reply-area:hover {
  background-color: #575268;
  border-color: #161320;
}
#message-list .footer-reply-area-wrap .footer-reply-area:active {
  background-color: #C9CBFF;
  border-color: #1A1826;
  color: #1A1826;
}
#message-list .footer-reply-area-wrap .footer-reply-area:active img.content-mask {
  background-color: #1A1826;
}
#message-list .message-item-wrap.collapsed .message-item-white-wrap {
  background-color: #1A1826;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-from {
  color: rgba(217, 224, 238, 0.8);
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-timestamp {
  color: rgba(217, 224, 238, 0.5);
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-snippet {
  color: rgba(217, 224, 238, 0.5);
}
#message-list .minified-bundle .msg-line {
  box-shadow: none;
  background-color: #1A1826;
  border: 1px solid #161320;
  border-radius: 3px 3px 0 0;
}
#message-list .minified-bundle .num-messages {
  background-color: #1A1826;
  border: 1px solid #161320;
  color: #C9CBFF;
}
#message-list .minified-bundle .num-messages:hover {
  background-color: #575268;
  border-color: #161320;
}
#message-list .minified-bundle .num-messages:active {
  background-color: #C9CBFF;
  border-color: #1A1826;
}
.selected-items-stack .selected-items-stack-content .stack .card svg {
  stroke: #161320;
  fill: #1A1826;
}
.selected-items-stack .selected-items-stack-content .count-info .count,
.selected-items-stack .selected-items-stack-content .count-info .count-message {
  color: rgba(217, 224, 238, 0.8);
}
.unsubscribe-action {
  color: #89DCEB;
}
.unsubscribe-action:hover {
  cursor: pointer;
  color: #89DCEB;
}
.quoted-text-control {
  background: #302D41;
  border: 1px solid #161320;
  color: #C9CBFF;
  height: 1.7em;
  font-size: 1em;
  padding: 0.3em 0.7em;
  margin: 16px 0 3px;
}
.quoted-text-control:hover {
  background: #575268;
  border: 1px solid #161320;
  color: #C9CBFF;
  cursor: default;
}
.quoted-text-control:active {
  background: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.nylas-attachment-item .file-thumbnail-preview,
.nylas-attachment-item.file-attachment-item:focus .file-thumbnail-preview {
  border: 1px solid #161320 !important;
  border-radius: 3px !important;
}
.nylas-attachment-item .inner,
.nylas-attachment-item.file-attachment-item:focus .inner {
  background: transparent;
  color: #D9E0EE;
}
.nylas-attachment-item .file-info-wrap,
.nylas-attachment-item.file-attachment-item:focus .file-info-wrap {
  border: none !important;
}
.nylas-attachment-item .file-info-wrap .file-size,
.nylas-attachment-item.file-attachment-item:focus .file-info-wrap .file-size {
  color: rgba(217, 224, 238, 0.5);
}
.nylas-attachment-item .file-info-wrap:hover,
.nylas-attachment-item.file-attachment-item:focus .file-info-wrap:hover {
  cursor: pointer;
}
.nylas-attachment-item .file-action-icon,
.nylas-attachment-item.file-attachment-item:focus .file-action-icon {
  background: #302D41;
  border: 1px solid #161320 !important;
  border-radius: 3px !important;
  color: #C9CBFF;
  font-size: 14px;
  height: 2.1em;
  line-height: 1.9em;
  padding: 0 0.8em;
  margin-left: 8px;
}
.nylas-attachment-item .file-action-icon img.content-mask,
.nylas-attachment-item.file-attachment-item:focus .file-action-icon img.content-mask {
  background-color: #C9CBFF;
}
.nylas-attachment-item .file-action-icon:hover,
.nylas-attachment-item.file-attachment-item:focus .file-action-icon:hover {
  background-color: #575268;
  border: 1px solid #161320;
}
.nylas-attachment-item .file-action-icon:active,
.nylas-attachment-item.file-attachment-item:focus .file-action-icon:active {
  background-color: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.nylas-attachment-item .file-action-icon:active img.content-mask,
.nylas-attachment-item.file-attachment-item:focus .file-action-icon:active img.content-mask {
  background-color: #1A1826;
}
.nylas-attachment-item.image-attachment-item:hover {
  border: none;
}
.nylas-attachment-item.image-attachment-item .file-action-icon {
  right: 0px;
  top: 0px;
  width: 100%;
  height: 100%;
  background: transparent;
  cursor: pointer;
}
.nylas-attachment-item.image-attachment-item .file-action-icon img {
  display: none;
}
.nylas-attachment-item.image-attachment-item .file-preview .file-name-container {
  background: rgba(26, 24, 38, 0.7);
}
.nylas-attachment-item.image-attachment-item .file-preview .file-name-container .file-name {
  color: #D9E0EE;
  font-weight: 700;
}
.download-all .attachment-number {
  font-size: 14px;
  color: rgba(217, 224, 238, 0.8);
}
.download-all .attachment-number img.content-mask {
  background-color: #D9E0EE;
}
.download-all .separator {
  display: none;
}
.download-all .download-all-action {
  background: #302D41;
  border: 1px solid #161320;
  border-radius: 3px;
  color: #C9CBFF;
  font-size: 14px;
  height: 2.1em;
  line-height: 1.9em;
  padding: 0 0.8em;
  margin-left: 12px;
}
.download-all .download-all-action img.content-mask {
  background-color: #C9CBFF;
}
.download-all .download-all-action:hover {
  background-color: #575268;
  border-color: #161320;
  color: #C9CBFF;
}
.download-all .download-all-action:active {
  background-color: #C9CBFF;
  border-color: #1A1826;
  color: #1A1826;
}
.download-all .download-all-action:active img.content-mask {
  background-color: #1A1826;
}
.mail-label.removable {
  box-shadow: none !important;
  background: #C9CBFF !important;
  color: #161320 !important;
  margin-top: 3px;
  font-weight: 500;
}
.mail-label.removable img.content-mask {
  background-color: #161320 !important;
}
.mail-label.removable img.content-mask:hover {
  cursor: pointer;
}
#message-list .show-hidden-messages {
  border: none;
  border-left: 3px solid #C9CBFF;
  border-radius: 0px;
  background-color: #252236;
  color: rgba(217, 224, 238, 0.8);
  margin-bottom: 16px;
}
#message-list .show-hidden-messages a {
  color: #C9CBFF;
  cursor: pointer;
}
.autoload-images-header {
  border: none;
  border-left: 3px solid #FAE3B0;
  border-radius: 0px;
  background-color: #252236;
  color: rgba(217, 224, 238, 0.8);
  margin-bottom: 16px;
}
.autoload-images-header .option {
  color: #FAE3B0;
  cursor: pointer;
}
.autoload-images-header .option:hover {
  color: #FAE3B0;
}
.event-wrapper {
  font-size: 14px;
  border: 1px solid rgba(217, 224, 238, 0.15);
}
.event-wrapper .event-header {
  border-bottom: 1px solid rgba(217, 224, 238, 0.15);
}
.event-wrapper .event-header .event-title-text {
  color: rgba(217, 224, 238, 0.5);
}
.event-wrapper .event-header .event-title {
  color: rgba(217, 224, 238, 0.8);
}
.event-wrapper .event-header .event-download {
  background-color: #302D41;
  border: 1px solid #161320;
  padding: 0 8px;
}
.event-wrapper .event-header .event-download img.content-mask {
  background: #C9CBFF;
}
.event-wrapper .event-header .event-download:hover {
  background-color: #575268;
  border-color: #161320;
}
.event-wrapper .event-header .event-download:active {
  background-color: #C9CBFF;
  border-color: #1A1826;
}
.event-wrapper .event-header .event-download:active img.content-mask {
  background: #1A1826;
}
.event-wrapper .event-body {
  color: #D9E0EE;
}
.event-wrapper .event-body .event-date .event-day {
  font-size: 14px;
  color: rgba(217, 224, 238, 0.8);
}
.event-wrapper .event-body .event-location {
  color: rgba(217, 224, 238, 0.8);
}
.translate-message-header {
  border: none;
  border-left: 3px solid #C9CBFF;
  border-radius: 0px;
  background-color: #14121d;
  color: rgba(217, 224, 238, 0.8);
}
.translate-message-header .message {
  border: none;
}
.translate-message-header .message .message-centered .note {
  opacity: 1;
  color: rgba(217, 224, 238, 0.5);
}
.translate-message-header .actions .action {
  background: #302D41;
  border: 1px solid #161320;
  border-radius: 3px;
  color: #C9CBFF;
  margin: 3px;
}
.translate-message-header .actions .action:last-child {
  border-bottom: 1px solid #161320;
  margin-top: 0;
}
.translate-message-header .actions .action.button-dropdown .only-item img.content-mask {
  background-color: #C9CBFF;
}
.translate-message-header .actions .action:hover {
  background: #575268;
  border: 1px solid #161320;
}
.translate-message-header .actions .action:active {
  background: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.translate-message-header .actions .action:active.button-dropdown .only-item img.content-mask {
  background-color: #1A1826;
}
#message-list .scrollbar-track,
.composer-full-window .scrollbar-track {
  background: transparent;
  border-left: 1px solid transparent;
  border-right: 1px solid transparent;
  width: 12px;
  padding: 3px 0px 3px 6px;
}
#message-list .scrollbar-track .scrollbar-handle,
.composer-full-window .scrollbar-track .scrollbar-handle {
  background: #575268;
  border: none;
  border-radius: 3px;
  opacity: 0.4;
}
#message-list .scrollbar-track:hover,
.composer-full-window .scrollbar-track:hover,
#message-list .scrollbar-track.dragging,
.composer-full-window .scrollbar-track.dragging {
  background: #302D41;
  border-left: 1px solid #161320;
  padding-left: 3px;
  padding-right: 2px;
}
#message-list .scrollbar-track:hover .scrollbar-handle,
.composer-full-window .scrollbar-track:hover .scrollbar-handle,
#message-list .scrollbar-track.dragging .scrollbar-handle,
.composer-full-window .scrollbar-track.dragging .scrollbar-handle {
  opacity: 0.99;
}
#message-list .scrollbar-track:hover:not(.dragging) .scrollbar-handle,
.composer-full-window .scrollbar-track:hover:not(.dragging) .scrollbar-handle {
  background: #6E6C7E;
}
#message-list .scrollbar-track:hover:not(.dragging) .scrollbar-handle:hover,
.composer-full-window .scrollbar-track:hover:not(.dragging) .scrollbar-handle:hover {
  background: #988BA2;
}
#message-list .scrollbar-track.dragging .scrollbar-handle,
.composer-full-window .scrollbar-track.dragging .scrollbar-handle {
  background: #C9CBFF;
}
