css:
.column-ThreadList {
  background: #1A1826;
}
.column-ThreadList .flexbox-handle-horizontal div {
  box-shadow: none;
}
.column-ThreadList .empty-state .perspective-empty-state .message {
  color: rgba(217, 224, 238, 0.5);
}
.column-ThreadList .empty-state .perspective-empty-state .message .btn {
  background-color: #302D41;
  border: 1px solid #161320;
  color: #C9CBFF;
  font-size: 14px;
}
.column-ThreadList .empty-state .perspective-empty-state .message .btn:hover {
  background-color: #575268;
  border: 1px solid #161320;
}
.column-ThreadList .empty-state .perspective-empty-state .message .btn:active {
  background-color: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.column-ThreadList .empty-state .perspective-empty-state img {
  background-color: rgba(217, 224, 238, 0.8);
}
.list-container .list-rows {
  background: #1A1826;
}
.thread-list .unread:not(.focused):not(.selected) .snippet {
  color: rgba(217, 224, 238, 0.8);
}
.thread-list .list-item {
  background-color: #1A1826 !important;
  border-bottom: 1px solid #161320 !important;
  color: #D9E0EE;
}
.thread-list .list-item:hover {
  background-color: #302D41 !important;
}
.thread-list .list-item.focused,
.thread-list .list-item.selected {
  background-color: #C9CBFF !important;
  color: #1A1826;
}
.thread-list .list-item.unread .participants span,
.thread-list .list-item.unread .subject {
  font-weight: bold;
}
.thread-list .list-item.keyboard-cursor {
  border-left-color: #C9CBFF;
}
body.is-blurred .thread-list .list-item.focused .participants span,
body.is-blurred .thread-list .list-item.selected .participants span,
body.is-blurred .thread-list .list-item.focused .timestamp,
body.is-blurred .thread-list .list-item.selected .timestamp,
body.is-blurred .thread-list .list-item.focused .subject,
body.is-blurred .thread-list .list-item.selected .subject,
body.is-blurred .thread-list .list-item.focused .snippet-and-labels,
body.is-blurred .thread-list .list-item.selected .snippet-and-labels {
  color: #1A1826;
}
.thread-list.handler-split .list-item.selected.unread .participants span,
.thread-list.handler-split .list-item.selected.unread .subject {
  font-weight: bold !important;
}
.list-tabular .list-tabular-item .checkmark {
  padding: 10px 11px 12px;
}
.list-tabular .list-tabular-item .checkmark .inner {
  width: 16px;
  height: 16px;
  background-image: url(mailspring://Catppuccin/styles/images/controls/checkbox-unchecked-dark.png);
  background-color: transparent;
  background-size: 16px 16px;
  border: none;
  border-radius: 0;
}
.list-tabular .list-tabular-item.selected {
  color: #1A1826;
}
.list-tabular .list-tabular-item.selected .checkmark .inner {
  width: 16px;
  height: 16px;
  background-image: url(mailspring://Catppuccin/styles/images/controls/checkbox-checked-selected.png);
  background-color: transparent;
  background-size: 16px 16px;
  border: none;
  border-radius: 0;
}
.syncing-list-state {
  color: rgba(217, 224, 238, 0.5);
}
.syncing-list-state a {
  color: #C9CBFF;
  font-size: 0.9em;
  cursor: pointer;
}
.thread-list .list-item:hover .list-column-HoverActions .inner {
  display: none;
}
.thread-list .scrollbar-track {
  background: transparent;
  border-left: 1px solid transparent;
  border-right: 1px solid transparent;
  width: 12px;
  padding: 3px 0px 3px 6px;
}
.thread-list .scrollbar-track .scrollbar-handle {
  background: #575268;
  border: none;
  border-radius: 3px;
  opacity: 0.4;
}
.thread-list .scrollbar-track:hover,
.thread-list .scrollbar-track.dragging {
  background: #302D41;
  border-left: 1px solid #161320;
  padding-left: 3px;
  padding-right: 2px;
}
.thread-list .scrollbar-track:hover .scrollbar-handle,
.thread-list .scrollbar-track.dragging .scrollbar-handle {
  opacity: 0.99;
}
.thread-list .scrollbar-track:hover:not(.dragging) .scrollbar-handle {
  background: #6E6C7E;
}
.thread-list .scrollbar-track:hover:not(.dragging) .scrollbar-handle:hover {
  background: #988BA2;
}
.thread-list .scrollbar-track.dragging .scrollbar-handle {
  background: #C9CBFF;
}
