css:
/**
 * Colors
 */
/**
 * Borders
 */
.category-mapper-container {
  width: 40%;
  min-width: 460px;
  margin: 0 auto;
}
.account-section-title {
  border-bottom: 1px solid #e6e6e6;
  margin: 13.5px 0;
}
input.search {
  border: 1px solid #e6e6e6;
  border-radius: 3px;
  background-color: #ffffff;
  box-shadow: inset 0 1px 0 rgba(0, 0, 0, 0.05), 0 1px 0 rgba(0, 0, 0, 0.05);
  color: #697278;
  padding-left: 0;
  background-repeat: no-repeat;
  background-image: url('../static/images/search/searchloupe@2x.png');
  background-size: 15px 15px;
  background-position: 7px 4px;
  text-indent: 31px;
}
.role-section {
  display: flex;
}
.role-section .col-left {
  color: #87939a;
  text-align: right;
  flex: 1;
  margin-right: 20px;
}
.role-section .col-right {
  text-align: left;
  margin-bottom: 5px;
  flex: 1;
}
