css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
.tag-picker .menu .content-container {
  height: 250px;
  overflow-y: scroll;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.down {
  margin: 0 0 0 1px;
  padding: 0 5px;
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.down:hover {
  background: rgba(221, 221, 221, 0.8);
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.down:hover {
  background: #e5e5e5;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.down.btn-icon:hover {
  color: #231f20;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.down.btn-icon:hover img.content-mask {
  background: rgba(35, 31, 32, 0.8);
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.up {
  margin: 0 0 0 1px;
  padding: 0 5px;
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.up:hover {
  background: rgba(221, 221, 221, 0.8);
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.up.btn-icon:hover {
  color: #231f20;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.up.btn-icon:hover img.content-mask {
  background: rgba(35, 31, 32, 0.8);
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.disabled:hover {
  background: transparent;
}
body.platform-win32 #message-list .message-item-wrap .message-item-white-wrap {
  border-radius: 0;
}
body.platform-win32 #message-list .minified-bundle .num-messages {
  border-radius: 0;
}
body.platform-win32 #message-list .minified-bundle .msg-line {
  border-radius: 0;
}
body.platform-win32 #message-list .footer-reply-area-wrap {
  border-radius: 0;
}
body.platform-win32 .sidebar-section {
  border-radius: 0;
}
.sheet-toolbar .message-toolbar-items {
  order: 200;
  flex-grow: 0;
  flex-shrink: 0;
}
.sheet-toolbar .message-toolbar-arrow.down {
  order: 201;
  margin-right: 0;
  margin-left: 21px;
}
.sheet-toolbar .message-toolbar-arrow.up {
  order: 202;
  margin-right: 10.5px;
}
.sheet-toolbar .message-toolbar-arrow.disabled {
  opacity: 0.3;
}
.mode-split .message-nav-title {
  display: none;
}
.hide-sidebar-button {
  font-size: 13.02px;
  color: rgba(35, 31, 32, 0.8);
  margin-left: 14px;
  cursor: default;
  user-select: none;
}
.hide-sidebar-button .img-wrap {
  margin-right: 7px;
  position: relative;
  top: -1px;
}
.hide-sidebar-button img {
  background: rgba(35, 31, 32, 0.8);
}
#message-list.height-fix {
  height: calc(100% - 35px);
  min-height: calc(100% - 35px);
}
#message-list {
  display: flex;
  flex-direction: column;
  position: relative;
  background: #f6f6f6;
  transition: all 125ms ease-in-out;
  width: 100%;
  height: 100%;
  min-height: 100%;
  padding: 0;
  order: 2;
}
#message-list.restrict-width .message-subject-wrap {
  max-width: 800px;
}
#message-list.restrict-width .message-list-headers {
  max-width: 800px;
}
#message-list.restrict-width .message-item-wrap {
  max-width: 800px;
}
#message-list.restrict-width .msg-lines {
  max-width: 800px;
}
#message-list.restrict-width .message-item-area {
  max-width: 800px;
}
#message-list.restrict-width .footer-reply-area-wrap {
  max-width: 800px;
}
#message-list.restrict-width .footer-reply-area {
  max-width: 800px;
}
#message-list search-match,
#message-list .search-match {
  background: #fff000;
  border-radius: 3px;
  box-shadow: 0 0.5px 0.5px rgba(0, 0, 0, 0.25);
}
#message-list search-match.current-match,
#message-list .search-match.current-match {
  background: #ff8b1a;
}
#message-list .show-hidden-messages {
  background-color: #ececec;
  border: 1px solid #e2e2e2;
  border-radius: 3px;
  color: rgba(35, 31, 32, 0.5);
  margin-bottom: 9px;
  cursor: default;
  padding: 5px 12px;
}
#message-list .show-hidden-messages a {
  float: right;
}
#message-list .message-body-error {
  background-color: #f6f6f6;
  border: 1px solid #e2e2e2;
  color: rgba(35, 31, 32, 0.5);
  margin-top: 9px;
  cursor: default;
  padding: 5px 12px;
}
#message-list .message-body-error a {
  float: right;
}
#message-list .message-body-loading {
  height: 1em;
  align-content: center;
  margin-top: 9px;
  margin-bottom: 9px;
}
#message-list .message-subject-wrap {
  margin: 5px auto 10px auto;
  user-select: text;
  line-height: 28.728px;
  display: flex;
  align-items: center;
  padding: 0 12px;
}
#message-list .mail-important-icon {
  margin-right: 7px;
  margin-bottom: 1px;
  flex-shrink: 0;
}
#message-list .message-subject {
  font-size: 15.96px;
  color: #231f20;
  margin-right: 14px;
}
#message-list .message-icons-wrap {
  flex-shrink: 0;
  cursor: pointer;
  user-select: none;
  margin-left: auto;
  display: flex;
  align-items: center;
}
#message-list .message-icons-wrap img {
  background: rgba(35, 31, 32, 0.8);
}
#message-list .message-icons-wrap div + div {
  margin-left: 10px;
}
#message-list .thread-injected-mail-labels {
  vertical-align: top;
  margin-top: 3px;
}
#message-list .message-list-headers {
  margin: 0 auto;
  width: 100%;
  display: block;
}
#message-list .message-list-headers .participants .contact-chip {
  display: inline-block;
}
#message-list .messages-wrap {
  flex: 1;
  opacity: 0;
  transition: opacity 0s;
}
#message-list .messages-wrap.ready {
  opacity: 1;
  transition: opacity 0.1s linear;
}
#message-list .messages-wrap .scroll-region-content-inner {
  padding: 6px;
}
#message-list .minified-bundle + .message-item-wrap {
  margin-top: -5px;
}
#message-list .message-item-wrap {
  transition: height 0.1s;
  position: relative;
  margin: 0 auto;
  padding-bottom: 12px;
}
#message-list .message-item-wrap .message-item-white-wrap {
  background: #ffffff;
  border: 0;
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08);
  border-radius: 4px;
}
#message-list .message-item-wrap.before-reply-area {
  padding-bottom: 0;
}
#message-list .message-item-wrap.collapsed .message-item-white-wrap {
  background-color: #fafafa;
  padding-top: 19px;
  padding-bottom: 8px;
  margin-bottom: 0;
}
#message-list .message-item-wrap.collapsed + .minified-bundle {
  margin-top: -6px;
}
#message-list .message-item-wrap.collapsed .message-item-area {
  padding-bottom: 10px;
  display: flex;
  flex-direction: row;
  font-size: 13.02px;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-snippet {
  flex: 1;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
  cursor: default;
  color: rgba(35, 31, 32, 0.5);
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-attachment {
  width: 15px;
  height: 15px;
  background-size: 15px;
  background-repeat: no-repeat;
  background-position: center;
  padding: 12px;
  margin-left: 0.5em;
  background-image: url(../static/images/message-list/icon-attachment-@2x.png);
  position: relative;
  top: -2px;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-from {
  font-weight: 600;
  color: rgba(35, 31, 32, 0.5);
  margin-right: 1em;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-timestamp {
  margin-left: 0.5em;
  color: rgba(35, 31, 32, 0.5);
}
#message-list .message-item-divider {
  border: 0;
  border-top: 2px solid #dddddd;
  height: 3px;
  background: #f6f6f6;
  border-bottom: 1px solid #e6e6e6;
  margin: 0;
}
#message-list .message-item-divider.collapsed {
  height: 0;
  border-bottom: 0;
}
#message-list .minified-bundle {
  position: relative;
}
#message-list .minified-bundle .num-messages {
  position: absolute;
  top: 50%;
  left: 50%;
  margin-left: -80px;
  margin-top: -15px;
  border-radius: 15px;
  border: 1px solid #dddddd;
  width: 160px;
  text-align: center;
  color: rgba(35, 31, 32, 0.5);
  z-index: 2;
  background: #ffffff;
}
#message-list .minified-bundle .num-messages:hover {
  cursor: default;
}
#message-list .minified-bundle .msg-lines {
  margin: 0 auto;
  width: 100%;
  margin-top: -13px;
}
#message-list .minified-bundle .msg-line {
  border-radius: 4px 4px 0 0;
  position: relative;
  border-top: 1px solid #dddddd;
  background-color: #fafafa;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.1), 0 -0.5px 0 rgba(0, 0, 0, 0.1), 0.5px 0 0 rgba(0, 0, 0, 0.1), -0.5px 0 0 rgba(0, 0, 0, 0.1);
}
#message-list .message-header {
  position: relative;
  font-size: 13.02px;
  padding-bottom: 0;
  padding-top: 19px;
}
#message-list .message-header.pending .message-actions-wrap {
  width: 0;
  opacity: 0;
  position: absolute;
}
#message-list .message-header.pending .pending-spinner {
  opacity: 1;
}
#message-list .message-header .pending-spinner {
  transition: opacity 100ms;
  transition-delay: 50ms, 0ms;
  transition-timing-function: ease-in;
  pointer-events: none;
  opacity: 0;
}
#message-list .message-header .header-row {
  margin-top: 0.2em;
  color: rgba(35, 31, 32, 0.5);
}
#message-list .message-header .header-row .header-label {
  float: left;
  display: block;
  font-weight: 400;
  margin-left: 0;
}
#message-list .message-header .message-actions-wrap {
  transition: opacity 100ms, width 150ms;
  transition-delay: 50ms, 0ms;
  transition-timing-function: ease-in-out;
  opacity: 1;
  text-align: left;
}
#message-list .message-header .message-actions-ellipsis {
  display: block;
  float: left;
}
#message-list .message-header .message-actions {
  display: inline-block;
  height: 23px;
  border: 1px solid #ececec;
  border-radius: 11px;
  z-index: 4;
  margin-top: 0.35em;
  margin-left: 0.5em;
  text-align: center;
}
#message-list .message-header .message-actions .btn-icon {
  opacity: 0.75;
  padding: 0 7px;
  height: 20px;
  line-height: 10px;
  border-radius: 0;
  border-right: 1px solid #ececec;
  margin: 0;
}
#message-list .message-header .message-actions .btn-icon:last-child {
  border-right: 0;
}
#message-list .message-header .message-actions .btn-icon:active {
  background: transparent;
}
#message-list .message-header .message-time {
  z-index: 2;
  position: relative;
  display: inline-block;
  min-width: 125px;
  cursor: default;
}
#message-list .message-header .msg-actions-tooltip {
  display: inline-block;
  margin-left: 1em;
}
#message-list .message-header .message-time,
#message-list .message-header .message-indicator {
  color: rgba(35, 31, 32, 0.5);
}
#message-list .message-header .message-header-right {
  z-index: 4;
  position: relative;
  top: -2px;
  float: right;
  text-align: right;
  display: flex;
  align-items: baseline;
  height: 1em;
}
#message-list .message-item-area {
  width: 100%;
  margin: 0 auto;
  padding: 0 20px 14px 20px;
}
#message-list .message-item-area .message-iframe-container {
  margin-top: 10px;
}
#message-list .message-item-area iframe {
  width: 100%;
  border: 0;
  padding: 0;
  overflow: auto;
}
#message-list .collapse-region {
  width: calc(100% - 30px);
  height: 56px;
  position: absolute;
  top: 0;
}
#message-list .header-toggle-control {
  z-index: 3;
  position: absolute;
  top: 0;
  left: -13px;
}
#message-list .header-toggle-control.inactive {
  display: none;
}
#message-list .header-toggle-control img {
  background: rgba(35, 31, 32, 0.5);
}
#message-list .message-item-wrap:hover .header-toggle-control.inactive {
  display: block;
}
#message-list .footer-reply-area-wrap {
  overflow: hidden;
  margin: -3px auto 10px auto;
  position: relative;
  z-index: 2;
  border: 0;
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08);
  border-top: 1px dashed #dddddd;
  border-radius: 0 0 4px 4px;
  background: #ffffff;
  color: rgba(35, 31, 32, 0.5);
}
#message-list .footer-reply-area-wrap img.content-mask {
  background-color: rgba(35, 31, 32, 0.5);
}
#message-list .footer-reply-area-wrap:hover {
  cursor: default;
}
#message-list .footer-reply-area-wrap .footer-reply-area {
  width: 100%;
  margin: 0 auto;
  padding: 12px 21px;
}
#message-list .footer-reply-area-wrap .reply-text {
  display: inline-block;
  vertical-align: middle;
  margin-left: 0.5em;
}
.download-all {
  display: flex;
  align-items: center;
  color: rgba(146, 146, 146, 0.8);
  font-size: 0.9em;
  cursor: default;
  margin-top: 10.5px;
}
.download-all .separator {
  margin: 0 5px;
}
.download-all .attachment-number {
  display: flex;
  align-items: center;
}
.download-all img {
  vertical-align: middle;
  margin-right: 7px;
  background-color: rgba(146, 146, 146, 0.8);
}
.download-all .download-all-action:hover {
  color: rgba(49, 135, 225, 0.8);
}
.download-all .download-all-action:hover img {
  background-color: rgba(49, 135, 225, 0.8);
}
.attachments-area {
  padding-top: 9px;
  margin-left: -14px;
  margin-right: -14px;
  cursor: default;
}
.pending .message-participants {
  padding-left: 27px;
}
.message-participants {
  z-index: 1;
  display: flex;
  transition: padding-left 150ms;
  transition-timing-function: ease-in-out;
}
.message-participants.collapsed:hover {
  cursor: default;
}
.message-participants .from-contact {
  font-weight: 600;
  color: #231f20;
}
.message-participants .from-label,
.message-participants .to-label,
.message-participants .reply-to-label,
.message-participants .cc-label,
.message-participants .bcc-label {
  color: rgba(35, 31, 32, 0.5);
}
.message-participants .to-contact,
.message-participants .reply-to-contact,
.message-participants .cc-contact,
.message-participants .bcc-contact,
.message-participants .to-everyone {
  color: rgba(35, 31, 32, 0.5);
}
.message-participants.from-participants .expanded-participants .participant-type {
  margin-top: 0;
}
.message-participants.to-participants {
  width: 100%;
}
.message-participants.to-participants .collapsed-participants {
  width: 100%;
}
.message-participants .collapsed-participants {
  display: flex;
  align-items: center;
}
.message-participants .collapsed-participants .to-contact {
  display: inline-block;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
}
.message-participants .expanded-participants {
  padding-right: 1.2em;
  width: 100%;
}
.message-participants .expanded-participants .participant {
  display: inline-block;
  margin-right: 0.25em;
}
.message-participants .expanded-participants .participant-type {
  margin-top: 0.2em;
}
.message-participants .expanded-participants .from-label,
.message-participants .expanded-participants .to-label,
.message-participants .expanded-participants .reply-to-label,
.message-participants .expanded-participants .cc-label,
.message-participants .expanded-participants .bcc-label {
  float: left;
  display: block;
  text-transform: capitalize;
  font-weight: 400;
  margin-left: 0;
}
.message-participants .expanded-participants .from-contact,
.message-participants .expanded-participants .subject {
  font-weight: 600;
}
.message-participants .expanded-participants .to-label,
.message-participants .expanded-participants .reply-to-label,
.message-participants .expanded-participants .cc-label {
  margin-right: 0.5em;
}
.message-participants .expanded-participants .bcc-label {
  margin-right: 0;
}
.message-participants .expanded-participants .participant-primary {
  color: rgba(35, 31, 32, 0.5);
  margin-right: 0.15em;
  display: inline-block;
}
.message-participants .expanded-participants .participant-secondary {
  color: rgba(35, 31, 32, 0.5);
  display: inline-block;
}
.message-participants .expanded-participants .from-contact .participant-primary {
  color: #231f20;
}
.message-participants .expanded-participants .from-contact .participant-secondary {
  color: #231f20;
}
.sidebar-section {
  opacity: 0;
  margin: 5px;
  cursor: default;
  border: 1px solid #e6e6e6;
  border-radius: 5px;
  background: #ffffff;
  padding: 15px;
}
.sidebar-section.visible {
  transition: opacity 0.1s ease-out;
  opacity: 1;
}
.sidebar-section h2 {
  font-size: 11px;
  font-weight: 600;
  text-transform: uppercase;
  color: rgba(35, 31, 32, 0.5);
  margin: 0 0 18px 0;
  position: relative;
}
.sidebar-section h2:after {
  content: ' ';
  background-image: url(images/sidebar/sidebar-section-divider@2x.png);
  background-size: 100%;
  background-repeat: repeat-x;
  background-color: transparent;
  position: absolute;
  left: -15px;
  bottom: -10px;
  width: calc(100% + 30px);
  height: 3px;
}
.sidebar-section h2:first-child {
  margin-top: 0;
}
.sidebar-participant-picker {
  padding: 10px 5px 20px 5px;
  text-align: right;
}
.sidebar-participant-picker select {
  max-width: 100%;
  width: 100%;
}
.column-MessageListSidebar {
  background-color: #f6f6f6;
  overflow: auto;
  border-left: 1px solid #dddddd;
  color: rgba(35, 31, 32, 0.8);
}
.column-MessageListSidebar .flexbox-handle-horizontal div {
  border-right: 0;
  width: 1px;
}
