css:
/*
Include the standard variables from Mailspring's base theme:
https://github.com/Foundry376/Mailspring/blob/master/app/static/base/ui-variables.less#L37:L40
*/
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(67, 76, 94, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(67, 76, 94, 0.8);
}
.translate-language-picker .footer-container {
  display: none;
}
.translate-language-picker .content-container {
  height: 185px;
  width: 170px;
  overflow: scroll;
}
.translate-message-header {
  display: flex;
  align-items: stretch;
  background-color: #354a63;
  color: rgba(66, 171, 248, 0.92);
  margin: 10px 0;
}
.translate-message-header .message {
  flex: 1;
  border: 1px solid #0b263c;
  border-radius: 3px;
  padding: 2px 8px;
  display: flex;
  align-items: center;
}
.translate-message-header .message.with-actions {
  border-right: 0;
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
}
.translate-message-header .message .message-centered .note {
  font-size: 0.85rem;
  opacity: 0.7;
}
.translate-message-header .actions {
  display: flex;
  flex-direction: column;
}
.translate-message-header .actions .action {
  padding: 0 8px;
  min-height: 27px;
  background: #2f5375;
  border: 1px solid #0b263c;
  border-bottom: 0;
  display: flex;
  align-items: center;
  min-width: 90px;
  flex: 1;
}
.translate-message-header .actions .action:active {
  background: #295b86;
}
.translate-message-header .actions .action.button-dropdown {
  display: inline-block;
  padding: 0;
}
.translate-message-header .actions .action.button-dropdown .only-item {
  border: none;
  padding: 0;
  margin: 0;
  box-shadow: none;
  background: transparent;
  color: inherit;
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 100%;
  padding: 0 8px;
}
.translate-message-header .actions .action.button-dropdown .only-item img.content-mask {
  background: rgba(66, 171, 248, 0.92);
}
.translate-message-header .actions .action:first-child {
  border-top-right-radius: 3px;
}
.translate-message-header .actions .action:last-child {
  border-bottom-right-radius: 3px;
  border-bottom: 1px solid #0b263c;
}
