css:
/*
Include the standard variables from Mailspring's base theme:
https://github.com/Foundry376/Mailspring/blob/master/app/static/base/ui-variables.less#L37:L40
*/
.preferences-signatures-container {
  max-width: 800px;
  margin: 0 auto;
}
.preferences-signatures-container .empty-list {
  height: 300px;
  width: inherit;
  background-color: #3B4252;
  border: 1px solid #434c5e;
  text-align: center;
  padding: 60px;
}
.preferences-signatures-container .empty-list .icon-signature {
  height: 120px;
}
.preferences-signatures-container .empty-list h2 {
  color: rgba(216, 222, 233, 0.6);
}
.preferences-signatures-container .empty-list .btn {
  margin-top: 10px;
}
.preferences-signatures-container .signature-list {
  position: relative;
  height: inherit;
  width: inherit;
}
.preferences-signatures-container .signature-list .items-wrapper {
  min-width: 200px;
  height: 262px;
}
.preferences-signatures-container .signature-list .item-rule-disabled {
  color: #d9534f;
  padding: 4px 10px;
  border-bottom: 1px solid #434c5e;
}
.preferences-signatures-container .signature-list .selected .item-rule-disabled {
  color: #3B4252;
}
.preferences-signatures-container .signature-list .editable-item {
  padding: 8px 10px;
  border-bottom: none;
}
.preferences-signatures-container .signature-list .btn-editable-list {
  height: 37px;
  width: 37px;
  line-height: 37px;
  font-size: 1em;
}
.preferences-signatures-container .signature-wrap {
  display: flex;
  position: relative;
  margin-left: 12px;
  flex-direction: column;
  overflow: hidden;
  min-height: 200px;
  width: 100%;
}
.preferences-signatures-container .signature-wrap.empty {
  opacity: 0.5;
  pointer-events: none;
}
.preferences-signatures-container .signature-wrap .section {
  background-color: #3B4252;
  border: 1px solid #434c5e;
}
.preferences-signatures-container .signature-wrap .section.basic-info {
  background: none;
  border: none;
  display: flex;
  white-space: nowrap;
  margin-bottom: 12px;
}
.preferences-signatures-container .signature-wrap .section.basic-info input {
  max-width: 300px;
}
.preferences-signatures-container .signature-wrap .section-header {
  margin-top: 10px;
}
.preferences-signatures-container .signature-wrap .section-header:first-child {
  margin-top: 0;
}
.preferences-signatures-container .signature-wrap textarea.raw-html {
  font-family: 'DejaVu Sans Mono', Menlo, Monaco, Consolas, 'Courier New', monospace;
  font-size: 0.9em;
  width: 100%;
  height: 240px;
  padding: 12px;
}
.preferences-signatures-container .signature-wrap .signature-template-picker {
  height: 58px;
  overflow-x: scroll;
  overflow-y: hidden;
  background: #2e333f;
  white-space: nowrap;
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option {
  width: 100px;
  height: 50px;
  overflow: hidden;
  display: inline-block;
  border: 1px solid #434c5e;
  background: #3B4252;
  position: relative;
  margin-top: 4px;
  margin-left: 4px;
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option:last-child {
  margin-right: 4px;
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option .centered {
  align-items: center;
  justify-content: center;
  white-space: initial;
  flex-shrink: 0;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  position: absolute;
  display: flex;
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option .centered .preview {
  pointer-events: none;
  transform: scale(0.25);
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option.active {
  border: 1px solid #6fa9a8;
}
.preferences-signatures-container .signature-wrap .section.preview {
  padding: 12px;
  min-height: 180px;
}
.preferences-signatures-container .signature-wrap .section.preview .label {
  font-size: 0.9em;
  color: rgba(216, 222, 233, 0.6);
  float: right;
  border-radius: 5px;
  padding: 0 5px;
  color: lightgray;
}
.preferences-signatures-container .signature-wrap .section.information {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  padding: 12px 0;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker {
  display: block;
  width: 100%;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-well {
  width: 60px;
  height: 60px;
  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
  border: 1px solid rgba(80, 90, 112, 0.9);
  margin-right: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-well:hover,
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-well.dropping {
  border: 1px solid #6fa9a8;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-options {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: baseline;
  justify-content: space-between;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker select {
  margin: 0;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .drop-note {
  font-size: 12.04px;
  color: rgba(216, 222, 233, 0.6);
  margin-top: 4px;
}
.preferences-signatures-container .signature-wrap .section.information .field {
  padding: 5px 12px;
  width: 50%;
}
.preferences-signatures-container .signature-wrap .section.information .field label {
  color: rgba(216, 222, 233, 0.6);
  margin-right: 20px;
}
.preferences-signatures-container .signature-wrap .account-picker {
  display: inline-block;
  color: rgba(216, 222, 233, 0.8);
  margin-left: 12px;
  margin-right: 2px;
}
.preferences-signatures-container .signature-wrap .account-picker .button-dropdown .secondary-items .menu .item {
  padding: 5px 10px 5px 25px;
}
.preferences-signatures-container .signature-wrap .account-picker .button-dropdown .secondary-items .menu .item.checked {
  background-image: url(images/menu/osx-checkmark@2x.svg);
  background-position: left;
  background-position-x: 6%;
  background-size: 10px;
  margin: 0;
  padding: 5px 10px 5px 25px;
  background-repeat: no-repeat;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component {
  display: inline-block;
  float: right;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item {
  background: #43474f;
  box-shadow: none;
  padding-right: 0;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown img.content-mask {
  display: none;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown img.signature-button {
  display: inline-block;
  background-color: #919191;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown.open img.signature-button {
  background-color: #6fa9a8;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items {
  right: 0;
  left: auto;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .header-container {
  display: inline-block;
  padding: 0;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .footer-container {
  display: inline-block;
  border-top: 1px solid #dddddd;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .item {
  padding: 5px 20px 5px 20px;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .item.checked {
  background-image: url(images/menu/osx-checkmark@2x.svg);
  background-position: left;
  background-position-x: 5%;
  background-size: 10px;
  background-repeat: no-repeat;
}
.message-item-white-wrap.composer-outer-wrap.focused .composer-participant-field .dropdown-component .only-item {
  background: #3B4252;
}
.composer-full-window .message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item {
  background: #3B4252;
}
