css:
.message-subject-wrap .thread-avatar,
.thread-list-wide .list-column-Message .thread-avatar,
.icons-column .thread-avatar {
  display: none !important;
}
.thread-list-narrow .list-tabular-item.focused .thread-avatar {
  transform: translate3d(0, 0px, 0px) scale(0.8);
}
.thread-list-narrow .thread-injected-mail-labels .thread-avatar {
  margin: 1px;
  position: fixed !important;
  top: 8px;
  left: 7px;
  transform: translate3d(0, 0px, 0px) scale(0.8);
  transition: all 0.15s cubic-bezier(0.35, 0.91, 0.41, 0.99);
}
.thread-list-narrow .list-tabular-item.focused .thread-avatar {
  transition: all 0.15s cubic-bezier(0.35, 0.91, 0.41, 0.99);
}
.thread-list-narrow .icons-column {
  width: 45px;
  left: 0;
  top: 0;
  flex-direction: row;
  position: absolute;
  margin-right: 0;
  z-index: 10;
}
.thread-list-narrow .list-tabular .list-column.list-column-Item {
  padding-left: 20px;
}
.thread-list-narrow .subject,
.thread-list-narrow .participants-wrapper,
.thread-list-narrow .snippet-and-labels {
  margin-left: 35px;
}
.thread-list-narrow .mail-important-icon {
  margin-left: 0;
  padding: 0;
}
/** Star & Important icons **/
.thread-list .list-item,
.draft-list .list-item {
  line-height: unset;
}
.thread-list .thread-icon,
.draft-list .thread-icon {
  width: 25px !important;
  height: 25px;
}
.thread-list .mail-important-icon,
.draft-list .mail-important-icon {
  margin-left: 0 !important;
  margin-right: 5px !important;
}
.thread-list .list-column-Item .mail-important-icon,
.draft-list .list-column-Item .mail-important-icon {
  margin-left: 5px !important;
  margin-right: 0 !important;
}
/** Eye icon **/
.open-tracking-icon {
  position: absolute;
  top: 18px;
  left: 22px;
}
.list-column-Item .open-tracking-icon {
  top: 23px;
  left: 4px;
}
.thread-injected-icons > span {
  width: 0 !important;
}
