css:
.sheet-toolbar {
  position: relative;
  -webkit-app-region: drag;
  border-bottom: 1px solid #1E1E2E;
}
.column-MessageListSidebar {
  border-left: transparent;
}
.flexbox-handle-horizontal div {
  height: 100%;
  box-shadow: transparent;
}
#inbox-html-wrapper > :not(table) a,
#inbox-plain-wrapper > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a {
  color: #313244;
}
/* Badge with counter, secondary */
.item-count-box:not(.selected):not(.focused) {
  color: #1E1E2E;
  box-shadow: 0 0.5px 0 #181825, 0 -0.5px 0 #181825, 0.5px 0 0 #181825, -0.5px 0 0 #181825;
  background: #B4BEFE;
}
/* Badge with counter, primary, selected */
.outline-view .item .item-count-box.alt-count {
  color: #B4BEFE;
  background: #1E1E2E;
  box-shadow: none;
}
/* Badge with counter, primary, not selected */
.outline-view .item .item-count-box.alt-count:not(.selected):not(.focused) {
  color: #1E1E2E;
  background: #B4BEFE;
  box-shadow: none;
}
/* tracking icon, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, unopened, focused */
.list-item.focused .open-tracking-icon img.content-mask.unopened,
.list-item.selected .open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, opened, unfocused */
.open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, opened, focused */
.list-item.focused .open-tracking-icon img.content-mask.opened,
.list-item.selected .open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, message list, opened */
.open-tracking-message-status.opened img.content-mask {
  background-color: #CDD6F4;
}
/* tracking icon, mail list, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
.toolbar-activity .unread-count.active {
  background: #B4BEFE;
  color: #1E1E2E;
}
.open-tracking-message-status.opened {
  color: #CDD6F4;
}
.open-tracking-message-status.unopened {
  color: #7F849C;
}
.account-switcher img {
  zoom: 1 !important;
  max-width: 10px;
  max-height: 6px;
  transform: none;
  background-image: none;
  background-color: #7F849C;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="10" height="6" viewBox="0 0 10 6"><path fill="#FFF" d="M0 0h1l4 4 4-4h1v1L5 6 0 1z"/></svg>');
}
.toolbar-activity .activity-toolbar-icon {
  background: #A6ADC8;
}
.div.suggestions:hover {
  color: #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item,
.button-dropdown.btn-emphasis .secondary-picker,
.button-dropdown.btn-emphasis .only-item {
  position: relative;
  color: #1E1E2E;
  font-weight: 500;
  background: #B4BEFE;
  box-shadow: none;
  border: 0px solid #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item img.content-mask,
.button-dropdown.btn-emphasis .secondary-picker img.content-mask,
.button-dropdown.btn-emphasis .only-item img.content-mask {
  background-color: #1E1E2E;
}
.button-dropdown.btn-emphasis .primary-item:active,
.button-dropdown.btn-emphasis .secondary-picker:active,
.button-dropdown.btn-emphasis .only-item:active {
  background: #B4BEFE;
}
.tokenizing-field .token {
  background: linear-gradient(to bottom, #313244 0%, #45475A 100%);
}
.tokenizing-field .token.selected,
.tokenizing-field .token.dragging {
  color: #1E1E2E;
}
.tokenizing-field .token.selected .action img,
.tokenizing-field .token.dragging .action img {
  background-color: #1E1E2E;
}
.menu .item.selected,
.menu .item:active {
  background-color: #4e4947;
  color: #CDD6F4;
}
.div.button-dropdown.open.open-down {
  background-color: #CDD6F4;
  color: #CDD6F4;
}
.menu .item.selected,
.menu .item:active {
  background-color: #B4BEFE;
  color: #1E1E2E;
}
.menu .item.selected .primary,
.menu .item:active .primary {
  color: #1E1E2E;
}
.menu .item.selected .secondary,
.menu .item:active .secondary {
  color: #313244;
}
.button-dropdown .primary-item,
.button-dropdown .only-item {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown .secondary-picker {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown.open.open-down .secondary-items {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08), 0 0 3px #B4BEFE;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content.unfocused {
  background-color: #313244;
}
.unsubscribe-action {
  color: #B4BEFE;
  text-decoration: underline;
}
.undo-redo-toast .content {
  background: #B4BEFE;
}
.preferences-wrap .container-accounts .accounts-content .account-details {
  background-color: #1E1E2E;
}
.nylas-editable-list .items-wrapper {
  background-color: #313244;
}
.thread-injected-quick-actions .btn {
  box-shadow: none;
}
.thread-list .list-item .list-column-HoverActions .action.action-trash {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-archive {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url('../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .thread-icon.thread-icon-star,
.draft-list .thread-icon.thread-icon-star {
  filter: grayscale(100%);
}
#message-list .mail-label.removable {
  color: #1E1E2E !important;
  background: #B4BEFE !important;
  box-shadow: none !important;
}
.thread-list .thread-icon.thread-icon-unread,
.draft-list .thread-icon.thread-icon-unread {
  filter: grayscale(100%);
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(24, 24, 37, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(24, 24, 37, 0.8);
}
.preferences-wrap {
  height: 100%;
  background-color: #1E1E2E;
  color: #CDD6F4;
}
.preferences-wrap input[type='checkbox'] {
  margin: 0 7px 0 0;
  position: relative;
  top: -1px;
}
.preferences-wrap input[type='radio'] {
  margin: 0 7px 0 0;
  position: relative;
  top: -1px;
}
.preferences-wrap select {
  margin: 4px 0 0 0;
}
.preferences-wrap h6 {
  color: #7F849C;
  margin-top: 20px;
  margin-bottom: 10px;
}
.preferences-wrap section:first-child h6:first-child {
  margin-top: 0;
}
.preferences-wrap p {
  color: #7F849C;
  font-size: 12.04px;
}
.preferences-wrap p a {
  color: #7F849C;
  font-weight: bold;
  text-decoration: underline;
}
.preferences-wrap *[contenteditable] p {
  color: #CDD6F4;
  font-size: 14px;
}
.preferences-wrap section {
  padding-bottom: 5px;
}
.preferences-wrap section .item {
  padding-top: 4px;
  padding-bottom: 4px;
}
.preferences-wrap .container-preference-tabs {
  width: 100%;
  background-color: #181825;
  border-bottom: 1px solid #181825;
}
.preferences-wrap .container-preference-tabs .preferences-tabs {
  padding-top: 10px;
  background-color: #181825;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item {
  cursor: default;
  margin: 0 auto;
  flex: 1.3;
  min-width: 54px;
  max-width: 120px;
  text-align: center;
  padding-top: 8px;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item:active img {
  filter: brightness(40%);
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item.active {
  background: #0a0a0f;
  border-radius: 5px 5px 0 0;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item .tab-icon {
  display: block;
  margin: auto;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item .name {
  padding: 7.5px 3.6px 9px 3.6px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 13.02px;
  line-height: 13.02px;
}
.preferences-wrap .preferences-content {
  flex: 8;
  background-color: #333447;
}
.preferences-wrap .preferences-content > .scroll-region-content {
  padding: 27px 48px;
}
.preferences-wrap .preferences-content .container-dropdown {
  margin: 5px 0;
}
.preferences-wrap .preferences-content .container-dropdown .dropdown {
  padding-left: 10px;
}
.preferences-wrap .container-general {
  width: 100%;
  min-width: 430px;
  max-width: 800px;
  margin: 0 auto;
}
.preferences-wrap .container-general .local-data .btn {
  margin: 4px 0 0 8px;
  border: 1px solid rgba(24, 24, 37, 0.9);
  box-shadow: none;
  border-radius: 5px;
}
.preferences-wrap .container-appearance {
  width: 600px;
  margin: 0 auto;
}
.preferences-wrap .container-appearance input[type='radio'] {
  vertical-align: middle;
}
.preferences-wrap .container-appearance .appearance-scale-slider .ruler {
  display: flex;
  align-items: bottom;
}
.preferences-wrap .container-appearance .appearance-scale-slider .ruler .midpoint {
  border-right: 1px solid #181825;
  width: 1px;
  flex-grow: 0;
}
.preferences-wrap .container-appearance .appearance-mode-switch {
  max-width: 600px;
  text-align: right;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode {
  background-color: #1E1E2E;
  border-radius: 10px;
  border: 1px solid #313244;
  text-align: center;
  flex: 1;
  padding: 25px;
  padding-bottom: 9px;
  margin-right: 10px;
  margin-bottom: 7px;
  margin-top: 0;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode img {
  background-color: #313244;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode div {
  margin-top: 15px;
  text-transform: capitalize;
  cursor: default;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode:last-child {
  margin-right: 0;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode.active {
  border: 1px solid #B4BEFE;
  color: #B4BEFE;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode.active img {
  background-color: #B4BEFE;
}
.preferences-wrap .container-general .two-columns-flexbox,
.preferences-wrap .container-keymaps .two-columns-flexbox {
  display: flex;
}
.preferences-wrap .container-keymaps {
  width: 100%;
  max-width: 800px;
  min-width: 430px;
  margin: 0 auto;
}
.preferences-wrap .container-keymaps .col-left {
  text-align: right;
  flex: 1;
  margin-right: 20px;
}
.preferences-wrap .container-keymaps .col-right {
  text-align: left;
  flex: 1;
}
.preferences-wrap .container-keymaps .col-right select {
  width: 75%;
}
.preferences-wrap .container-keymaps .shortcut-section-title {
  border-bottom: 1px solid #181825;
  margin: 13.5px 0;
}
.preferences-wrap .container-keymaps .shortcut {
  padding: 3px 0;
  color: #7F849C;
}
.preferences-wrap .container-keymaps .shortcut .values {
  font-family: 'DejaVu Sans Mono', Menlo, Monaco, Consolas, 'Courier New', monospace;
  font-weight: 600;
  color: #CDD6F4;
  display: inline-block;
  padding-left: 10px;
  padding-right: 10px;
  cursor: text;
}
.preferences-wrap .container-keymaps .shortcut .values .shortcut-value .then {
  font-size: 0.9em;
  color: #7F849C;
}
.preferences-wrap .container-keymaps .shortcut .values .shortcut-value:after {
  content: ', ';
}
.preferences-wrap .container-keymaps .shortcut .values .shortcut-value:last-child:after {
  content: '';
}
.preferences-wrap .container-keymaps .shortcut:hover .values {
  background: #313244;
  border-radius: 3px;
  outline: 1px solid rgba(24, 24, 37, 0.9);
}
.preferences-wrap .container-keymaps .shortcut.editing .values {
  background: #313244;
  color: #B4BEFE;
  border-radius: 3px;
  outline: 1px solid rgba(24, 24, 37, 0.9);
}
.preferences-wrap .platform-note {
  padding: 5px 12px;
  background: rgba(35, 31, 32, 0.04);
  border-left: 3px solid #3187e1;
  margin: 5px 0;
  font-size: 0.95em;
}
.preferences-wrap .platform-note:before {
  color: #3187e1;
  font-weight: 600;
  content: 'NOTE: ';
}
.preferences-wrap .platform-linux-only {
  display: none;
}
body.platform-win32 .preferences-wrap .well {
  border-radius: 0;
}
body.platform-win32 .preferences-wrap .container-appearance .appearance-mode {
  border-radius: 0;
}
body.platform-linux .preferences-wrap .platform-linux-only {
  display: block;
}
@media (max-width: 680px) {
  .preferences-wrap .container-keymaps .two-columns-flexbox,
  .preferences-wrap .container-general .two-columns-flexbox {
    flex-direction: column;
  }
  .preferences-wrap .container-preference-tabs .preferences-tabs .item img.tab-icon {
    margin-bottom: 10px;
  }
  .preferences-wrap .container-preference-tabs .preferences-tabs .item .name {
    display: none;
  }
  .preferences-wrap .preferences-content > .scroll-region-content {
    padding-left: 16px;
    padding-right: 16px;
  }
}
