css:
.sheet-toolbar {
  position: relative;
  -webkit-app-region: drag;
  border-bottom: 1px solid #1E1E2E;
}
.column-MessageListSidebar {
  border-left: transparent;
}
.flexbox-handle-horizontal div {
  height: 100%;
  box-shadow: transparent;
}
#inbox-html-wrapper > :not(table) a,
#inbox-plain-wrapper > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a {
  color: #313244;
}
/* Badge with counter, secondary */
.item-count-box:not(.selected):not(.focused) {
  color: #1E1E2E;
  box-shadow: 0 0.5px 0 #181825, 0 -0.5px 0 #181825, 0.5px 0 0 #181825, -0.5px 0 0 #181825;
  background: #B4BEFE;
}
/* Badge with counter, primary, selected */
.outline-view .item .item-count-box.alt-count {
  color: #B4BEFE;
  background: #1E1E2E;
  box-shadow: none;
}
/* Badge with counter, primary, not selected */
.outline-view .item .item-count-box.alt-count:not(.selected):not(.focused) {
  color: #1E1E2E;
  background: #B4BEFE;
  box-shadow: none;
}
/* tracking icon, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, unopened, focused */
.list-item.focused .open-tracking-icon img.content-mask.unopened,
.list-item.selected .open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, opened, unfocused */
.open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, opened, focused */
.list-item.focused .open-tracking-icon img.content-mask.opened,
.list-item.selected .open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, message list, opened */
.open-tracking-message-status.opened img.content-mask {
  background-color: #CDD6F4;
}
/* tracking icon, mail list, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
.toolbar-activity .unread-count.active {
  background: #B4BEFE;
  color: #1E1E2E;
}
.open-tracking-message-status.opened {
  color: #CDD6F4;
}
.open-tracking-message-status.unopened {
  color: #7F849C;
}
.account-switcher img {
  zoom: 1 !important;
  max-width: 10px;
  max-height: 6px;
  transform: none;
  background-image: none;
  background-color: #7F849C;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="10" height="6" viewBox="0 0 10 6"><path fill="#FFF" d="M0 0h1l4 4 4-4h1v1L5 6 0 1z"/></svg>');
}
.toolbar-activity .activity-toolbar-icon {
  background: #A6ADC8;
}
.div.suggestions:hover {
  color: #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item,
.button-dropdown.btn-emphasis .secondary-picker,
.button-dropdown.btn-emphasis .only-item {
  position: relative;
  color: #1E1E2E;
  font-weight: 500;
  background: #B4BEFE;
  box-shadow: none;
  border: 0px solid #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item img.content-mask,
.button-dropdown.btn-emphasis .secondary-picker img.content-mask,
.button-dropdown.btn-emphasis .only-item img.content-mask {
  background-color: #1E1E2E;
}
.button-dropdown.btn-emphasis .primary-item:active,
.button-dropdown.btn-emphasis .secondary-picker:active,
.button-dropdown.btn-emphasis .only-item:active {
  background: #B4BEFE;
}
.tokenizing-field .token {
  background: linear-gradient(to bottom, #313244 0%, #45475A 100%);
}
.tokenizing-field .token.selected,
.tokenizing-field .token.dragging {
  color: #1E1E2E;
}
.tokenizing-field .token.selected .action img,
.tokenizing-field .token.dragging .action img {
  background-color: #1E1E2E;
}
.menu .item.selected,
.menu .item:active {
  background-color: #4e4947;
  color: #CDD6F4;
}
.div.button-dropdown.open.open-down {
  background-color: #CDD6F4;
  color: #CDD6F4;
}
.menu .item.selected,
.menu .item:active {
  background-color: #B4BEFE;
  color: #1E1E2E;
}
.menu .item.selected .primary,
.menu .item:active .primary {
  color: #1E1E2E;
}
.menu .item.selected .secondary,
.menu .item:active .secondary {
  color: #313244;
}
.button-dropdown .primary-item,
.button-dropdown .only-item {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown .secondary-picker {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown.open.open-down .secondary-items {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08), 0 0 3px #B4BEFE;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content.unfocused {
  background-color: #313244;
}
.unsubscribe-action {
  color: #B4BEFE;
  text-decoration: underline;
}
.undo-redo-toast .content {
  background: #B4BEFE;
}
.preferences-wrap .container-accounts .accounts-content .account-details {
  background-color: #1E1E2E;
}
.nylas-editable-list .items-wrapper {
  background-color: #313244;
}
.thread-injected-quick-actions .btn {
  box-shadow: none;
}
.thread-list .list-item .list-column-HoverActions .action.action-trash {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-archive {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url('../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .thread-icon.thread-icon-star,
.draft-list .thread-icon.thread-icon-star {
  filter: grayscale(100%);
}
#message-list .mail-label.removable {
  color: #1E1E2E !important;
  background: #B4BEFE !important;
  box-shadow: none !important;
}
.thread-list .thread-icon.thread-icon-unread,
.draft-list .thread-icon.thread-icon-unread {
  filter: grayscale(100%);
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
@keyframes indicator-animation {
  0% {
    background-position-x: 200px;
  }
  100% {
    background-position-x: 760px;
  }
}
.activity-dashboard {
  height: 100%;
  color: #CDD6F4;
}
.activity-dashboard .header {
  margin: 20px;
  margin-bottom: 6px;
  display: flex;
}
.activity-dashboard .header .accounts {
  font-size: 14px;
  color: #7F849C;
}
.activity-dashboard .header h2 {
  margin: 0;
}
.activity-dashboard .header .timespan-selector {
  display: flex;
  align-items: baseline;
}
.activity-dashboard .header .timespan-selector .timespan-text {
  color: #7F849C;
}
.activity-dashboard .header .timespan-selector .dropdown-menu {
  margin-left: 24px;
}
.activity-dashboard .header .dropdown-menu .menu {
  width: 160px;
}
.activity-dashboard .usage-note {
  color: #b5adb0;
  padding: 5px 12px;
  background: #332c31;
  border-left: 4px solid #43362d;
  margin: 0 20px;
  margin-top: 20px;
}
.activity-dashboard .empty-note {
  color: #7F849C;
  text-align: center;
  max-width: 500px;
  margin: 50px auto;
}
.activity-dashboard .copy-to-clipboard {
  margin-left: 9px;
  position: relative;
  display: inline-block;
  cursor: default;
  top: -2px;
}
.activity-dashboard .copy-to-clipboard img {
  background-color: #919191;
}
.activity-dashboard .copy-to-clipboard img:active {
  background-color: #231f20;
}
.activity-dashboard .loading-cover {
  opacity: 0;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
  background-color: rgba(30, 30, 46, 0.82);
  transition: opacity 200ms linear;
  z-index: 5;
}
.activity-dashboard .loading-cover.active {
  pointer-events: auto;
  opacity: 1;
}
.activity-dashboard .loading-cover.active .loading-indicator img {
  animation: indicator-animation 1s linear infinite;
}
.activity-dashboard .loading-indicator {
  position: absolute;
  top: 30vh;
  left: 50%;
  transform: translate(-50%, -50%);
}
.activity-dashboard .loading-indicator img {
  background-image: linear-gradient(105deg, #1E1E2E 0%, #1E1E2E 15%, #B4BEFE 45%, #1E1E2E 49%, #1E1E2E 100%);
  background-position-x: 200px;
  background-size: 590px;
}
.activity-dashboard .section {
  margin-bottom: 25px;
}
.activity-dashboard .section:last-child {
  margin-bottom: 45px;
}
.activity-dashboard .section-divider {
  margin: 0 20px 15px 20px;
  border-bottom: 1px solid #181825;
}
.activity-dashboard .section-divider div {
  font-size: 15.4px;
  font-weight: 500;
  color: #CDD6F4;
  background: #1E1E2E;
  position: relative;
  top: 10px;
  display: inline-block;
  padding-right: 10px;
}
.activity-dashboard .metric-container {
  flex: 1;
  display: flex;
  flex-direction: column;
  margin: 20px;
}
.activity-dashboard .metric-container .footer {
  border-top: 1px solid #181825;
  color: #7F849C;
}
.activity-dashboard .metric-container .layer {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
}
.activity-dashboard .metric-histogram {
  position: relative;
  height: 120px;
}
.activity-dashboard .metric-histogram .legend {
  display: flex;
  font-size: 14px;
  color: #7F849C;
}
.activity-dashboard .metric-histogram .column {
  display: inline-block;
  background: rgba(180, 190, 254, 0.15);
  border-top: 2px solid #B4BEFE;
  transform-origin: bottom;
  transform: scaleY(0.2);
  opacity: 0;
  transition: opacity 1s ease-out, transform 1s ease-out;
}
.activity-dashboard .metric-histogram.visible .column {
  transform: scaleY(1);
  opacity: 1;
}
.activity-dashboard .metric-stat {
  height: 120px;
  position: relative;
}
.activity-dashboard .metric-stat.read-receipts img {
  background-color: rgba(124, 25, 204, 0.2);
}
.activity-dashboard .metric-stat.link-tracking img {
  background-color: rgba(212, 51, 83, 0.2);
}
.activity-dashboard .metric-stat.replies img {
  background-color: rgba(205, 214, 244, 0.2);
}
.activity-dashboard .metric-stat .layer.text-overlay {
  font-size: 60px;
  display: flex;
  align-items: flex-end;
}
.activity-dashboard .metric-stat .layer.text-overlay .text {
  line-height: 1em;
}
.activity-dashboard .metric-graph {
  position: relative;
  height: 120px;
  transform: scaleX(1) /*rtl: scaleX(-1) */;
}
.activity-dashboard .metric-graph .gridline {
  width: 1;
  height: 100%;
  position: absolute;
  border-left: 1px solid #4f5165;
}
.activity-dashboard .metric-graph .layer.text-overlay {
  background: linear-gradient(to right, #1e1e2e 15%, rgba(30, 30, 46, 0) 100%) /*rtl:ignore */;
  font-size: 60px;
  display: flex;
  align-items: flex-end;
}
.activity-dashboard .metric-graph .layer.text-overlay .text {
  line-height: 1em;
}
.activity-dashboard .metric-graph path {
  stroke-dasharray: 1000 10000;
  stroke-dashoffset: -200;
  stroke: #B4BEFE;
  stroke-width: 0.4pt;
  stroke-linejoin: round;
  stroke-linecap: round;
  fill: none;
  fill-rule: evenodd;
  transition: stroke-dashoffset ease-out 1500ms;
}
.activity-dashboard .metric-graph.visible path {
  stroke-dashoffset: 0;
}
.activity-dashboard .table-container {
  width: 100%;
  margin: 15px 20px 40px 20px;
}
.activity-dashboard .table-container table {
  width: 100%;
}
.activity-dashboard .table-container table th {
  text-align: left;
  color: #7F849C;
  font-weight: 400;
}
.activity-dashboard .table-container table tr {
  border-bottom: 1px solid rgba(24, 24, 37, 0.25);
}
.activity-dashboard .table-container table td {
  line-height: 34px;
}
.activity-dashboard .table-container table td.ellipsis {
  position: relative;
}
.activity-dashboard .table-container table td.ellipsis span {
  position: absolute;
  top: 0;
  left: 0;
  right: 12px;
  bottom: 0;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.activity-dashboard .table-container table td .empty {
  color: #7F849C;
}
.toolbar-activity {
  order: 100;
  position: relative;
}
.toolbar-activity .unread-count {
  display: none;
}
.toolbar-activity .unread-count.active {
  display: inline-block;
  background: #B4BEFE;
  text-align: center;
  color: #ffffff;
  border-radius: 3px;
  font-size: 8px;
  padding: 0 4px;
  position: absolute;
  right: -7px;
  top: 5px;
  line-height: 11px;
}
.toolbar-activity .activity-toolbar-icon {
  margin-top: 20px;
  background: #586069;
}
.toolbar-activity .activity-toolbar-icon.unread {
  background: #B4BEFE;
}
.activity-list-container {
  width: 260px;
  overflow: hidden;
  font-size: 13.02px;
  color: #A6ADC8;
  height: 282px;
}
.activity-list-container .spacer {
  flex: 1 1 0;
}
.activity-list-container.empty {
  height: 182px;
}
.activity-list-container.empty .activity-summary-cta {
  display: none;
}
.activity-list-container .activity-summary-cta {
  border-top: 1px solid #181825;
  border-bottom-right-radius: 3px;
  border-bottom-left-radius: 3px;
  background: #313244;
  text-align: center;
  padding: 5px 12px;
  color: #CDD6F4;
}
.activity-list-container .activity-summary-cta img {
  vertical-align: baseline;
}
.activity-list-container .activity-summary-cta:active {
  background: #1e1e29;
}
.activity-list-container .empty-state-container {
  text-align: center;
  padding: 24px;
  padding-top: 40px;
}
.activity-list-container .empty-state-container img.logo {
  background-color: #7F849C;
}
.activity-list-container .empty-state-container .text {
  margin-top: 30px;
  color: #7F849C;
}
.activity-list-container .activity-list-item {
  padding: 4px 10px;
  white-space: nowrap;
  border-bottom: 1px solid #181825;
  cursor: default;
}
.activity-list-container .activity-list-item.unread {
  color: #CDD6F4;
  background: #1E1E2E;
}
.activity-list-container .activity-list-item.unread:hover {
  background: #1a1a28;
}
.activity-list-container .activity-list-item.unread .action-message {
  font-weight: 600;
}
.activity-list-container .activity-list-item:hover {
  background: #2d2e3e;
}
.activity-list-container .activity-list-item .disclosure-triangle {
  padding-top: 5px;
  padding-bottom: 0;
}
.activity-list-container .activity-list-item .activity-icon-container {
  flex-shrink: 0;
}
.activity-list-container .activity-list-item .activity-icon {
  vertical-align: text-bottom;
}
.activity-list-container .activity-list-item .action-message,
.activity-list-container .activity-list-item .title {
  text-overflow: ellipsis;
  overflow: hidden;
}
.activity-list-container .activity-list-item .timestamp {
  color: #7F849C;
  text-overflow: ellipsis;
  overflow: hidden;
  flex-shrink: 0;
  padding-left: 5px;
}
.activity-list-container .activity-list-toggle-item {
  height: 30px;
  white-space: nowrap;
  background: #313244;
  cursor: default;
  overflow-y: hidden;
  transition-property: all;
  transition-duration: 0.5s;
  transition-timing-function: cubic-bezier(0, 1, 0.5, 1);
}
.activity-list-container .activity-list-toggle-item:last-child {
  border-bottom: 1px solid #181825;
}
.activity-list-container .activity-list-toggle-item .action-message {
  padding: 4px 10px;
  text-overflow: ellipsis;
  overflow: hidden;
}
.activity-list-container .activity-list-toggle-item .timestamp {
  padding: 4px 10px;
  color: #7F849C;
  text-overflow: ellipsis;
  overflow: hidden;
}
.activity-list-container .activity-toggle-container.hidden .activity-list-toggle-item {
  height: 0;
}
.activity-list-container .activity-toggle-container.hidden .activity-list-toggle-item:last-child {
  border-bottom: none;
}
body.platform-win32 .toolbar-activity,
body.platform-linux .toolbar-activity {
  margin-right: 12px;
}
