css:
.sheet-toolbar {
  position: relative;
  -webkit-app-region: drag;
  border-bottom: 1px solid #1E1E2E;
}
.column-MessageListSidebar {
  border-left: transparent;
}
.flexbox-handle-horizontal div {
  height: 100%;
  box-shadow: transparent;
}
#inbox-html-wrapper > :not(table) a,
#inbox-plain-wrapper > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a {
  color: #313244;
}
/* Badge with counter, secondary */
.item-count-box:not(.selected):not(.focused) {
  color: #1E1E2E;
  box-shadow: 0 0.5px 0 #181825, 0 -0.5px 0 #181825, 0.5px 0 0 #181825, -0.5px 0 0 #181825;
  background: #B4BEFE;
}
/* Badge with counter, primary, selected */
.outline-view .item .item-count-box.alt-count {
  color: #B4BEFE;
  background: #1E1E2E;
  box-shadow: none;
}
/* Badge with counter, primary, not selected */
.outline-view .item .item-count-box.alt-count:not(.selected):not(.focused) {
  color: #1E1E2E;
  background: #B4BEFE;
  box-shadow: none;
}
/* tracking icon, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, unopened, focused */
.list-item.focused .open-tracking-icon img.content-mask.unopened,
.list-item.selected .open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, opened, unfocused */
.open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, opened, focused */
.list-item.focused .open-tracking-icon img.content-mask.opened,
.list-item.selected .open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, message list, opened */
.open-tracking-message-status.opened img.content-mask {
  background-color: #CDD6F4;
}
/* tracking icon, mail list, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
.toolbar-activity .unread-count.active {
  background: #B4BEFE;
  color: #1E1E2E;
}
.open-tracking-message-status.opened {
  color: #CDD6F4;
}
.open-tracking-message-status.unopened {
  color: #7F849C;
}
.account-switcher img {
  zoom: 1 !important;
  max-width: 10px;
  max-height: 6px;
  transform: none;
  background-image: none;
  background-color: #7F849C;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="10" height="6" viewBox="0 0 10 6"><path fill="#FFF" d="M0 0h1l4 4 4-4h1v1L5 6 0 1z"/></svg>');
}
.toolbar-activity .activity-toolbar-icon {
  background: #A6ADC8;
}
.div.suggestions:hover {
  color: #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item,
.button-dropdown.btn-emphasis .secondary-picker,
.button-dropdown.btn-emphasis .only-item {
  position: relative;
  color: #1E1E2E;
  font-weight: 500;
  background: #B4BEFE;
  box-shadow: none;
  border: 0px solid #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item img.content-mask,
.button-dropdown.btn-emphasis .secondary-picker img.content-mask,
.button-dropdown.btn-emphasis .only-item img.content-mask {
  background-color: #1E1E2E;
}
.button-dropdown.btn-emphasis .primary-item:active,
.button-dropdown.btn-emphasis .secondary-picker:active,
.button-dropdown.btn-emphasis .only-item:active {
  background: #B4BEFE;
}
.tokenizing-field .token {
  background: linear-gradient(to bottom, #313244 0%, #45475A 100%);
}
.tokenizing-field .token.selected,
.tokenizing-field .token.dragging {
  color: #1E1E2E;
}
.tokenizing-field .token.selected .action img,
.tokenizing-field .token.dragging .action img {
  background-color: #1E1E2E;
}
.menu .item.selected,
.menu .item:active {
  background-color: #4e4947;
  color: #CDD6F4;
}
.div.button-dropdown.open.open-down {
  background-color: #CDD6F4;
  color: #CDD6F4;
}
.menu .item.selected,
.menu .item:active {
  background-color: #B4BEFE;
  color: #1E1E2E;
}
.menu .item.selected .primary,
.menu .item:active .primary {
  color: #1E1E2E;
}
.menu .item.selected .secondary,
.menu .item:active .secondary {
  color: #313244;
}
.button-dropdown .primary-item,
.button-dropdown .only-item {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown .secondary-picker {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown.open.open-down .secondary-items {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08), 0 0 3px #B4BEFE;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content.unfocused {
  background-color: #313244;
}
.unsubscribe-action {
  color: #B4BEFE;
  text-decoration: underline;
}
.undo-redo-toast .content {
  background: #B4BEFE;
}
.preferences-wrap .container-accounts .accounts-content .account-details {
  background-color: #1E1E2E;
}
.nylas-editable-list .items-wrapper {
  background-color: #313244;
}
.thread-injected-quick-actions .btn {
  box-shadow: none;
}
.thread-list .list-item .list-column-HoverActions .action.action-trash {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-archive {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url('../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .thread-icon.thread-icon-star,
.draft-list .thread-icon.thread-icon-star {
  filter: grayscale(100%);
}
#message-list .mail-label.removable {
  color: #1E1E2E !important;
  background: #B4BEFE !important;
  box-shadow: none !important;
}
.thread-list .thread-icon.thread-icon-unread,
.draft-list .thread-icon.thread-icon-unread {
  filter: grayscale(100%);
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.preferences-signatures-container {
  max-width: 800px;
  margin: 0 auto;
}
.preferences-signatures-container .empty-list {
  height: 300px;
  width: inherit;
  background-color: #1E1E2E;
  border: 1px solid #181825;
  text-align: center;
  padding: 60px;
}
.preferences-signatures-container .empty-list .icon-signature {
  height: 120px;
}
.preferences-signatures-container .empty-list h2 {
  color: #7F849C;
}
.preferences-signatures-container .empty-list .btn {
  margin-top: 10px;
}
.preferences-signatures-container .signature-list {
  position: relative;
  height: inherit;
  width: inherit;
}
.preferences-signatures-container .signature-list .items-wrapper {
  min-width: 200px;
  height: 262px;
}
.preferences-signatures-container .signature-list .item-rule-disabled {
  color: #d9534f;
  padding: 4px 10px;
  border-bottom: 1px solid #181825;
}
.preferences-signatures-container .signature-list .selected .item-rule-disabled {
  color: #1E1E2E;
}
.preferences-signatures-container .signature-list .editable-item {
  padding: 8px 10px;
  border-bottom: none;
}
.preferences-signatures-container .signature-list .btn-editable-list {
  height: 37px;
  width: 37px;
  line-height: 37px;
  font-size: 1em;
}
.preferences-signatures-container .signature-wrap {
  display: flex;
  position: relative;
  margin-left: 12px;
  flex-direction: column;
  overflow: hidden;
  min-height: 200px;
  width: 100%;
}
.preferences-signatures-container .signature-wrap.empty {
  opacity: 0.5;
  pointer-events: none;
}
.preferences-signatures-container .signature-wrap .section {
  background-color: #1E1E2E;
  border: 1px solid #181825;
}
.preferences-signatures-container .signature-wrap .section.basic-info {
  background: none;
  border: none;
  display: flex;
  white-space: nowrap;
  margin-bottom: 12px;
}
.preferences-signatures-container .signature-wrap .section.basic-info input {
  max-width: 300px;
}
.preferences-signatures-container .signature-wrap .section-header {
  margin-top: 10px;
}
.preferences-signatures-container .signature-wrap .section-header:first-child {
  margin-top: 0;
}
.preferences-signatures-container .signature-wrap textarea.raw-html {
  font-family: 'DejaVu Sans Mono', Menlo, Monaco, Consolas, 'Courier New', monospace;
  font-size: 0.9em;
  width: 100%;
  height: 240px;
  padding: 12px;
}
.preferences-signatures-container .signature-wrap .signature-template-picker {
  height: 58px;
  overflow-x: scroll;
  overflow-y: hidden;
  background: #313143;
  white-space: nowrap;
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option {
  width: 100px;
  height: 50px;
  overflow: hidden;
  display: inline-block;
  border: 1px solid #181825;
  background: #1E1E2E;
  position: relative;
  margin-top: 4px;
  margin-left: 4px;
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option:last-child {
  margin-right: 4px;
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option .centered {
  align-items: center;
  justify-content: center;
  white-space: initial;
  flex-shrink: 0;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  position: absolute;
  display: flex;
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option .centered .preview {
  pointer-events: none;
  transform: scale(0.25);
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option.active {
  border: 1px solid #B4BEFE;
}
.preferences-signatures-container .signature-wrap .section.preview {
  padding: 12px;
  min-height: 180px;
}
.preferences-signatures-container .signature-wrap .section.preview .label {
  font-size: 0.9em;
  color: #7F849C;
  float: right;
  border-radius: 5px;
  padding: 0 5px;
  color: lightgray;
}
.preferences-signatures-container .signature-wrap .section.information {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  padding: 12px 0;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker {
  display: block;
  width: 100%;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-well {
  width: 60px;
  height: 60px;
  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
  border: 1px solid rgba(24, 24, 37, 0.9);
  margin-right: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-well:hover,
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-well.dropping {
  border: 1px solid #B4BEFE;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-options {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: baseline;
  justify-content: space-between;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker select {
  margin: 0;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .drop-note {
  font-size: 12.04px;
  color: #7F849C;
  margin-top: 4px;
}
.preferences-signatures-container .signature-wrap .section.information .field {
  padding: 5px 12px;
  width: 50%;
}
.preferences-signatures-container .signature-wrap .section.information .field label {
  color: #7F849C;
  margin-right: 20px;
}
.preferences-signatures-container .signature-wrap .account-picker {
  display: inline-block;
  color: #A6ADC8;
  margin-left: 12px;
  margin-right: 2px;
}
.preferences-signatures-container .signature-wrap .account-picker .button-dropdown .secondary-items .menu .item {
  padding: 5px 10px 5px 25px;
}
.preferences-signatures-container .signature-wrap .account-picker .button-dropdown .secondary-items .menu .item.checked {
  background-image: url(images/menu/osx-checkmark@2x.svg);
  background-position: left;
  background-position-x: 6%;
  background-size: 10px;
  margin: 0;
  padding: 5px 10px 5px 25px;
  background-repeat: no-repeat;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component {
  display: inline-block;
  float: right;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item {
  background: #27242c;
  box-shadow: none;
  padding-right: 0;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown img.content-mask {
  display: none;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown img.signature-button {
  display: inline-block;
  background-color: #919191;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown.open img.signature-button {
  background-color: #B4BEFE;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items {
  right: 0;
  left: auto;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .header-container {
  display: inline-block;
  padding: 0;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .footer-container {
  display: inline-block;
  border-top: 1px solid #dddddd;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .item {
  padding: 5px 20px 5px 20px;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .item.checked {
  background-image: url(images/menu/osx-checkmark@2x.svg);
  background-position: left;
  background-position-x: 5%;
  background-size: 10px;
  background-repeat: no-repeat;
}
.message-item-white-wrap.composer-outer-wrap.focused .composer-participant-field .dropdown-component .only-item {
  background: #1E1E2E;
}
.composer-full-window .message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item {
  background: #1E1E2E;
}
