css:
.sheet-toolbar {
  position: relative;
  -webkit-app-region: drag;
  border-bottom: 1px solid #1E1E2E;
}
.column-MessageListSidebar {
  border-left: transparent;
}
.flexbox-handle-horizontal div {
  height: 100%;
  box-shadow: transparent;
}
#inbox-html-wrapper > :not(table) a,
#inbox-plain-wrapper > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a {
  color: #313244;
}
/* Badge with counter, secondary */
.item-count-box:not(.selected):not(.focused) {
  color: #1E1E2E;
  box-shadow: 0 0.5px 0 #181825, 0 -0.5px 0 #181825, 0.5px 0 0 #181825, -0.5px 0 0 #181825;
  background: #B4BEFE;
}
/* Badge with counter, primary, selected */
.outline-view .item .item-count-box.alt-count {
  color: #B4BEFE;
  background: #1E1E2E;
  box-shadow: none;
}
/* Badge with counter, primary, not selected */
.outline-view .item .item-count-box.alt-count:not(.selected):not(.focused) {
  color: #1E1E2E;
  background: #B4BEFE;
  box-shadow: none;
}
/* tracking icon, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, unopened, focused */
.list-item.focused .open-tracking-icon img.content-mask.unopened,
.list-item.selected .open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, opened, unfocused */
.open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, opened, focused */
.list-item.focused .open-tracking-icon img.content-mask.opened,
.list-item.selected .open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, message list, opened */
.open-tracking-message-status.opened img.content-mask {
  background-color: #CDD6F4;
}
/* tracking icon, mail list, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
.toolbar-activity .unread-count.active {
  background: #B4BEFE;
  color: #1E1E2E;
}
.open-tracking-message-status.opened {
  color: #CDD6F4;
}
.open-tracking-message-status.unopened {
  color: #7F849C;
}
.account-switcher img {
  zoom: 1 !important;
  max-width: 10px;
  max-height: 6px;
  transform: none;
  background-image: none;
  background-color: #7F849C;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="10" height="6" viewBox="0 0 10 6"><path fill="#FFF" d="M0 0h1l4 4 4-4h1v1L5 6 0 1z"/></svg>');
}
.toolbar-activity .activity-toolbar-icon {
  background: #A6ADC8;
}
.div.suggestions:hover {
  color: #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item,
.button-dropdown.btn-emphasis .secondary-picker,
.button-dropdown.btn-emphasis .only-item {
  position: relative;
  color: #1E1E2E;
  font-weight: 500;
  background: #B4BEFE;
  box-shadow: none;
  border: 0px solid #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item img.content-mask,
.button-dropdown.btn-emphasis .secondary-picker img.content-mask,
.button-dropdown.btn-emphasis .only-item img.content-mask {
  background-color: #1E1E2E;
}
.button-dropdown.btn-emphasis .primary-item:active,
.button-dropdown.btn-emphasis .secondary-picker:active,
.button-dropdown.btn-emphasis .only-item:active {
  background: #B4BEFE;
}
.tokenizing-field .token {
  background: linear-gradient(to bottom, #313244 0%, #45475A 100%);
}
.tokenizing-field .token.selected,
.tokenizing-field .token.dragging {
  color: #1E1E2E;
}
.tokenizing-field .token.selected .action img,
.tokenizing-field .token.dragging .action img {
  background-color: #1E1E2E;
}
.menu .item.selected,
.menu .item:active {
  background-color: #4e4947;
  color: #CDD6F4;
}
.div.button-dropdown.open.open-down {
  background-color: #CDD6F4;
  color: #CDD6F4;
}
.menu .item.selected,
.menu .item:active {
  background-color: #B4BEFE;
  color: #1E1E2E;
}
.menu .item.selected .primary,
.menu .item:active .primary {
  color: #1E1E2E;
}
.menu .item.selected .secondary,
.menu .item:active .secondary {
  color: #313244;
}
.button-dropdown .primary-item,
.button-dropdown .only-item {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown .secondary-picker {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown.open.open-down .secondary-items {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08), 0 0 3px #B4BEFE;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content.unfocused {
  background-color: #313244;
}
.unsubscribe-action {
  color: #B4BEFE;
  text-decoration: underline;
}
.undo-redo-toast .content {
  background: #B4BEFE;
}
.preferences-wrap .container-accounts .accounts-content .account-details {
  background-color: #1E1E2E;
}
.nylas-editable-list .items-wrapper {
  background-color: #313244;
}
.thread-injected-quick-actions .btn {
  box-shadow: none;
}
.thread-list .list-item .list-column-HoverActions .action.action-trash {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-archive {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url('../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .thread-icon.thread-icon-star,
.draft-list .thread-icon.thread-icon-star {
  filter: grayscale(100%);
}
#message-list .mail-label.removable {
  color: #1E1E2E !important;
  background: #B4BEFE !important;
  box-shadow: none !important;
}
.thread-list .thread-icon.thread-icon-unread,
.draft-list .thread-icon.thread-icon-unread {
  filter: grayscale(100%);
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(24, 24, 37, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(24, 24, 37, 0.8);
}
button,
html input[type='button'] {
  cursor: default;
}
button:hover,
html input[type='button']:hover,
button:active,
html input[type='button']:active {
  cursor: default;
}
body.platform-win32 .btn {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
  border: 0;
  box-shadow: 0 0 0 2px rgba(24, 24, 37, 0.8);
}
body.platform-win32 .btn:hover {
  background: rgba(24, 24, 37, 0.8);
}
body.platform-win32 .btn.btn-action {
  border: 2px solid #498e38;
}
body.platform-win32 .btn.btn-action:hover {
  background: #5cb346;
}
body.platform-win32 .btn.btn-emphasis {
  background: #5b90fb;
  border: 0;
}
body.platform-win32 .btn.btn-emphasis:hover {
  border-radius: 0;
  background: #296efa;
}
body.platform-win32 .btn.btn-emphasis:active {
  background: #5b90fb;
  box-shadow: 0 0 0;
}
body.platform-win32 .btn.btn-danger,
body.platform-win32 .btn .btn-destructive {
  background: #d9534f;
  border: 0;
}
body.platform-win32 .btn.btn-danger:hover,
body.platform-win32 .btn .btn-destructive:hover {
  border-radius: 0;
  background: #c9302c;
}
.btn {
  padding: 0 0.8em;
  border-radius: 3px;
  border: 0;
  cursor: default;
  display: inline-block;
  color: #CDD6F4;
  background: #1E1E2E;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 0.5px 1px rgba(0, 0, 0, 0.15);
  height: 1.9em;
  line-height: 1.9em;
  font-size: 13.02px;
}
.btn img.content-mask {
  background-color: #CDD6F4;
}
.btn .text {
  margin-left: 6px;
}
.btn:active {
  cursor: default;
  background: #1e1e29;
}
.btn:focus {
  outline: none;
}
.btn.btn-small {
  font-size: 12.04px;
}
.btn.btn-large {
  font-size: 14px;
  padding: 0 1.3em;
  line-height: 2.2em;
  height: 2.3em;
}
.btn.btn-larger {
  font-size: 15.96px;
  padding: 0 1.6em;
}
.btn.btn-action {
  color: #CDD6F4;
  background: #5cb346;
}
.btn.btn-action img.content-mask {
  background-color: #CDD6F4;
}
.btn.btn-disabled {
  color: rgba(205, 214, 244, 0.5);
  background: rgba(49, 50, 68, 0.75);
}
.btn.btn-disabled:active {
  background: rgba(49, 50, 68, 0.75);
}
.btn.btn-emphasis {
  position: relative;
  color: #CDD6F4;
  font-weight: 500;
  background: linear-gradient(to bottom, #6bb1f9 0%, #0a80ff 100%);
  box-shadow: none;
  border: 1px solid #3878fa;
}
.btn.btn-emphasis img.content-mask {
  background-color: #CDD6F4;
}
.btn.btn-emphasis.btn-disabled {
  opacity: 0.4;
}
.btn.btn-emphasis:before {
  content: ' ';
  width: calc(100% + 2px);
  height: calc(100% + 2px);
  border-radius: 4px;
  top: -1px;
  left: -1px;
  position: absolute;
  z-index: -1;
  background: linear-gradient(to bottom, #4ca2f9 0%, #015cff 100%);
}
.btn.btn-emphasis:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#296efa), to(#4783fb));
}
.btn.btn-danger,
.btn .btn-destructive {
  color: #CDD6F4;
  background: #d9534f;
}
.btn.btn-danger img.content-mask,
.btn .btn-destructive img.content-mask {
  background-color: #CDD6F4;
}
.btn-toolbar {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#272836));
  padding: 0 13px;
}
.btn-toolbar.narrow {
  padding: 0 9px;
}
.btn-toolbar.btn-disabled {
  opacity: 0.6;
}
.btn-gradient {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#272836));
}
.btn-gradient:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#1e1e29), to(#14151c));
}
.btn-icon {
  background: transparent;
  border: 0;
  box-shadow: none;
  color: #A6ADC8;
  margin-right: 10px;
  outline: none !important;
  font-size: 20px;
}
.btn-icon img.content-mask {
  background-color: #A6ADC8;
}
.btn-icon:last-child {
  margin-right: 0;
}
.btn-icon.inverse {
  color: #CDD6F4;
}
.btn-icon.inverse img.content-mask {
  background-color: #CDD6F4;
}
.btn-icon.inverse:hover {
  color: white;
}
.btn-icon.inverse:active {
  color: #CDD6F4;
}
.btn-icon.inverse:active img.content-mask {
  background-color: #CDD6F4;
}
.btn-icon:hover {
  cursor: default;
  color: #B4BEFE;
  box-shadow: none;
}
.btn-icon:hover img.content-mask {
  background-color: #B4BEFE;
}
.btn-icon:active {
  color: #B4BEFE;
  box-shadow: none;
}
.btn-icon:active img.content-mask {
  background-color: #B4BEFE;
}
.composer-editor-plaintext textarea {
  padding: 13px 22px;
  box-sizing: border-box;
  resize: none;
  min-height: 70px;
  color: #CDD6F4;
  outline: none;
  font-family: 'DejaVu Sans Mono', Menlo, Monaco, Consolas, 'Courier New', monospace;
}
.composer-editor-plaintext textarea:focus {
  border: none;
  box-shadow: none;
  outline: none;
}
.RichEditor-root {
  padding: 0 22px 0 22px;
  min-height: 70px;
}
.RichEditor-root .RichEditor-content {
  padding-top: 12px;
  position: relative;
  color: #CDD6F4;
}
.RichEditor-root blockquote {
  border-left: 2px solid #eee;
  color: #a76bba;
  margin: 0;
  padding: 0 0 0 10px;
  -webkit-margin-after: initial !important;
  -webkit-margin-before: initial !important;
  -webkit-margin-start: initial !important;
  -webkit-margin-end: initial !important;
}
.RichEditor-root .link {
  color: #B4BEFE;
  text-decoration: underline;
}
.RichEditor-root .custom-block,
.RichEditor-root .uneditable.custom-block {
  border: 1px dashed transparent;
}
.RichEditor-root .custom-block.focused,
.RichEditor-root .uneditable.custom-block.focused {
  border: 1px dashed #CDD6F4;
}
.RichEditor-root .uneditable {
  -webkit-user-modify: read-only;
  display: inline-block;
  border: 1px solid transparent;
  position: relative;
  white-space: normal;
  min-width: 30px;
}
.RichEditor-root .uneditable .uneditable-remove {
  position: absolute;
  display: none;
  right: -5px;
  top: -5px;
  z-index: 2;
}
.RichEditor-root .uneditable .uneditable-remove:active {
  filter: brightness(85%);
}
.RichEditor-root .uneditable blockquote {
  color: #a76bba;
}
.RichEditor-root .uneditable:hover {
  border: 1px dashed #181825;
}
.RichEditor-root .uneditable:hover .uneditable-remove {
  display: initial;
}
.RichEditor-toolbar {
  font-family: 'Helvetica', sans-serif;
  font-size: 14px;
  user-select: none;
  position: relative;
}
.RichEditor-toolbar .floating-container {
  border-bottom: 1px solid #181825;
  z-index: 2;
  width: 100%;
  min-height: 29px;
}
.RichEditor-toolbar .inner {
  background: #1E1E2E;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  width: 100%;
}
.RichEditor-toolbar .divider {
  width: 1px;
  height: 22px;
  border-right: 1px solid #181825;
  margin-right: 1px;
  padding-right: 1px;
}
.RichEditor-toolbar button {
  cursor: pointer;
  color: rgba(202, 211, 245, 0.7);
  padding: 4px 4.7px;
  border: none;
  background: none;
  width: initial;
  flex: 1;
  flex-shrink: 0;
  max-width: 32px;
}
.RichEditor-toolbar button:hover {
  color: rgba(202, 211, 245, 0.95);
  background: none;
}
.RichEditor-toolbar button.with-select {
  max-width: 100px;
  white-space: nowrap;
}
.RichEditor-toolbar button.with-select select {
  border: none;
  background: none;
  margin: 0;
  min-width: 72px;
  max-width: 92px;
}
.RichEditor-toolbar button.with-select select:active,
.RichEditor-toolbar button.with-select select:focus {
  background-color: #1E1E2E;
}
.RichEditor-toolbar button.active {
  color: #B4BEFE;
}
.RichEditor-toolbar .dropdown {
  z-index: 4;
  position: absolute;
  background: #1E1E2E;
  border-radius: 3px 3px 0 3px;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 -3px 12px rgba(0, 0, 0, 0.15);
}
.RichEditor-toolbar .color-picker {
  display: inline-block;
  position: relative;
}
.RichEditor-toolbar .color-picker > button {
  cursor: pointer;
  width: 20px;
  height: 14px;
  margin-right: 4px;
  margin-left: 4px;
  margin-bottom: 2px;
  vertical-align: middle;
  box-shadow: #CDD6F4 0 0.5px, #CDD6F4 0 -0.5px, #CDD6F4 0.5px 0, #CDD6F4 -0.5px 0;
}
.RichEditor-toolbar .link-picker {
  flex: 1;
  flex-shrink: 0;
  max-width: 32px;
  text-align: center;
}
.RichEditor-toolbar .link-picker .dropdown {
  padding: 5px;
  display: flex;
  flex-direction: row;
}
.RichEditor-toolbar .link-picker .dropdown button {
  max-width: 50px;
}
body.platform-win32 .composer-inner-wrap .composer-drop-cover {
  border-radius: 0;
}
body.platform-win32 .composer-inner-wrap .composer-action-bar-wrap {
  border-radius: 0;
}
body.platform-win32 .composer-inner-wrap input,
body.platform-win32 .composer-inner-wrap input:focus {
  box-shadow: none;
}
.composer-inner-wrap {
  position: relative;
  height: 100%;
  display: flex;
  flex-direction: column;
}
.composer-inner-wrap .composer-drop-cover {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1000;
  background: rgba(255, 255, 255, 0.7);
  border-radius: 3px;
  border: 4px dashed #a5adb4;
  text-align: center;
  line-height: 2.3em;
  pointer-events: none;
}
.composer-inner-wrap .composer-drop-cover .centered {
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  color: #8a939d;
  font-weight: 500;
  font-size: 1.1em;
}
.composer-inner-wrap .composer-drop-cover .centered img {
  margin: auto;
  display: block;
  margin-bottom: 20px;
  background-color: #8a939d;
}
.composer-inner-wrap .nylas-attachment-item.image-attachment-item {
  min-width: 0;
}
.composer-inner-wrap .composer-action-bar-wrap {
  position: relative;
  width: 100%;
  background: #1E1E2E;
  border-top: 1px solid #181825;
  box-shadow: inset 0 2px 1px rgba(0, 0, 0, 0.03);
  border-bottom: 0;
  border-radius: 3px;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-cover {
  background-image: linear-gradient(to right, rgba(30, 30, 46, 0) 0%, #1E1E2E 10%);
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:not(.btn-emphasis) {
  background: transparent;
  box-shadow: 0 0 0;
  margin: 0;
  padding: 0 8px;
  white-space: nowrap;
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:not(.btn-emphasis) img.content-mask {
  background-color: rgba(202, 211, 245, 0.7);
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:not(.btn-emphasis):hover img.content-mask {
  background-color: rgba(202, 211, 245, 0.95);
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:not(.btn-emphasis).btn-enabled {
  color: #B4BEFE;
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:not(.btn-emphasis).btn-enabled img.content-mask {
  background-color: #B4BEFE;
}
.composer-inner-wrap .composer-action-bar-wrap .btn-send {
  margin-right: 10px;
  white-space: nowrap;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content {
  display: flex;
  margin: 0 auto;
  flex-direction: row;
  padding: 9px 22.5px;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content.restrict-width {
  max-width: 800px;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content .composer-action-bar-plugins {
  flex-wrap: wrap;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-animation-wrap {
  position: relative;
  overflow: hidden;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-animation-wrap .composer-action-bar-plugins {
  opacity: 0;
  transition: opacity 30ms;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-animation-wrap .action-bar-cover {
  transition: left 200ms ease-out;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 2;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-animation-wrap.plugins-loaded .composer-action-bar-plugins {
  opacity: 1;
}
.composer-inner-wrap .composer-action-bar-wrap .action-bar-animation-wrap.plugins-loaded .action-bar-cover {
  left: 100%;
}
.composer-inner-wrap .composer-content-wrap {
  padding: 0;
  flex: 1;
  display: flex;
  position: relative;
  flex-flow: column;
}
.composer-inner-wrap .composer-centered {
  display: flex;
  position: relative;
  flex-direction: column;
  flex: 1;
  width: 100%;
  margin: 0 auto;
  padding-top: 9.8px;
}
.composer-inner-wrap .composer-centered.restrict-width {
  max-width: 800px;
}
.composer-inner-wrap .composer-body-wrap {
  padding: 0 0 5px 0;
}
.composer-inner-wrap .composer-header .key-commands-region {
  height: initial;
}
.composer-inner-wrap .composer-header-actions {
  position: relative;
  float: right;
  z-index: 2;
  cursor: default;
  padding-right: 21px;
  padding-top: 12px;
}
.composer-inner-wrap .composer-header-actions .action {
  color: #7F849C;
  font-size: 13.02px;
  padding: 10px 6px;
}
.composer-inner-wrap .composer-header-actions .action img.content-mask {
  background-color: #7F849C;
}
.composer-inner-wrap .composer-header-actions .action:first-child {
  padding-left: 14px;
}
.composer-inner-wrap .composer-header-actions .action:last-child {
  padding-right: 0;
}
.composer-inner-wrap .composer-header-actions .action:hover {
  color: #B4BEFE;
  cursor: default;
}
.composer-inner-wrap .composer-header-actions .action:hover img.content-mask {
  background-color: #B4BEFE;
}
.composer-inner-wrap input,
.composer-inner-wrap textarea {
  color: #CDD6F4;
  position: relative;
  display: block;
  background: inherit;
  width: 100%;
  resize: none;
  border: none;
}
.composer-inner-wrap .composer-field-label {
  color: #7F849C;
  float: left;
  padding-top: 13px;
  display: block;
}
.composer-inner-wrap .composer-field-label:hover {
  cursor: default;
}
.composer-inner-wrap .composer-subject {
  position: relative;
  margin: 0;
  flex-shrink: 0;
}
.composer-inner-wrap .composer-subject:after {
  width: calc(100% - 44px);
  height: 1px;
  position: absolute;
  bottom: 0;
  left: 23px;
  content: ' ';
  background: #181825;
}
.composer-inner-wrap .composer-subject div[contenteditable] {
  padding-left: 22px;
  padding-right: 22px;
}
.composer-inner-wrap .composer-subject input {
  display: inline-block;
  padding: 13px 22px 9px 22px;
  min-width: 5em;
  background-color: transparent;
  border: none;
  margin: 0;
}
.composer-inner-wrap .composer-subject input::-webkit-input-placeholder {
  color: #7F849C;
}
.composer-inner-wrap .composer-subject input:focus {
  box-shadow: none;
}
.composer-inner-wrap .compose-body-scroll {
  position: initial;
}
.composer-inner-wrap .compose-body-scroll .scroll-region-content .scroll-region-content-inner {
  min-height: 100%;
  display: flex;
}
.composer-inner-wrap .compose-body {
  flex: 1;
  cursor: text;
  position: relative;
  margin: 0;
}
.composer-inner-wrap .compose-body .RichEditor-toolbar .hide-in-composer {
  display: none;
}
.composer-inner-wrap .compose-body .hiding-quoted-text blockquote {
  height: 0;
  overflow: hidden;
  padding: 0;
}
.composer-inner-wrap .compose-body .hiding-quoted-text .gmail_quote_attribution {
  height: 0;
  overflow: hidden;
  padding: 0;
}
.composer-inner-wrap .compose-body .quoted-text-control {
  position: relative;
  margin: 0 14px 14px 22px;
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text {
  display: none;
  cursor: pointer;
  position: absolute;
  z-index: 2;
  right: -6px;
  top: -6px;
  border-radius: 0 0 0 3px;
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text:active {
  background: none;
  filter: brightness(95%);
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text img {
  height: 24px;
}
.composer-inner-wrap .compose-body .quoted-text-control:hover .remove-quoted-text {
  display: block;
}
.composer-inner-wrap .composer-footer-region {
  padding: 0 22px;
  cursor: default;
}
.composer-inner-wrap .composer-footer-region:hover {
  cursor: default;
}
.composer-inner-wrap .attachments-area {
  padding: 0;
  margin: 0 8px;
}
.composer-full-window {
  width: 100%;
  height: 100%;
}
.composer-full-window .composer-outer-wrap {
  width: 100%;
  height: 100%;
}
.composer-full-window .composer-inner-wrap .composer-action-bar-wrap {
  background: #1c1c2b;
  border-top: 1px solid #181825;
  box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.03);
}
.composer-full-window .composer-inner-wrap .composer-action-bar-wrap .action-bar-cover {
  background-image: linear-gradient(to right, rgba(28, 28, 43, 0) 0%, #1c1c2b 10%);
}
.composer-full-window .composer-inner-wrap .composer-action-bar-content {
  padding: 9px 13.5px 9px 22.5px;
}
.composer-full-window .composer-inner-wrap .compose-body {
  margin-bottom: 0;
  position: relative;
}
.composer-full-window .RichEditor-root {
  min-height: 21px;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #27242c;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .btn.btn-toolbar.btn-trash {
  padding-right: 0;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .show-more-fade {
  background: linear-gradient(to right, rgba(39, 36, 44, 0) 0%, #27242c 40%);
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .composer-action-bar-wrap {
  background: #27242c;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .composer-action-bar-wrap .action-bar-cover {
  background-image: linear-gradient(to right, rgba(39, 36, 44, 0) 0%, #27242c 10%);
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .RichEditor-toolbar .inner {
  background: #27242c;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08), 0 0 3px #B4BEFE;
  background-color: #1E1E2E;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .show-more-fade {
  background: linear-gradient(to right, rgba(30, 30, 46, 0) 0%, #1e1e2e 40%);
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .composer-action-bar-wrap {
  background: #1E1E2E;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .composer-action-bar-wrap .action-bar-cover {
  background-image: linear-gradient(to right, rgba(30, 30, 46, 0) 0%, #1E1E2E 10%);
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .RichEditor-toolbar .inner {
  background: #1E1E2E;
}
.composer-participant-field {
  position: relative;
  margin: 0;
  flex-shrink: 0;
  min-height: 46px;
  color: #CDD6F4;
}
.composer-participant-field .tokenizing-field-wrap {
  padding: 0 22px;
}
.composer-participant-field .content-container {
  margin-left: 22px;
  width: calc(100% - 44px);
}
.composer-participant-field .button-dropdown .content-container {
  margin-left: 0;
  width: 100%;
}
.composer-participant-field:after {
  width: calc(100% - 44px);
  height: 1px;
  position: absolute;
  bottom: 0;
  left: 23px;
  content: ' ';
  background: #181825;
}
.composer-participant-field.from-field {
  padding: 0 22px;
}
.composer-participant-field .from-single-name:hover {
  cursor: default;
}
.composer-participant-field .button-dropdown {
  margin-left: 10px;
  padding-top: 11px;
  vertical-align: -webkit-baseline-middle;
}
.composer-participant-field .button-dropdown .primary-item,
.composer-participant-field .button-dropdown .only-item {
  line-height: 2em;
}
.composer-participant-field .button-dropdown:hover .primary-item,
.composer-participant-field .button-dropdown:hover .only-item {
  border-radius: 3px;
}
.composer-participant-field .button-dropdown .secondary-items {
  border-radius: 3px;
}
.composer-participant-field .button-dropdown .item .contact.is-alias {
  font-style: italic;
  padding-left: 10px;
  border-left: 2px solid #181825;
  margin-left: 2px;
}
.composer-participant-field .participant {
  display: flex;
  align-items: center;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
}
.composer-participant-field .participant .participant-primary {
  font-weight: 600;
}
.composer-participant-field .participant .participant-secondary {
  color: #7F849C;
}
.composer-participant-field .tokenizing-field-input {
  margin-left: 2.8em;
}
.emoji-picker {
  z-index: 3;
  padding: 4px;
  background: #1E1E2E;
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.15);
  min-width: 140px;
}
.emoji-picker .btn.btn-icon {
  display: block;
  font-size: 14px !important;
  padding: 0 0.5em;
  text-align: left;
  word-break: break-all;
  overflow: hidden;
}
.emoji-picker .btn.btn-icon:first-child {
  padding-left: 0.5em !important;
}
.emoji-picker .btn.btn-icon.emoji-option,
.emoji-picker .btn.btn-icon:hover {
  background-color: #5b90fb;
  color: #ffffff;
  border-radius: 5px;
}
.emoji-button-popover {
  width: 210px;
  height: 290px;
  overflow: hidden;
}
.emoji-button-popover .emoji-tabs {
  display: flex;
  flex-direction: row;
  padding: 5px 5px 5px 10px;
  border-bottom: 1px solid #181825;
  transition: box-shadow 0.5s;
}
.emoji-button-popover .emoji-tabs.shadow {
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.15);
}
.emoji-button-popover .emoji-tabs .emoji-tab {
  background-color: #77828d;
}
.emoji-button-popover .emoji-tabs .emoji-tab.active {
  background-color: #B4BEFE;
}
.emoji-button-popover .emoji-finder-container {
  height: 232px;
}
.emoji-button-popover .emoji-finder-container .scrollbar-track {
  background: transparent;
  border-left: none;
  width: 10px;
}
.emoji-button-popover .emoji-finder-container .emoji-search-container {
  padding: 7.5px 12px 0;
}
.emoji-button-popover .emoji-name {
  height: 25px;
  width: 192px;
  margin-top: 2px;
  margin-left: 10px;
  overflow: hidden;
  text-overflow: ellipsis;
  color: #7F849C;
}
