css:
.sheet-toolbar {
  position: relative;
  -webkit-app-region: drag;
  border-bottom: 1px solid #1E1E2E;
}
.column-MessageListSidebar {
  border-left: transparent;
}
.flexbox-handle-horizontal div {
  height: 100%;
  box-shadow: transparent;
}
#inbox-html-wrapper > :not(table) a,
#inbox-plain-wrapper > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a {
  color: #313244;
}
/* Badge with counter, secondary */
.item-count-box:not(.selected):not(.focused) {
  color: #1E1E2E;
  box-shadow: 0 0.5px 0 #181825, 0 -0.5px 0 #181825, 0.5px 0 0 #181825, -0.5px 0 0 #181825;
  background: #B4BEFE;
}
/* Badge with counter, primary, selected */
.outline-view .item .item-count-box.alt-count {
  color: #B4BEFE;
  background: #1E1E2E;
  box-shadow: none;
}
/* Badge with counter, primary, not selected */
.outline-view .item .item-count-box.alt-count:not(.selected):not(.focused) {
  color: #1E1E2E;
  background: #B4BEFE;
  box-shadow: none;
}
/* tracking icon, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, unopened, focused */
.list-item.focused .open-tracking-icon img.content-mask.unopened,
.list-item.selected .open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, opened, unfocused */
.open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, opened, focused */
.list-item.focused .open-tracking-icon img.content-mask.opened,
.list-item.selected .open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, message list, opened */
.open-tracking-message-status.opened img.content-mask {
  background-color: #CDD6F4;
}
/* tracking icon, mail list, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
.toolbar-activity .unread-count.active {
  background: #B4BEFE;
  color: #1E1E2E;
}
.open-tracking-message-status.opened {
  color: #CDD6F4;
}
.open-tracking-message-status.unopened {
  color: #7F849C;
}
.account-switcher img {
  zoom: 1 !important;
  max-width: 10px;
  max-height: 6px;
  transform: none;
  background-image: none;
  background-color: #7F849C;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="10" height="6" viewBox="0 0 10 6"><path fill="#FFF" d="M0 0h1l4 4 4-4h1v1L5 6 0 1z"/></svg>');
}
.toolbar-activity .activity-toolbar-icon {
  background: #A6ADC8;
}
.div.suggestions:hover {
  color: #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item,
.button-dropdown.btn-emphasis .secondary-picker,
.button-dropdown.btn-emphasis .only-item {
  position: relative;
  color: #1E1E2E;
  font-weight: 500;
  background: #B4BEFE;
  box-shadow: none;
  border: 0px solid #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item img.content-mask,
.button-dropdown.btn-emphasis .secondary-picker img.content-mask,
.button-dropdown.btn-emphasis .only-item img.content-mask {
  background-color: #1E1E2E;
}
.button-dropdown.btn-emphasis .primary-item:active,
.button-dropdown.btn-emphasis .secondary-picker:active,
.button-dropdown.btn-emphasis .only-item:active {
  background: #B4BEFE;
}
.tokenizing-field .token {
  background: linear-gradient(to bottom, #313244 0%, #45475A 100%);
}
.tokenizing-field .token.selected,
.tokenizing-field .token.dragging {
  color: #1E1E2E;
}
.tokenizing-field .token.selected .action img,
.tokenizing-field .token.dragging .action img {
  background-color: #1E1E2E;
}
.menu .item.selected,
.menu .item:active {
  background-color: #4e4947;
  color: #CDD6F4;
}
.div.button-dropdown.open.open-down {
  background-color: #CDD6F4;
  color: #CDD6F4;
}
.menu .item.selected,
.menu .item:active {
  background-color: #B4BEFE;
  color: #1E1E2E;
}
.menu .item.selected .primary,
.menu .item:active .primary {
  color: #1E1E2E;
}
.menu .item.selected .secondary,
.menu .item:active .secondary {
  color: #313244;
}
.button-dropdown .primary-item,
.button-dropdown .only-item {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown .secondary-picker {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown.open.open-down .secondary-items {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08), 0 0 3px #B4BEFE;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content.unfocused {
  background-color: #313244;
}
.unsubscribe-action {
  color: #B4BEFE;
  text-decoration: underline;
}
.undo-redo-toast .content {
  background: #B4BEFE;
}
.preferences-wrap .container-accounts .accounts-content .account-details {
  background-color: #1E1E2E;
}
.nylas-editable-list .items-wrapper {
  background-color: #313244;
}
.thread-injected-quick-actions .btn {
  box-shadow: none;
}
.thread-list .list-item .list-column-HoverActions .action.action-trash {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-archive {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url('../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .thread-icon.thread-icon-star,
.draft-list .thread-icon.thread-icon-star {
  filter: grayscale(100%);
}
#message-list .mail-label.removable {
  color: #1E1E2E !important;
  background: #B4BEFE !important;
  box-shadow: none !important;
}
.thread-list .thread-icon.thread-icon-unread,
.draft-list .thread-icon.thread-icon-unread {
  filter: grayscale(100%);
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(24, 24, 37, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(24, 24, 37, 0.8);
}
.sidebar-activity-floating-container {
  order: 2;
  z-index: 2;
}
.sidebar-activity {
  display: block;
  width: 100%;
  bottom: 0;
  background: #1E1E2E;
  font-size: 13.02px;
  color: #A6ADC8;
  line-height: 19.95px;
  box-shadow: inset 0 1px 0 #181825;
  transition: height 0.4s;
  transition-delay: 2s;
}
.sidebar-activity:hover {
  cursor: default;
}
.sidebar-activity .item:hover {
  cursor: default;
}
.sidebar-activity .item .clickable {
  cursor: pointer;
}
.sidebar-activity .item .inner {
  padding: 9px 12px 9px 12px;
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}
.sidebar-activity .item .inner .ellipsis1 {
  animation: show-ellipsis 3s 0s infinite;
}
.sidebar-activity .item .inner .ellipsis2 {
  animation: show-ellipsis 3s 250ms infinite;
}
.sidebar-activity .item .inner .ellipsis3 {
  animation: show-ellipsis 3s 500ms infinite;
}
.sidebar-activity .item .count {
  color: #7F849C;
  float: right;
}
.sidebar-activity .item .btn {
  display: block;
  text-align: center;
  margin-top: 4px;
  margin-bottom: 4px;
  font-size: 13.02px;
}
.sidebar-activity .item .sync-activity {
  max-height: 70vh;
  overflow-y: scroll;
}
.sidebar-activity .item .sync-activity .account {
  padding: 5px 12px 13.5px 12px;
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}
.sidebar-activity .item .sync-activity .account .folder-progress::before {
  height: 9px;
  width: 9px;
  vertical-align: baseline;
  background-color: #00dd00;
  content: '';
  display: inline-block;
  border-radius: 5px;
  margin-right: 5px;
  box-sizing: border-box;
}
.sidebar-activity .item .sync-activity .account .folder-progress.busy::before {
  background-color: transparent;
  border: solid 2px #00dd00;
  animation: border-pulse 3s infinite;
}
.sidebar-activity .item .sync-activity .account .folder-progress {
  font-size: 12.04px;
  margin: 3px;
  max-width: 100%;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}
.sidebar-activity .item .sync-activity .account .folder-progress .progress-label {
  color: #7F849C;
  margin-left: 2px;
  font-size: 12.04px;
}
.sidebar-activity .item h2 {
  font-size: 14px;
  overflow: hidden;
  text-overflow: ellipsis;
  font-weight: 500;
  margin: 6px 0 6px;
}
.sidebar-activity .item h3 {
  font-size: 14px;
  margin: 10px 0 4px 0;
}
.sidebar-activity .item .amount {
  margin-top: 2px;
  font-size: 12px;
  color: #A6ADC8;
}
.notifications-sticky {
  width: 100%;
}
.notifications-sticky .notification-info {
  background-color: #009ec4;
}
.notifications-sticky .notification-developer {
  background-color: #615396;
}
.notifications-sticky .notification-upgrade {
  background-image: linear-gradient(to top, #429e91, #40b1ac);
}
.notifications-sticky .notification-upgrade img {
  background-color: #CDD6F4;
}
.notifications-sticky .notification-error {
  background: linear-gradient(to top, #b9223b 0%, #ca2541 100%);
  border-color: #ca2541;
  color: #d9534f;
}
.notifications-sticky .notification-offline {
  background: linear-gradient(to top, #b88a00 0%, #cc9900 100%);
  border-color: #b38600;
}
.notifications-sticky .notifications-sticky-item {
  display: flex;
  font-size: 14px;
  color: #CDD6F4;
  border-bottom: 1px solid rgba(0, 0, 0, 0.25);
  padding-left: 12px;
  line-height: 2.25;
  align-items: baseline;
}
.notifications-sticky .notifications-sticky-item a {
  flex-shrink: 0;
  color: #CDD6F4;
  padding: 0 12px;
}
.notifications-sticky .notifications-sticky-item a:hover {
  background-color: rgba(255, 255, 255, 0.15);
  text-decoration: none;
  color: #CDD6F4;
}
.notifications-sticky .notifications-sticky-item a.default {
  background-color: rgba(0, 0, 0, 0.15);
}
.notifications-sticky .notifications-sticky-item a.default:hover {
  background-color: rgba(255, 255, 255, 0.15);
}
.notifications-sticky .notifications-sticky-item i {
  margin-right: 12px;
}
.notifications-sticky .notifications-sticky-item .icon {
  display: inline-block;
  align-self: center;
  line-height: 16px;
  margin-right: 12px;
}
.notifications-sticky .notifications-sticky-item .icon img {
  vertical-align: initial;
}
.notifications-sticky .notifications-sticky-item div.message {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
  line-height: 1.65;
  padding: 4px 0;
}
.notifications-sticky .notifications-sticky-item.has-default-action:hover {
  filter: brightness(110%);
  cursor: default;
}
.blink {
  animation: blink 1s ease;
}
@keyframes blink {
  0%,
  100% {
    box-shadow: none;
  }
  50% {
    box-shadow: 5px 5px 1px #258fe1 inset, -5px -5px 1px #258fe1 inset;
  }
}
@keyframes border-pulse {
  0%,
  100% {
    border-color: #00dd00;
  }
  50% {
    border-color: transparent;
  }
}
@keyframes show-ellipsis {
  0%,
  100% {
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
}
body.platform-win32 .notifications-sticky .notifications-sticky-item a {
  border-radius: 0;
}
