css:
.sheet-toolbar {
  position: relative;
  -webkit-app-region: drag;
  border-bottom: 1px solid #1E1E2E;
}
.column-MessageListSidebar {
  border-left: transparent;
}
.flexbox-handle-horizontal div {
  height: 100%;
  box-shadow: transparent;
}
#inbox-html-wrapper > :not(table) a,
#inbox-plain-wrapper > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) a,
#inbox-html-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a,
#inbox-plain-wrapper > :not(table) > :not(table) > :not(table) > :not(table) a {
  color: #313244;
}
/* Badge with counter, secondary */
.item-count-box:not(.selected):not(.focused) {
  color: #1E1E2E;
  box-shadow: 0 0.5px 0 #181825, 0 -0.5px 0 #181825, 0.5px 0 0 #181825, -0.5px 0 0 #181825;
  background: #B4BEFE;
}
/* Badge with counter, primary, selected */
.outline-view .item .item-count-box.alt-count {
  color: #B4BEFE;
  background: #1E1E2E;
  box-shadow: none;
}
/* Badge with counter, primary, not selected */
.outline-view .item .item-count-box.alt-count:not(.selected):not(.focused) {
  color: #1E1E2E;
  background: #B4BEFE;
  box-shadow: none;
}
/* tracking icon, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, unopened, focused */
.list-item.focused .open-tracking-icon img.content-mask.unopened,
.list-item.selected .open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
/* tracking icon, mail list, opened, unfocused */
.open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, opened, focused */
.list-item.focused .open-tracking-icon img.content-mask.opened,
.list-item.selected .open-tracking-icon img.content-mask.opened {
  background-color: #CDD6F4;
}
/* tracking icon, message list, opened */
.open-tracking-message-status.opened img.content-mask {
  background-color: #CDD6F4;
}
/* tracking icon, mail list, unopened, unfocused */
.open-tracking-icon img.content-mask.unopened {
  background-color: #45475A;
}
.toolbar-activity .unread-count.active {
  background: #B4BEFE;
  color: #1E1E2E;
}
.open-tracking-message-status.opened {
  color: #CDD6F4;
}
.open-tracking-message-status.unopened {
  color: #7F849C;
}
.account-switcher img {
  zoom: 1 !important;
  max-width: 10px;
  max-height: 6px;
  transform: none;
  background-image: none;
  background-color: #7F849C;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="10" height="6" viewBox="0 0 10 6"><path fill="#FFF" d="M0 0h1l4 4 4-4h1v1L5 6 0 1z"/></svg>');
}
.toolbar-activity .activity-toolbar-icon {
  background: #A6ADC8;
}
.div.suggestions:hover {
  color: #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item,
.button-dropdown.btn-emphasis .secondary-picker,
.button-dropdown.btn-emphasis .only-item {
  position: relative;
  color: #1E1E2E;
  font-weight: 500;
  background: #B4BEFE;
  box-shadow: none;
  border: 0px solid #B4BEFE;
}
.button-dropdown.btn-emphasis .primary-item img.content-mask,
.button-dropdown.btn-emphasis .secondary-picker img.content-mask,
.button-dropdown.btn-emphasis .only-item img.content-mask {
  background-color: #1E1E2E;
}
.button-dropdown.btn-emphasis .primary-item:active,
.button-dropdown.btn-emphasis .secondary-picker:active,
.button-dropdown.btn-emphasis .only-item:active {
  background: #B4BEFE;
}
.tokenizing-field .token {
  background: linear-gradient(to bottom, #313244 0%, #45475A 100%);
}
.tokenizing-field .token.selected,
.tokenizing-field .token.dragging {
  color: #1E1E2E;
}
.tokenizing-field .token.selected .action img,
.tokenizing-field .token.dragging .action img {
  background-color: #1E1E2E;
}
.menu .item.selected,
.menu .item:active {
  background-color: #4e4947;
  color: #CDD6F4;
}
.div.button-dropdown.open.open-down {
  background-color: #CDD6F4;
  color: #CDD6F4;
}
.menu .item.selected,
.menu .item:active {
  background-color: #B4BEFE;
  color: #1E1E2E;
}
.menu .item.selected .primary,
.menu .item:active .primary {
  color: #1E1E2E;
}
.menu .item.selected .secondary,
.menu .item:active .secondary {
  color: #313244;
}
.button-dropdown .primary-item,
.button-dropdown .only-item {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown .secondary-picker {
  background: -webkit-gradient(linear, left top, left bottom, from(#313244), to(#45475A));
}
.button-dropdown.open.open-down .secondary-items {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #313244;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08), 0 0 3px #B4BEFE;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content.unfocused {
  background-color: #313244;
}
.unsubscribe-action {
  color: #B4BEFE;
  text-decoration: underline;
}
.undo-redo-toast .content {
  background: #B4BEFE;
}
.preferences-wrap .container-accounts .accounts-content .account-details {
  background-color: #1E1E2E;
}
.nylas-editable-list .items-wrapper {
  background-color: #313244;
}
.thread-injected-quick-actions .btn {
  box-shadow: none;
}
.thread-list .list-item .list-column-HoverActions .action.action-trash {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-trash@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-archive {
  background: url('../static/images/thread-list-quick-actions/ic-quick-button-archive@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url('../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png') center no-repeat, #B4BEFE;
}
.thread-list .thread-icon.thread-icon-star,
.draft-list .thread-icon.thread-icon-star {
  filter: grayscale(100%);
}
#message-list .mail-label.removable {
  color: #1E1E2E !important;
  background: #B4BEFE !important;
  box-shadow: none !important;
}
.thread-list .thread-icon.thread-icon-unread,
.draft-list .thread-icon.thread-icon-unread {
  filter: grayscale(100%);
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(24, 24, 37, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(24, 24, 37, 0.8);
}
.tag-picker .menu .content-container {
  height: 250px;
  overflow-y: scroll;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.down {
  margin: 0 0 0 1px;
  padding: 0 5px;
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.down:hover {
  background: rgba(24, 24, 37, 0.8);
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.down:hover {
  background: #e5e5e5;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.down.btn-icon:hover {
  color: #CDD6F4;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.down.btn-icon:hover img.content-mask {
  background: rgba(35, 31, 32, 0.8);
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.up {
  margin: 0 0 0 1px;
  padding: 0 5px;
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.up:hover {
  background: rgba(24, 24, 37, 0.8);
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.up.btn-icon:hover {
  color: #CDD6F4;
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.up.btn-icon:hover img.content-mask {
  background: rgba(35, 31, 32, 0.8);
}
body.platform-win32 .sheet-toolbar .message-toolbar-arrow.disabled:hover {
  background: transparent;
}
body.platform-win32 #message-list .message-item-wrap .message-item-white-wrap {
  border-radius: 0;
}
body.platform-win32 #message-list .minified-bundle .num-messages {
  border-radius: 0;
}
body.platform-win32 #message-list .minified-bundle .msg-line {
  border-radius: 0;
}
body.platform-win32 #message-list .footer-reply-area-wrap {
  border-radius: 0;
}
body.platform-win32 .sidebar-section {
  border-radius: 0;
}
.sheet-toolbar .message-toolbar-items {
  order: 200;
  flex-grow: 0;
  flex-shrink: 0;
}
.sheet-toolbar .message-toolbar-arrow.down {
  order: 201;
  margin-right: 0;
  margin-left: 21px;
}
.sheet-toolbar .message-toolbar-arrow.up {
  order: 202;
  margin-right: 10.5px;
}
.sheet-toolbar .message-toolbar-arrow.disabled {
  opacity: 0.3;
}
.mode-split .message-nav-title {
  display: none;
}
.hide-sidebar-button {
  font-size: 13.02px;
  color: #A6ADC8;
  margin-left: 14px;
  cursor: default;
  user-select: none;
}
.hide-sidebar-button .img-wrap {
  margin-right: 7px;
  position: relative;
  top: -1px;
}
.hide-sidebar-button img {
  background: #A6ADC8;
}
#message-list.height-fix {
  height: calc(100% - 35px);
  min-height: calc(100% - 35px);
}
#message-list {
  display: flex;
  flex-direction: column;
  position: relative;
  background: #313244;
  transition: all 125ms ease-in-out;
  width: 100%;
  height: 100%;
  min-height: 100%;
  padding: 0;
  order: 2;
}
#message-list.restrict-width .message-subject-wrap {
  max-width: 800px;
}
#message-list.restrict-width .message-list-headers {
  max-width: 800px;
}
#message-list.restrict-width .message-item-wrap {
  max-width: 800px;
}
#message-list.restrict-width .msg-lines {
  max-width: 800px;
}
#message-list.restrict-width .message-item-area {
  max-width: 800px;
}
#message-list.restrict-width .footer-reply-area-wrap {
  max-width: 800px;
}
#message-list.restrict-width .footer-reply-area {
  max-width: 800px;
}
#message-list search-match,
#message-list .search-match {
  background: #fff000;
  border-radius: 3px;
  box-shadow: 0 0.5px 0.5px rgba(0, 0, 0, 0.25);
}
#message-list search-match.current-match,
#message-list .search-match.current-match {
  background: #ff8b1a;
}
#message-list .show-hidden-messages {
  background-color: #282938;
  border: 1px solid #20212c;
  border-radius: 3px;
  color: #7F849C;
  margin-bottom: 9px;
  cursor: default;
  padding: 5px 12px;
}
#message-list .show-hidden-messages a {
  float: right;
}
#message-list .message-body-error {
  background-color: #313244;
  border: 1px solid #20212c;
  color: #7F849C;
  margin-top: 9px;
  cursor: default;
  padding: 5px 12px;
}
#message-list .message-body-error a {
  float: right;
}
#message-list .message-body-loading {
  height: 1em;
  align-content: center;
  margin-top: 9px;
  margin-bottom: 9px;
}
#message-list .message-subject-wrap {
  margin: 5px auto 10px auto;
  user-select: text;
  line-height: 28.728px;
  display: flex;
  align-items: center;
  padding: 0 12px;
}
#message-list .mail-important-icon {
  margin-right: 7px;
  margin-bottom: 1px;
  flex-shrink: 0;
}
#message-list .message-subject {
  font-size: 15.96px;
  color: #CDD6F4;
  margin-right: 14px;
}
#message-list .message-icons-wrap {
  flex-shrink: 0;
  cursor: pointer;
  user-select: none;
  margin-left: auto;
  display: flex;
  align-items: center;
}
#message-list .message-icons-wrap img {
  background: #A6ADC8;
}
#message-list .message-icons-wrap div + div {
  margin-left: 10px;
}
#message-list .thread-injected-mail-labels {
  vertical-align: top;
  margin-top: 3px;
}
#message-list .message-list-headers {
  margin: 0 auto;
  width: 100%;
  display: block;
}
#message-list .message-list-headers .participants .contact-chip {
  display: inline-block;
}
#message-list .messages-wrap {
  flex: 1;
  opacity: 0;
  transition: opacity 0s;
}
#message-list .messages-wrap.ready {
  opacity: 1;
  transition: opacity 0.1s linear;
}
#message-list .messages-wrap .scroll-region-content-inner {
  padding: 6px;
}
#message-list .minified-bundle + .message-item-wrap {
  margin-top: -5px;
}
#message-list .message-item-wrap {
  transition: height 0.1s;
  position: relative;
  margin: 0 auto;
  padding-bottom: 12px;
}
#message-list .message-item-wrap .message-item-white-wrap {
  background: #1E1E2E;
  border: 0;
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08);
  border-radius: 4px;
}
#message-list .message-item-wrap.before-reply-area {
  padding-bottom: 0;
}
#message-list .message-item-wrap.collapsed .message-item-white-wrap {
  background-color: #1a1a28;
  padding-top: 19px;
  padding-bottom: 8px;
  margin-bottom: 0;
}
#message-list .message-item-wrap.collapsed + .minified-bundle {
  margin-top: -6px;
}
#message-list .message-item-wrap.collapsed .message-item-area {
  padding-bottom: 10px;
  display: flex;
  flex-direction: row;
  font-size: 13.02px;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-snippet {
  flex: 1;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
  cursor: default;
  color: #7F849C;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-attachment {
  width: 15px;
  height: 15px;
  background-size: 15px;
  background-repeat: no-repeat;
  background-position: center;
  padding: 12px;
  margin-left: 0.5em;
  background-image: url(../static/images/message-list/icon-attachment-@2x.png);
  position: relative;
  top: -2px;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-from {
  font-weight: 600;
  color: #7F849C;
  margin-right: 1em;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-timestamp {
  margin-left: 0.5em;
  color: #7F849C;
}
#message-list .message-item-divider {
  border: 0;
  border-top: 2px solid #181825;
  height: 3px;
  background: #313244;
  border-bottom: 1px solid #181825;
  margin: 0;
}
#message-list .message-item-divider.collapsed {
  height: 0;
  border-bottom: 0;
}
#message-list .minified-bundle {
  position: relative;
}
#message-list .minified-bundle .num-messages {
  position: absolute;
  top: 50%;
  left: 50%;
  margin-left: -80px;
  margin-top: -15px;
  border-radius: 15px;
  border: 1px solid #181825;
  width: 160px;
  text-align: center;
  color: #7F849C;
  z-index: 2;
  background: #1E1E2E;
}
#message-list .minified-bundle .num-messages:hover {
  cursor: default;
}
#message-list .minified-bundle .msg-lines {
  margin: 0 auto;
  width: 100%;
  margin-top: -13px;
}
#message-list .minified-bundle .msg-line {
  border-radius: 4px 4px 0 0;
  position: relative;
  border-top: 1px solid #181825;
  background-color: #1a1a28;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.1), 0 -0.5px 0 rgba(0, 0, 0, 0.1), 0.5px 0 0 rgba(0, 0, 0, 0.1), -0.5px 0 0 rgba(0, 0, 0, 0.1);
}
#message-list .message-header {
  position: relative;
  font-size: 13.02px;
  padding-bottom: 0;
  padding-top: 19px;
}
#message-list .message-header.pending .message-actions-wrap {
  width: 0;
  opacity: 0;
  position: absolute;
}
#message-list .message-header.pending .pending-spinner {
  opacity: 1;
}
#message-list .message-header .pending-spinner {
  transition: opacity 100ms;
  transition-delay: 50ms, 0ms;
  transition-timing-function: ease-in;
  pointer-events: none;
  opacity: 0;
}
#message-list .message-header .header-row {
  margin-top: 0.2em;
  color: #7F849C;
}
#message-list .message-header .header-row .header-label {
  float: left;
  display: block;
  font-weight: 400;
  margin-left: 0;
}
#message-list .message-header .message-actions-wrap {
  transition: opacity 100ms, width 150ms;
  transition-delay: 50ms, 0ms;
  transition-timing-function: ease-in-out;
  opacity: 1;
  text-align: left;
}
#message-list .message-header .message-actions-ellipsis {
  display: block;
  float: left;
}
#message-list .message-header .message-actions {
  display: inline-block;
  height: 23px;
  border: 1px solid #242438;
  border-radius: 11px;
  z-index: 4;
  margin-top: 0.35em;
  margin-left: 0.5em;
  text-align: center;
}
#message-list .message-header .message-actions .btn-icon {
  opacity: 0.75;
  padding: 0 7px;
  height: 20px;
  line-height: 10px;
  border-radius: 0;
  border-right: 1px solid #242438;
  margin: 0;
}
#message-list .message-header .message-actions .btn-icon:last-child {
  border-right: 0;
}
#message-list .message-header .message-actions .btn-icon:active {
  background: transparent;
}
#message-list .message-header .message-time {
  z-index: 2;
  position: relative;
  display: inline-block;
  min-width: 125px;
  cursor: default;
}
#message-list .message-header .msg-actions-tooltip {
  display: inline-block;
  margin-left: 1em;
}
#message-list .message-header .message-time,
#message-list .message-header .message-indicator {
  color: #7F849C;
}
#message-list .message-header .message-header-right {
  z-index: 4;
  position: relative;
  top: -2px;
  float: right;
  text-align: right;
  display: flex;
  align-items: baseline;
  height: 1em;
}
#message-list .message-item-area {
  width: 100%;
  margin: 0 auto;
  padding: 0 20px 14px 20px;
}
#message-list .message-item-area .message-iframe-container {
  margin-top: 10px;
}
#message-list .message-item-area iframe {
  width: 100%;
  border: 0;
  padding: 0;
  overflow: auto;
}
#message-list .collapse-region {
  width: calc(100% - 30px);
  height: 56px;
  position: absolute;
  top: 0;
}
#message-list .header-toggle-control {
  z-index: 3;
  position: absolute;
  top: 0;
  left: -13px;
}
#message-list .header-toggle-control.inactive {
  display: none;
}
#message-list .header-toggle-control img {
  background: #7F849C;
}
#message-list .message-item-wrap:hover .header-toggle-control.inactive {
  display: block;
}
#message-list .footer-reply-area-wrap {
  overflow: hidden;
  margin: -3px auto 10px auto;
  position: relative;
  z-index: 2;
  border: 0;
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08);
  border-top: 1px dashed #181825;
  border-radius: 0 0 4px 4px;
  background: #1E1E2E;
  color: #7F849C;
}
#message-list .footer-reply-area-wrap img.content-mask {
  background-color: #7F849C;
}
#message-list .footer-reply-area-wrap:hover {
  cursor: default;
}
#message-list .footer-reply-area-wrap .footer-reply-area {
  width: 100%;
  margin: 0 auto;
  padding: 12px 21px;
}
#message-list .footer-reply-area-wrap .reply-text {
  display: inline-block;
  vertical-align: middle;
  margin-left: 0.5em;
}
.download-all {
  display: flex;
  align-items: center;
  color: rgba(146, 146, 146, 0.8);
  font-size: 0.9em;
  cursor: default;
  margin-top: 10.5px;
}
.download-all .separator {
  margin: 0 5px;
}
.download-all .attachment-number {
  display: flex;
  align-items: center;
}
.download-all img {
  vertical-align: middle;
  margin-right: 7px;
  background-color: rgba(146, 146, 146, 0.8);
