css:
.notifications {
  background-color: #f6f7f8;
  box-shadow: 0 -6px 4px #f6f7f8;
  z-index: 2;
}
.notification {
  background: #009ec4;
  display: none;
  color: #ffffff;
  margin: 10px;
  margin-top: 0;
  border-radius: 5px;
}
.notification.error {
  background: #ca2541;
}
.notification.offline {
  background: linear-gradient(to top, #b88a00 0%, #cc9900 100%);
}
.notification.highest-priority {
  display: block;
}
.notif-top {
  display: flex;
  align-items: flex-start;
  padding: 10px;
}
.notification .icon {
  margin-right: 10px;
}
.notification .title {
  padding: 10px;
}
.notification .subtitle {
  font-size: 12.04px;
  position: relative;
  opacity: 0.8;
}
.notification .subtitle.has-action {
  cursor: pointer;
}
.notification .subtitle.has-action::after {
  content: '';
  background: url(mailspring://notifications/assets/minichevron@2x.png) top left no-repeat;
  background-size: 4.5px 7px;
  margin-left: 3px;
  display: inline-block;
  width: 4.5px;
  height: 7px;
  vertical-align: baseline;
}
.notification .actions-wrapper {
  display: flex;
}
.notification .action {
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  flex: 1;
  border-top: solid rgba(255, 255, 255, 0.5) 1px;
  border-left: solid rgba(255, 255, 255, 0.5) 1px;
  padding: 10px;
  cursor: pointer;
  /* The semi-transparent backgrounds that can be layered on top
     of this class shouldn't have sharp corners on the bottom */
  border-bottom-left-radius: 5px;
  border-bottom-right-radius: 5px;
}
.notification .action:first-child {
  border-left: none;
}
.notification .action:hover {
  background-color: rgba(255, 255, 255, 0.2);
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.15) inset;
}
.notification .action.loading {
  cursor: progress;
  background-color: rgba(0, 0, 0, 0.2);
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.15) inset;
}
