css:
.sheet-container {
  border-top: 1px solid #161320;
}
.sheet {
  background-color: #1A1826;
}
.sheet-toolbar {
  border: none;
}
body.platform-linux .sheet-toolbar-container {
  background: #1A1826;
}
.fixed-popover-container .fixed-popover input[type='text'],
input[type='text'],
input[type='email'],
input[type='date'],
input[type='datetime'],
input[type='datetime-local'],
input[type='month'],
input[type='number'],
input[type='password'],
input[type='search'],
input[type='tel'],
input[type='time'],
input[type='url'] {
  background: #302D41;
  border: 1px solid #161320;
  color: #D9E0EE;
  height: 2em;
}
.fixed-popover-container .fixed-popover input[type='text']:focus,
input[type='text']:focus,
input[type='email']:focus,
input[type='date']:focus,
input[type='datetime']:focus,
input[type='datetime-local']:focus,
input[type='month']:focus,
input[type='number']:focus,
input[type='password']:focus,
input[type='search']:focus,
input[type='tel']:focus,
input[type='time']:focus,
input[type='url']:focus {
  border: 1px solid #C9CBFF;
  box-shadow: inset 1px 0 #C9CBFF, inset -1px 0 #C9CBFF, inset 0 1px #C9CBFF, inset 0 -1px #C9CBFF;
}
.fixed-popover-container .fixed-popover input[type='text']::placeholder,
input[type='text']::placeholder,
input[type='email']::placeholder,
input[type='date']::placeholder,
input[type='datetime']::placeholder,
input[type='datetime-local']::placeholder,
input[type='month']::placeholder,
input[type='number']::placeholder,
input[type='password']::placeholder,
input[type='search']::placeholder,
input[type='tel']::placeholder,
input[type='time']::placeholder,
input[type='url']::placeholder {
  color: rgba(217, 224, 238, 0.3);
}
.btn,
.btn.btn-emphasis {
  box-shadow: none;
  background: #302D41;
  border: 1px solid #161320;
  color: #C9CBFF;
  height: 2.1em;
  font-size: 1em;
}
.btn.btn-small,
.btn.btn-emphasis.btn-small,
.btn.btn-large,
.btn.btn-emphasis.btn-large {
  font-size: 1em;
  padding: 0 0.8em;
  line-height: 1.9em;
  height: 2.1em;
}
.btn:hover,
.btn.btn-emphasis:hover {
  background: #575268;
  border: 1px solid #161320;
}
.btn:active,
.btn.btn-emphasis:active {
  background: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.btn.btn-disabled,
.btn.btn-emphasis.btn-disabled,
.btn.btn-disabled:active,
.btn.btn-emphasis.btn-disabled:active {
  background: #6E6C7E;
  border: 1px solid #161320;
  color: rgba(217, 224, 238, 0.45);
}
select {
  background: #302D41;
  border: 1px solid #161320;
  border-radius: 3px;
  color: #C9CBFF;
  height: 2.1em;
  padding: 0.1em 0.8em;
}
select:hover {
  background: #575268;
  border: 1px solid #161320;
}
select:active {
  background: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
select option {
  background: #1E1E2E;
  color: #D9E0EE;
}
.fixed-popover-container .fixed-popover-pointer {
  height: 0;
}
.fixed-popover-container .fixed-popover,
.category-picker-popover .menu,
.category-picker-dropdown .menu,
.dropdown-menu .menu,
.button-dropdown.open.open-down .secondary-items,
.button-dropdown.open.open-up .secondary-items {
  box-shadow: 0 2px 6px 1px rgba(0, 0, 0, 0.35);
  background: #1E1E2E;
  border-radius: 0;
}
.fixed-popover-container .fixed-popover .menu .header-container,
.category-picker-popover .menu .header-container,
.category-picker-dropdown .menu .header-container,
.menu .header-container {
  background: #1E1E2E;
}
.tokenizing-field .content-container {
  border: 1px solid #161320;
}
.fixed-popover-container .fixed-popover .menu .header-container {
  color: rgba(217, 224, 238, 0.6);
}
.menu .header-container {
  border-bottom: 1px solid #1E1E2E;
}
.menu .content-container {
  background: #1E1E2E;
}
.menu .footer-container,
.button-dropdown .secondary-items .menu .footer-container {
  background: #1E1E2E;
}
.template-picker .footer-container {
  border-top: 1px solid #1E1E2E;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .footer-container {
  border-top: 1px solid #1E1E2E;
}
.button-dropdown .secondary-items .menu .item,
.menu .item {
  background: #1E1E2E;
  color: #D9E0EE;
  border-radius: 0 !important;
}
.button-dropdown .secondary-items .menu .item img.content-mask,
.menu .item img.content-mask {
  background-color: #D9E0EE;
}
.button-dropdown .secondary-items .menu .item .secondary,
.menu .item .secondary {
  color: rgba(217, 224, 238, 0.6);
}
.button-dropdown .secondary-items .menu .item:not(.active):not(.selected):hover,
.menu .item:not(.active):not(.selected):hover,
.button-dropdown .secondary-items .menu .item.selected,
.menu .item.selected,
.button-dropdown .secondary-items .menu .item:active,
.menu .item:active {
  background-color: #C9CBFF;
  color: #161320;
}
.button-dropdown .secondary-items .menu .item:not(.active):not(.selected):hover img.content-mask,
.menu .item:not(.active):not(.selected):hover img.content-mask,
.button-dropdown .secondary-items .menu .item.selected img.content-mask,
.menu .item.selected img.content-mask,
.button-dropdown .secondary-items .menu .item:active img.content-mask,
.menu .item:active img.content-mask {
  background-color: #161320;
}
.button-dropdown .secondary-items .menu .item:not(.active):not(.selected):hover .secondary,
.menu .item:not(.active):not(.selected):hover .secondary,
.button-dropdown .secondary-items .menu .item.selected .secondary,
.menu .item.selected .secondary,
.button-dropdown .secondary-items .menu .item:active .secondary,
.menu .item:active .secondary {
  color: rgba(22, 19, 32, 0.6);
}
.category-picker-popover .menu .item.divider,
.category-picker-dropdown .menu .item.divider,
.fixed-popover-container .fixed-popover .divider {
  background: #1E1E2E;
  border-top: 1px solid #1E1E2E;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .item {
  padding: 5px 20px 5px 28px;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .item.checked {
  background-image: url(mailspring://Catppuccin/styles/images/controls/checkbox-checked-dark.png);
  background-size: 16px;
  background-position-y: 5px;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .item.checked:not(.active):not(.selected):hover,
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .item.checked.selected,
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown .secondary-items .menu .item.checked:active {
  background-image: url(mailspring://Catppuccin/styles/images/controls/checkbox-checked-selected.png);
}
.preferences-signatures-container .signature-wrap .account-picker .button-dropdown .secondary-items .menu .item {
  padding: 5px 20px 5px 32px;
}
.preferences-signatures-container .signature-wrap .account-picker .button-dropdown .secondary-items .menu .item.checked {
  background-image: url(mailspring://Catppuccin/styles/images/controls/checkbox-checked-dark.png);
  background-size: 16px;
  background-position-y: 7px;
  padding: 5px 20px 5px 32px;
}
.preferences-signatures-container .signature-wrap .account-picker .button-dropdown .secondary-items .menu .item.checked:not(.active):not(.selected):hover,
.preferences-signatures-container .signature-wrap .account-picker .button-dropdown .secondary-items .menu .item.checked.selected,
.preferences-signatures-container .signature-wrap .account-picker .button-dropdown .secondary-items .menu .item.checked:active {
  background-image: url(mailspring://Catppuccin/styles/images/controls/checkbox-checked-selected.png);
}
.undo-redo-toast .content {
  background: #1E1E2E;
  border: 1px solid #161320;
  border-radius: 2px;
  opacity: 0.95;
}
.undo-redo-toast .message {
  color: #D9E0EE;
}
.undo-redo-toast .action {
  background: #302D41;
  border: 1px solid #161320;
  border-radius: 3px;
}
.undo-redo-toast .action .undo-action-text {
  color: #D9E0EE;
}
.undo-redo-toast .action img {
  background-color: #D9E0EE;
}
.undo-redo-toast .action:hover {
  background: #575268;
  border: 1px solid #161320;
}
.undo-redo-toast .action:active {
  background: #C9CBFF;
  border: 1px solid #161320;
}
.undo-redo-toast .action:active .undo-action-text {
  color: #161320;
}
.undo-redo-toast .action:active img {
  background-color: #161320;
}
.countdown .countdown-number {
  color: #D9E0EE;
}
.countdown svg circle {
  stroke: red;
}
@keyframes countdown {
  0% {
    stroke-dashoffset: 0;
    stroke: #D9E0EE;
  }
  65% {
    stroke-dashoffset: 57.2px;
    stroke: #D9E0EE;
  }
  100% {
    stroke-dashoffset: 88px;
    stroke: #FAE3B0;
  }
}
.scroll-tooltip {
  box-shadow: none;
  background: #1E1E2E;
  border: 1px solid #161320;
  border-radius: 2px;
  opacity: 0.95;
  color: #D9E0EE;
  transform: translate(-8px, 0);
}
.scroll-tooltip:before,
.scroll-tooltip:after {
  display: none;
}
