css:
.column-DraftList {
  background: #1A1826;
  border-top: 5px solid #1E1E2E;
}
.column-DraftList .draft-list {
  border-top: 1px solid #161320;
}
.column-DraftList .empty-state .perspective-empty-state .message {
  color: rgba(217, 224, 238, 0.5);
}
.column-DraftList .empty-state .perspective-empty-state .message .btn {
  background-color: #302D41;
  border: 1px solid #161320;
  color: #C9CBFF;
  font-size: 14px;
}
.column-DraftList .empty-state .perspective-empty-state .message .btn:hover {
  background-color: #575268;
  border: 1px solid #161320;
}
.column-DraftList .empty-state .perspective-empty-state .message .btn:active {
  background-color: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.column-DraftList .empty-state .perspective-empty-state img {
  background-color: rgba(217, 224, 238, 0.8);
}
.draft-list .list-container .list-item {
  background-color: #1A1826;
  border-bottom: 1px solid #161320;
  color: #D9E0EE;
}
.draft-list .list-container .list-item .participants.no-recipients {
  color: rgba(217, 224, 238, 0.8);
}
.draft-list .list-container .list-item .checkmark .inner {
  width: 16px;
  height: 16px;
  background-image: url(mailspring://Catppuccin/styles/images/controls/checkbox-unchecked-dark.png);
  background-color: transparent;
  background-size: 16px 16px;
  border: none;
  border-radius: 0;
}
.draft-list .list-container .list-item:hover {
  background-color: #302D41;
}
.draft-list .list-container .list-item.selected .checkmark .inner {
  width: 16px;
  height: 16px;
  background-image: url(mailspring://Catppuccin/styles/images/controls/checkbox-checked-dark.png);
  background-color: transparent;
  background-size: 16px 16px;
  border: none;
  border-radius: 0;
}
.draft-list .list-container .list-item.keyboard-cursor {
  border-left-color: #C9CBFF;
}
.draft-list .scrollbar-track {
  background: transparent;
  border-left: 1px solid transparent;
  border-right: 1px solid transparent;
  width: 12px;
  padding: 3px 0px 3px 6px;
}
.draft-list .scrollbar-track .scrollbar-handle {
  background: #575268;
  border: none;
  border-radius: 3px;
  opacity: 0.4;
}
.draft-list .scrollbar-track:hover,
.draft-list .scrollbar-track.dragging {
  background: #302D41;
  border-left: 1px solid #161320;
  padding-left: 3px;
  padding-right: 2px;
}
.draft-list .scrollbar-track:hover .scrollbar-handle,
.draft-list .scrollbar-track.dragging .scrollbar-handle {
  opacity: 0.99;
}
.draft-list .scrollbar-track:hover:not(.dragging) .scrollbar-handle {
  background: #6E6C7E;
}
.draft-list .scrollbar-track:hover:not(.dragging) .scrollbar-handle:hover {
  background: #988BA2;
}
.draft-list .scrollbar-track.dragging .scrollbar-handle {
  background: #C9CBFF;
}
