css:
.toolbar-RootSidebar {
  height: 100%;
  background-color: #161320;
  border-right: 1px solid #161320;
}
.toolbar-RootSidebar .item-compose {
  background: none;
  box-shadow: none;
  border: 1px solid transparent;
  border-radius: 3px;
  height: 26px;
}
.toolbar-RootSidebar .item-compose img.content-mask {
  background-color: #c9cbff;
}
.toolbar-RootSidebar .item-compose:hover {
  background-color: #302D41;
  border-color: #161320;
}
.toolbar-RootSidebar .item-compose:active {
  background-color: #C9CBFF;
  border-color: #C9CBFF;
}
.toolbar-RootSidebar .item-compose:active img.content-mask {
  background-color: #161320;
}
body.is-blurred .toolbar-RootSidebar .item-compose {
  box-shadow: none !important;
}
body.is-blurred .toolbar-RootSidebar .item-compose img.content-mask {
  opacity: 1.0;
}
body.is-blurred .toolbar-RootSidebar .item-compose:hover {
  background-color: #302D41;
  border-color: #161320;
}
.toolbar-activity .activity-toolbar-icon {
  background-color: rgba(245, 224, 220, 0.5);
}
.toolbar-activity .activity-toolbar-icon:hover {
  background-color: #F5E0DC;
  cursor: pointer;
}
.toolbar-activity .activity-toolbar-icon.unread {
  background-color: #C9CBFF;
}
.toolbar-activity .activity-toolbar-icon.unread:hover {
  background-color: #C9CBFF;
}
.toolbar-activity .unread-count.active {
  background: #C9CBFF;
  font-weight: 600;
  padding-top: 1px;
}
.activity-list-container {
  background-color: #1A1826;
  border: 1px solid #161320;
}
.activity-list-container .activity-list-item {
  border-bottom: 1px solid #161320;
  color: #D9E0EE;
}
.activity-list-container .activity-list-item .timestamp {
  color: rgba(217, 224, 238, 0.5);
}
.activity-list-container .activity-list-item:hover {
  background-color: #302D41;
}
.activity-list-container .activity-list-item.unread,
.activity-list-container .activity-list-item.unread:hover {
  background-color: #C9CBFF;
  color: #1A1826;
}
.activity-list-container .activity-list-item.unread .timestamp,
.activity-list-container .activity-list-item.unread:hover .timestamp {
  color: rgba(26, 24, 38, 0.5);
}
.activity-list-container .activity-list-toggle-item {
  background-color: #1A1826;
  color: rgba(217, 224, 238, 0.5);
}
.activity-list-container .activity-list-toggle-item .timestamp {
  color: rgba(217, 224, 238, 0.5);
}
.activity-list-container .activity-list-toggle-item:last-child {
  border-bottom: 1px solid #161320;
}
.activity-list-container .activity-summary-cta {
  background: #1E1E2E;
  border-top: 1px solid #161320;
  color: rgba(217, 224, 238, 0.8);
}
.activity-list-container .activity-summary-cta:hover {
  cursor: pointer;
  color: #C9CBFF;
}
.activity-list-container .activity-summary-cta:active {
  background: #1E1E2E;
}
.activity-list-container .scrollbar-track {
  background: transparent;
  border-left: 1px solid transparent;
  border-right: 1px solid transparent;
  width: 12px;
  padding: 3px 0px 3px 6px;
}
.activity-list-container .scrollbar-track .scrollbar-handle {
  background: #575268;
  border: none;
  border-radius: 3px;
  opacity: 0.4;
}
.activity-list-container .scrollbar-track:hover,
.activity-list-container .scrollbar-track.dragging {
  background: #302D41;
  border-left: 1px solid #161320;
  padding-left: 3px;
  padding-right: 2px;
}
.activity-list-container .scrollbar-track:hover .scrollbar-handle,
.activity-list-container .scrollbar-track.dragging .scrollbar-handle {
  opacity: 0.99;
}
.activity-list-container .scrollbar-track:hover:not(.dragging) .scrollbar-handle {
  background: #6E6C7E;
}
.activity-list-container .scrollbar-track:hover:not(.dragging) .scrollbar-handle:hover {
  background: #988BA2;
}
.activity-list-container .scrollbar-track.dragging .scrollbar-handle {
  background: #C9CBFF;
}
