css:
.toolbar-ThreadList {
  height: 100%;
  background: #1A1826;
  border-bottom: 1px solid #161320;
}
.thread-search-bar {
  background: rgba(0, 0, 0, 0);
  box-shadow: none;
  padding-left: 9px;
  width: 100%;
  color: #D9E0EE;
}
.thread-search-bar.placeholder .tokenizing-contenteditable .layer-tokens {
  color: rgba(217, 224, 238, 0.5);
}
.thread-search-bar .tokenizing-contenteditable .layer-tokens {
  color: #D9E0EE;
}
.thread-search-bar .tokenizing-contenteditable .layer-tokens .layer-token {
  font-weight: 500;
  color: #C9CBFF;
}
.thread-search-bar .tokenizing-contenteditable .layer-text {
  caret-color: rgba(217, 224, 238, 0.5);
}
.thread-search-bar .search-accessory {
  background-image: none;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url(../static/images/toolbar/searchloupe@2x.png);
  -webkit-mask-size: 15px;
  -webkit-mask-position: 0px 0px;
  filter: none;
  background-color: #D9E0EE;
  padding-top: 15px;
}
.thread-search-bar .search-accessory.clear {
  background-image: none;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url(../static/images/search/searchclear@2x.png);
  -webkit-mask-size: 21px;
  -webkit-mask-position: 0px 0px;
  filter: none;
  background-color: rgba(217, 224, 238, 0.8);
}
.thread-search-bar .search-accessory.clear:hover {
  background-color: #D9E0EE;
}
.thread-search-bar .suggestions {
  background: #1A1826;
  border-bottom-left-radius: 0;
  border-bottom-right-radius: 0;
  top: 29px;
}
.thread-search-bar .suggestions .suggestion {
  color: rgba(217, 224, 238, 0.8);
}
.thread-search-bar .suggestions .suggestion .suggestion-token {
  color: #C9CBFF;
}
.thread-search-bar .suggestions .suggestion.selected {
  background-color: #C9CBFF;
  color: #1A1826;
}
.thread-search-bar .suggestions .suggestion.selected .suggestion-token {
  color: #1A1826;
}
.thread-search-bar .suggestions .suggestion:hover:not(.selected) {
  background-color: #302D41;
}
.thread-search-bar .suggestions .footer {
  background-color: #1E1E2E;
  border-color: #161320;
  color: rgba(217, 224, 238, 0.8);
}
.thread-search-bar .suggestions .footer a {
  color: #89DCEB;
  cursor: pointer;
}
.toolbar-ThreadList .selection-bar .absolute {
  background: #1E1E2E;
  border-color: #161320;
  height: 100%;
}
.toolbar-ThreadList .selection-bar .absolute .btn-toolbar {
  box-shadow: none;
  background: transparent;
  border: 1px solid transparent;
  border-radius: 3px !important;
  height: 26px;
  color: #c9cbff;
  margin-top: 4px;
  margin-bottom: 1px;
}
.toolbar-ThreadList .selection-bar .absolute .btn-toolbar img.content-mask {
  background-color: #c9cbff;
}
.toolbar-ThreadList .selection-bar .absolute .btn-toolbar:hover {
  background: #302d41;
  border-color: #1a1826;
}
.toolbar-ThreadList .selection-bar .absolute .btn-toolbar:active {
  background: #C9CBFF;
  border-color: #C9CBFF;
  color: #1A1826;
}
.toolbar-ThreadList .selection-bar .absolute .btn-toolbar:active img.content-mask {
  background-color: #1A1826;
}
.toolbar-ThreadList .selection-bar .absolute .inner .centered {
  padding-top: 1px;
  color: rgba(217, 224, 238, 0.8) !important;
}
.thread-list-empty-folder-bar {
  background-color: #C9CBFF;
  border-bottom-color: #161320;
  color: #161320;
  font-size: 1em;
}
.thread-list-empty-folder-bar .btn {
  background-color: rgba(26, 24, 38, 0);
  border: 1px solid rgba(26, 24, 38, 0.5);
  color: #161320;
}
.thread-list-empty-folder-bar .btn:hover {
  background-color: rgba(26, 24, 38, 0.2);
  border-color: rgba(26, 24, 38, 0.8);
}
.thread-list-empty-folder-bar .btn:active {
  background-color: #161320;
  border-color: #161320;
  color: #C9CBFF;
}
