css:
.sheet-toolbar {
  border-bottom: 1px solid #2c2c2c;
  box-shadow: none;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  filter: invert(100%);
}
img.content-dark {
  filter: invert(100%);
}
img.content-light {
  filter: invert(100%);
}
.mail-label {
  filter: contrast(110%) brightness(85%);
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: 0 0 0.5px rgba(255, 255, 255, 0.28), 0 1px 1.5px rgba(255, 255, 255, 0.08);
}
.thread-list .list-item:not(.unread):not(.selected):not(.focused) {
  color: #aaa;
}
.thread-list .list-item.thread-list-reminder-item {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread:not(.focused):not(.selected) {
  background-color: #312856;
}
.send-reminders-header {
  color: #7d5bef;
  background: #312856;
  border-bottom: 1px solid #4d3b8a;
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(44, 44, 44, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(44, 44, 44, 0.8);
}
.thread-search-bar {
  position: relative;
  order: -100;
  overflow: visible;
  z-index: 100;
  width: 450px;
  height: 23px;
  font-weight: 400;
  line-height: 23px;
  background: #2e2e2e;
  color: #eee;
  margin-top: 5px;
  padding-left: 5px;
  padding-right: 5px;
  border-radius: 3px;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
  display: flex;
  align-items: center;
}
.thread-search-bar:focus {
  border: 0;
  border-radius: 0;
  box-shadow: 0 0 0 2px rgba(8, 8, 8, 0.9);
}
.thread-search-bar.placeholder .tokenizing-contenteditable .layer-tokens {
  color: rgba(238, 238, 238, 0.6);
}
.thread-search-bar .search-accessory {
  width: 15px;
  height: 15px;
  object-fit: contain;
  zoom: 1 !important;
}
.thread-search-bar .search-accessory.clear {
  color: rgba(238, 238, 238, 0.8);
  width: 21px;
  height: 21px;
}
.thread-search-bar .suggestions {
  left: 0;
  top: 23px;
  width: 100%;
  position: absolute;
  background: #2e2e2e;
  border-bottom-left-radius: 3px;
  border-bottom-right-radius: 3px;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 4px 7px rgba(0, 0, 0, 0.15);
  padding-top: 4px;
}
.thread-search-bar .suggestions .suggestion {
  color: rgba(238, 238, 238, 0.6);
  padding: 4px 10px;
}
.thread-search-bar .suggestions .suggestion.selected {
  background-color: #58b660;
  color: white;
}
.thread-search-bar .suggestions .suggestion.selected .suggestion-token {
  color: white;
}
.thread-search-bar .suggestions .suggestion:hover:not(.selected) {
  background-color: rgba(35, 31, 32, 0.03);
}
.thread-search-bar .suggestions .suggestion .suggestion-token {
  color: #58b660;
  font-weight: 500;
}
.thread-search-bar .suggestions .footer {
  margin-top: 4px;
  color: rgba(238, 238, 238, 0.8);
  line-height: 1.4em;
  padding: 5px 10px;
  background-color: rgba(35, 31, 32, 0.03);
  border-top: 1px solid rgba(35, 31, 32, 0.06);
  font-size: 13.02px;
}
.thread-search-bar .tokenizing-contenteditable {
  flex: 1;
  height: 100%;
  position: relative;
  overflow-x: scroll;
  white-space: nowrap;
}
.thread-search-bar .tokenizing-contenteditable::-webkit-scrollbar {
  display: none;
}
.thread-search-bar .tokenizing-contenteditable .layer {
  top: 0;
  left: 0;
  bottom: 0;
  min-width: 100%;
  position: absolute;
  padding-left: 5px;
  padding-right: 5px;
}
.thread-search-bar .tokenizing-contenteditable .layer-tokens {
  color: #eee;
  user-select: none;
  pointer-events: none;
  z-index: 2;
}
.thread-search-bar .tokenizing-contenteditable .layer-tokens .layer-token {
  color: #58b660;
  line-height: 1.1em;
}
.thread-search-bar .tokenizing-contenteditable .layer-text {
  z-index: 1;
  caret-color: #eee;
  color: transparent;
}
