css:
.sheet-toolbar {
  border-bottom: 1px solid #2c2c2c;
  box-shadow: none;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  filter: invert(100%);
}
img.content-dark {
  filter: invert(100%);
}
img.content-light {
  filter: invert(100%);
}
.mail-label {
  filter: contrast(110%) brightness(85%);
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: 0 0 0.5px rgba(255, 255, 255, 0.28), 0 1px 1.5px rgba(255, 255, 255, 0.08);
}
.thread-list .list-item:not(.unread):not(.selected):not(.focused) {
  color: #aaa;
}
.thread-list .list-item.thread-list-reminder-item {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread:not(.focused):not(.selected) {
  background-color: #312856;
}
.send-reminders-header {
  color: #7d5bef;
  background: #312856;
  border-bottom: 1px solid #4d3b8a;
}
.related-threads {
  width: calc(100% + 30px);
  position: relative;
  left: -15px;
  border-top: 1px solid rgba(0, 0, 0, 0.15);
  transition: height 150ms ease-in-out;
  top: 15px;
  margin-top: -15px;
  overflow: hidden;
  border-radius: 0 0 5px 5px;
}
.related-threads .related-thread {
  display: flex;
  font-size: 12px;
  color: rgba(238, 238, 238, 0.6);
  width: 100%;
  padding: 0.5em 15px;
  border-top: 1px solid rgba(0, 0, 0, 0.08);
}
.related-threads .related-thread:hover {
  background: #292929;
}
.related-threads .related-thread .content {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  padding-right: 1em;
}
.related-threads .related-thread .content .snippet {
  opacity: 0.5;
}
.related-threads .toggle {
  font-size: 12px;
  text-align: center;
  padding: 0.5em 15px;
  border-top: 1px solid rgba(0, 0, 0, 0.08);
  color: #58b660;
}
.company-profile {
  padding-top: 20px;
  border-top: 1px solid #2c2c2c;
}
.company-profile .company-logo {
  margin-left: 12px;
  float: right;
  max-width: 60px;
  max-height: 44px;
}
.participant-profile,
.company-profile {
  margin-bottom: 22px;
}
.participant-profile .larger,
.company-profile .larger {
  font-size: 16px;
}
.participant-profile .contact-profile-photo,
.company-profile .contact-profile-photo {
  position: absolute;
  left: calc(50% - 25px);
  top: -31px;
}
.participant-profile .social-profiles-wrap,
.company-profile .social-profiles-wrap {
  margin-bottom: 14px;
}
.participant-profile .social-profile-item,
.company-profile .social-profile-item {
  margin: 0 10px;
  cursor: pointer;
}
.participant-profile .social-profile-item:first-child,
.company-profile .social-profile-item:first-child {
  margin-left: 0;
}
.participant-profile .social-profile-item:last-child,
.company-profile .social-profile-item:last-child {
  margin-right: 0;
}
.participant-profile .personal-info,
.company-profile .personal-info {
  padding-top: 30px;
  text-align: center;
  margin-bottom: 14px;
}
.participant-profile .personal-info .full-name,
.company-profile .personal-info .full-name,
.participant-profile .personal-info .email,
.company-profile .personal-info .email {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.participant-profile .personal-info .email,
.company-profile .personal-info .email {
  color: rgba(238, 238, 238, 0.6);
  margin-bottom: 14px;
}
.participant-profile .additional-info,
.company-profile .additional-info {
  margin-top: 10px;
  font-size: 12px;
  clear: both;
}
.participant-profile .additional-info p,
.company-profile .additional-info p {
  margin-bottom: 16px;
}
.participant-profile .additional-info .icon-row,
.company-profile .additional-info .icon-row {
  margin-bottom: 8px;
}
.participant-profile .additional-info .icon-row a.plain,
.company-profile .additional-info .icon-row a.plain {
  color: #eee;
}
.participant-profile .additional-info .bio,
.company-profile .additional-info .bio {
  color: rgba(238, 238, 238, 0.6);
}
body.platform-win32 .participant-profile,
body.platform-win32 .company-profile {
  border-radius: 0;
}
body.platform-win32 .participant-profile .profile-photo,
body.platform-win32 .company-profile .profile-photo {
  border-radius: 0;
}
body.platform-win32 .related-threads {
  border-radius: 0;
}
.feature-usage-modal.contact-profiles .feature-header {
  background: linear-gradient(to top, #777ff0, #a5aaf5);
}
.feature-usage-modal.contact-profiles .feature-name {
  color: #777ff0;
}
.feature-usage-modal.contact-profiles .pro-description li:before {
  color: #777ff0;
}
