css:
.sheet-toolbar {
  border-bottom: 1px solid #2c2c2c;
  box-shadow: none;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  filter: invert(100%);
}
img.content-dark {
  filter: invert(100%);
}
img.content-light {
  filter: invert(100%);
}
.mail-label {
  filter: contrast(110%) brightness(85%);
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: 0 0 0.5px rgba(255, 255, 255, 0.28), 0 1px 1.5px rgba(255, 255, 255, 0.08);
}
.thread-list .list-item:not(.unread):not(.selected):not(.focused) {
  color: #aaa;
}
.thread-list .list-item.thread-list-reminder-item {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread:not(.focused):not(.selected) {
  background-color: #312856;
}
.send-reminders-header {
  color: #7d5bef;
  background: #312856;
  border-bottom: 1px solid #4d3b8a;
}
body.platform-win32 .category-picker-popover {
  margin-left: 0;
}
.sheet-toolbar .btn-category-picker:only-of-type {
  margin-right: 0;
}
.category-picker-popover .menu,
.category-picker-dropdown .menu {
  background: #121212;
  width: 250px;
  max-height: 400px;
}
.category-picker-popover .menu .header-container,
.category-picker-dropdown .menu .header-container {
  border-bottom: 0;
}
.category-picker-popover .menu .item.divider,
.category-picker-dropdown .menu .item.divider {
  background-color: #e0e0e0;
  margin: 4px 0;
  height: 2px;
  padding: 0;
}
.category-picker-popover .btn.btn-toolbar,
.category-picker-dropdown .btn.btn-toolbar {
  margin-left: 0;
  margin-right: 0;
}
.category-picker-popover .check-wrap,
.category-picker-dropdown .check-wrap {
  width: 14px;
  height: 14px;
  border-radius: 2px;
  display: inline-block;
  position: relative;
  flex-shrink: 0;
  top: 2px;
}
.category-picker-popover .item img.content-mask,
.category-picker-dropdown .item img.content-mask {
  background-color: rgba(238, 238, 238, 0.8);
}
.category-picker-popover .item.selected img.content-mask,
.category-picker-dropdown .item.selected img.content-mask,
.category-picker-popover .item.active img.content-mask,
.category-picker-dropdown .item.active img.content-mask {
  background-color: white;
}
.category-picker-popover img.check-img,
.category-picker-dropdown img.check-img {
  position: absolute;
}
.category-picker-popover .category-item,
.category-picker-dropdown .category-item {
  font-size: 14px;
  display: flex;
  align-items: center;
}
.category-picker-popover .category-create-new-tag,
.category-picker-dropdown .category-create-new-tag {
  flex-shrink: 0;
}
.category-picker-popover .category-display-name,
.category-picker-dropdown .category-display-name {
  display: inline-block;
  margin-left: 10px;
  margin-right: 5px;
  word-break: break-word;
  flex: 1;
}
