css:
.column-MessageListSidebar {
  background: #1E1E2E;
  border-left: 1px solid #161320;
  padding: 10px;
}
.sidebar-section {
  background: #1A1826;
  border: 1px solid #161320;
  border-radius: 3px;
}
.sidebar-section p {
  color: #D9E0EE;
}
.contact-profile-photo {
  box-shadow: none;
  background: transparent;
  padding: 0;
}
.contact-profile-photo .profile-photo {
  width: 50px;
  height: 50px;
}
.contact-profile-photo .profile-photo .default-profile-image {
  box-shadow: none;
  background-image: none;
  border-radius: 50%;
  line-height: 50px;
  width: 50px;
  height: 50px;
}
.contact-profile-photo .profile-photo .default-profile-image .layer {
  border-radius: 50%;
}
.participant-profile .personal-info .email,
.company-profile .personal-info .email {
  color: #D9E0EE;
}
.participant-profile .larger,
.company-profile .larger,
.participant-profile .larger,
.company-profile .larger {
  color: #D9E0EE;
  font-weight: 700;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.related-threads {
  background: #1A1826;
  border-top: none;
}
.related-threads .related-thread {
  border-top: 1px solid #161320;
  color: rgba(217, 224, 238, 0.8);
}
.related-threads .related-thread:hover {
  background-color: #302D41;
}
.related-threads .related-thread:active {
  background-color: #C9CBFF;
  color: #161320;
}
.related-threads .toggle {
  border-top: 1px solid #161320;
  color: #C9CBFF;
  font-size: 14px;
}
.related-threads .toggle:hover {
  background-color: #575268;
}
.related-threads .toggle:active {
  background-color: #C9CBFF;
  color: #1A1826;
}
