css:
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #1A1826;
  border: none;
  box-shadow: none !important;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .composer-content-wrap {
  border: 1px solid #161320;
  border-radius: 3px;
  box-shadow: none;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap .composer-action-bar-wrap {
  background-color: #1E1E2E;
  box-shadow: none;
  border: none;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  background: #1A1826;
  border: none;
  box-shadow: none !important;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .composer-content-wrap {
  border: 1px solid #C9CBFF;
  box-shadow: inset 1px 0 #C9CBFF, inset -1px 0 #C9CBFF, inset 0 1px #C9CBFF, inset 0 -1px #C9CBFF;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused .composer-action-bar-wrap {
  background-color: #1E1E2E;
  box-shadow: none;
  border: none;
}
.composer-full-window .composer-inner-wrap .composer-action-bar-wrap {
  background-color: #1E1E2E;
  box-shadow: none;
  border-top: 1px solid #161320;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content {
  padding: 9px 0;
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar {
  box-shadow: none;
  background: #302D41 !important;
  border: 1px solid #161320;
  border-radius: 3px;
  color: #C9CBFF;
  height: 2.1em;
  line-height: 1.9em;
  padding: 0 0.8em !important;
  margin: 0 4px !important;
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar img.content-mask {
  background-color: #C9CBFF !important;
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:hover {
  background-color: #575268 !important;
  border-color: #161320;
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:active,
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar.btn-enabled {
  background-color: #C9CBFF !important;
  border-color: #1A1826;
}
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar:active img.content-mask,
.composer-inner-wrap .composer-action-bar-wrap .btn.btn-toolbar.btn-enabled img.content-mask {
  background-color: #1A1826 !important;
}
.composer-inner-wrap .composer-action-bar-wrap .btn-send {
  margin-top: 1px;
}
.composer-inner-wrap .composer-action-bar-wrap .btn-send .primary-item,
.composer-inner-wrap .composer-action-bar-wrap .btn-send .secondary-picker {
  box-shadow: none;
  background: #ABE9B3;
  border: 1px solid #161320;
  color: #1A1826;
  height: 2.1em;
  line-height: 1.9em;
  padding: 0 0.8em;
}
.composer-inner-wrap .composer-action-bar-wrap .btn-send .primary-item img.content-mask,
.composer-inner-wrap .composer-action-bar-wrap .btn-send .secondary-picker img.content-mask {
  background-color: #1A1826;
}
.composer-inner-wrap .composer-action-bar-wrap .btn-send .primary-item {
  border-radius: 3px 0px 0px 3px;
  margin: 0 0 0 4px;
}
.composer-inner-wrap .composer-action-bar-wrap .btn-send .secondary-picker {
  border-radius: 0px 3px 3px 0px;
  margin: 0 4px 0 0;
}
.composer-inner-wrap .composer-action-bar-wrap .btn-send .secondary-picker img.content-mask {
  margin-top: -3px;
}
.composer-inner-wrap .composer-action-bar-wrap .btn-send:hover .primary-item,
.composer-inner-wrap .composer-action-bar-wrap .btn-send:hover .secondary-picker {
  background-color: #B5E8E0;
  border-color: #161320;
}
.composer-inner-wrap .composer-action-bar-wrap .btn-send .primary-item:active,
.composer-inner-wrap .composer-action-bar-wrap .btn-send .secondary-picker:active {
  background-color: #B5E8E0;
  border-color: #161320;
}
.composer-full-window .composer-inner-wrap .composer-action-bar-content {
  padding: 9px 12px;
}
.tokenizing-field .tokenizing-field-label {
  color: rgba(217, 224, 238, 0.5);
  margin-top: 2px;
}
.composer-inner-wrap .composer-field-label {
  color: rgba(217, 224, 238, 0.5);
}
.composer-participant-field {
  color: #D9E0EE;
}
.composer-participant-field:after {
  background: rgba(217, 224, 238, 0.15);
}
.tokenizing-field .header-container {
  background: transparent;
  border: none;
}
.tokenizing-field .token {
  box-shadow: none;
  background: #302D41;
  border: 1px solid #161320;
  color: #C9CBFF;
  height: 2.1em;
  font-size: 1em;
}
.tokenizing-field .token .action img {
  background-color: #C9CBFF;
}
.tokenizing-field .token:hover {
  box-shadow: none;
  background: #575268;
  border: 1px solid #161320;
}
.tokenizing-field .token.selected,
.tokenizing-field .token.dragging {
  box-shadow: none;
  background: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.tokenizing-field .token.selected .action img,
.tokenizing-field .token.dragging .action img {
  background-color: #1A1826;
}
body.is-blurred .tokenizing-field .token:not(.invalid) {
  background: #302D41;
  border: 1px solid #161320;
  color: #C9CBFF;
}
body.is-blurred .tokenizing-field .token:not(.invalid):hover {
  box-shadow: none;
  background: #575268;
  border: 1px solid #161320;
}
body.is-blurred .tokenizing-field .token:not(.invalid).selected,
body.is-blurred .tokenizing-field .token:not(.invalid).dragging {
  box-shadow: none;
  background: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
body.is-blurred .tokenizing-field .token:not(.invalid).selected .action img,
body.is-blurred .tokenizing-field .token:not(.invalid).dragging .action img {
  background-color: #1A1826;
}
.tokenizing-field .token.invalid {
  border-bottom: 1px dashed #F28FAD;
}
.tokenizing-field .token.invalid:hover {
  box-shadow: none;
  border-bottom: 1px dashed #F28FAD;
}
.tokenizing-field .token.invalid.selected,
.tokenizing-field .token.invalid.dragging {
  box-shadow: none;
  background: #F28FAD;
  border: 1px solid #F28FAD;
}
.tokenizing-field .tokenizing-field-input input {
  margin-top: 1px;
}
.tokenizing-field .tokenizing-field-input input:focus {
  border: none;
}
.composer-inner-wrap .composer-header-actions .action {
  color: rgba(217, 224, 238, 0.8);
}
.composer-inner-wrap .composer-header-actions .action img.content-mask {
  background-color: rgba(217, 224, 238, 0.8);
}
.composer-inner-wrap .composer-header-actions .action:hover {
  color: #C9CBFF;
  cursor: pointer;
}
.composer-inner-wrap .composer-header-actions .action:hover img.content-mask {
  background-color: #C9CBFF;
}
.composer-participant-field .button-dropdown .primary-item,
.composer-participant-field .button-dropdown .only-item {
  box-shadow: none;
  background: #302D41;
  border: 1px solid #161320;
  color: #C9CBFF;
  height: 2.1em;
  font-size: 1em;
  margin-top: -2px;
}
.composer-participant-field .button-dropdown .primary-item img.content-mask,
.composer-participant-field .button-dropdown .only-item img.content-mask {
  background-color: #C9CBFF;
}
.composer-participant-field .button-dropdown .primary-item:hover,
.composer-participant-field .button-dropdown .only-item:hover {
  background: #575268;
  border: 1px solid #161320;
}
.composer-participant-field .button-dropdown .primary-item:active,
.composer-participant-field .button-dropdown .only-item:active {
  background: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.composer-participant-field .button-dropdown .primary-item:active img.content-mask,
.composer-participant-field .button-dropdown .only-item:active img.content-mask {
  background-color: #1A1826;
}
.composer-inner-wrap .composer-subject input {
  margin: 3px 0 6px;
}
.composer-inner-wrap .composer-subject input::placeholder {
  color: rgba(217, 224, 238, 0.3);
}
.composer-inner-wrap .composer-subject input:focus {
  border: none;
}
.composer-inner-wrap .composer-subject:after {
  background: rgba(217, 224, 238, 0.15);
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item,
.composer-full-window .message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item {
  box-shadow: none;
  background: #302D41;
  border: 1px solid #161320;
  height: 2.1em;
  font-size: 1em;
  padding: 0 0.8em;
  margin-top: -2px;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item img.signature-button,
.composer-full-window .message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item img.signature-button {
  background-color: #C9CBFF;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item:hover,
.composer-full-window .message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item:hover {
  background: #575268;
  border: 1px solid #161320;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item:active,
.composer-full-window .message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item:active {
  background: #C9CBFF;
  border: 1px solid #1A1826;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item:active img.signature-button,
.composer-full-window .message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item:active img.signature-button {
  background-color: #1A1826;
}
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown.open img.signature-button,
.composer-full-window .message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .button-dropdown.open img.signature-button,
.message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item img.signature-button,
.composer-full-window .message-item-white-wrap.composer-outer-wrap .composer-participant-field .dropdown-component .signature-button-dropdown .only-item img.signature-button {
  background-color: #C9CBFF;
}
.RichEditor-toolbar .inner {
  background: #1A1826 !important;
  padding-top: 1px;
}
.RichEditor-toolbar .floating-container {
  border-bottom: 1px solid rgba(217, 224, 238, 0.15);
}
.RichEditor-toolbar button {
  color: #C9CBFF;
}
.RichEditor-toolbar button:hover {
  color: #C9CBFF;
}
.RichEditor-toolbar button.active {
  color: #C9CBFF;
}
.RichEditor-toolbar button.with-select {
  margin: 3px 0;
  max-width: 120px;
}
.RichEditor-toolbar button.with-select select {
  background: #302D41;
  border: 1px solid #161320;
  padding: 0 0.8em;
}
.RichEditor-toolbar button.with-select select:hover,
.RichEditor-toolbar button.with-select select:focus {
  background: #575268;
  border: 1px solid #161320;
}
.RichEditor-toolbar button.with-select select:active {
  background: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.RichEditor-toolbar button.with-select .fa {
  padding-right: 6px;
}
.RichEditor-toolbar .divider {
  height: 13px;
  border-color: rgba(217, 224, 238, 0.15);
}
.RichEditor-root .RichEditor-content {
  color: #D9E0EE;
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text {
  background: #302D41;
  border: 1px solid #161320;
  border-radius: 50%;
  height: 20px;
  width: 20px;
  top: -10px;
  right: -10px;
  padding-top: 2px;
  cursor: default;
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text img {
  height: 0;
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text:before {
  content: 'x';
  margin-left: 6px;
  color: #C9CBFF;
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text:hover {
  background-color: #575268;
  border-color: #161320;
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text:active {
  background-color: #C9CBFF;
  border-color: #1A1826;
}
.composer-inner-wrap .compose-body .quoted-text-control .remove-quoted-text:active:before {
  content: 'x';
  color: #1A1826;
}
