css:
.toolbar-MessageList .message-toolbar-items {
  margin-right: 6px;
}
.toolbar-MessageList .btn-toolbar.mode-toggle {
  margin-left: 4.5px;
}
.toolbar-MessageList,
.toolbar-MessageListSidebar {
  height: 100%;
  background: #1E1E2E;
  border-left: 1px solid #161320;
}
.toolbar-MessageList .btn-toolbar,
.toolbar-MessageListSidebar .btn-toolbar,
.toolbar-MessageList .btn-icon,
.toolbar-MessageListSidebar .btn-icon {
  background: none;
  box-shadow: none;
  border: 1px solid transparent !important;
  border-radius: 3px !important;
  height: 26px;
}
.toolbar-MessageList .btn-toolbar img.content-mask,
.toolbar-MessageListSidebar .btn-toolbar img.content-mask,
.toolbar-MessageList .btn-icon img.content-mask,
.toolbar-MessageListSidebar .btn-icon img.content-mask {
  background-color: #c9cbff;
}
.toolbar-MessageList .btn-toolbar:hover,
.toolbar-MessageListSidebar .btn-toolbar:hover,
.toolbar-MessageList .btn-icon:hover,
.toolbar-MessageListSidebar .btn-icon:hover {
  border-color: #1a1826 !important;
  background-color: #302d41;
}
.toolbar-MessageList .btn-toolbar:active,
.toolbar-MessageListSidebar .btn-toolbar:active,
.toolbar-MessageList .btn-icon:active,
.toolbar-MessageListSidebar .btn-icon:active,
.toolbar-MessageList .btn-toolbar.mode-toggle.mode-false,
.toolbar-MessageListSidebar .btn-toolbar.mode-toggle.mode-false,
.toolbar-MessageList .btn-icon.mode-toggle.mode-false,
.toolbar-MessageListSidebar .btn-icon.mode-toggle.mode-false {
  border-color: #C9CBFF !important;
  background-color: #C9CBFF;
}
.toolbar-MessageList .btn-toolbar:active img.content-mask,
.toolbar-MessageListSidebar .btn-toolbar:active img.content-mask,
.toolbar-MessageList .btn-icon:active img.content-mask,
.toolbar-MessageListSidebar .btn-icon:active img.content-mask,
.toolbar-MessageList .btn-toolbar.mode-toggle.mode-false img.content-mask,
.toolbar-MessageListSidebar .btn-toolbar.mode-toggle.mode-false img.content-mask,
.toolbar-MessageList .btn-icon.mode-toggle.mode-false img.content-mask,
.toolbar-MessageListSidebar .btn-icon.mode-toggle.mode-false img.content-mask {
  background-color: #1A1826;
}
.toolbar-MessageList .btn-toolbar.disabled,
.toolbar-MessageListSidebar .btn-toolbar.disabled,
.toolbar-MessageList .btn-icon.disabled,
.toolbar-MessageListSidebar .btn-icon.disabled {
  opacity: 1;
  border-color: transparent !important;
  background-color: transparent;
}
.toolbar-MessageList .btn-toolbar.disabled img.content-mask,
.toolbar-MessageListSidebar .btn-toolbar.disabled img.content-mask,
.toolbar-MessageList .btn-icon.disabled img.content-mask,
.toolbar-MessageListSidebar .btn-icon.disabled img.content-mask {
  background-color: #C9CBFF;
}
body.is-blurred .toolbar-MessageList .btn-toolbar,
body.is-blurred .toolbar-MessageListSidebar .btn-toolbar,
body.is-blurred .toolbar-MessageList .btn-icon,
body.is-blurred .toolbar-MessageListSidebar .btn-icon {
  box-shadow: none;
}
body.is-blurred .toolbar-MessageList .btn-toolbar img.content-mask,
body.is-blurred .toolbar-MessageListSidebar .btn-toolbar img.content-mask,
body.is-blurred .toolbar-MessageList .btn-icon img.content-mask,
body.is-blurred .toolbar-MessageListSidebar .btn-icon img.content-mask {
  opacity: 1.0;
}
body.is-blurred .toolbar-MessageList .btn-toolbar:hover,
body.is-blurred .toolbar-MessageListSidebar .btn-toolbar:hover,
body.is-blurred .toolbar-MessageList .btn-icon:hover,
body.is-blurred .toolbar-MessageListSidebar .btn-icon:hover {
  border-color: #1a1826;
  background-color: #302d41;
}
body.is-blurred .toolbar-MessageList .btn-toolbar.mode-toggle.mode-false,
body.is-blurred .toolbar-MessageListSidebar .btn-toolbar.mode-toggle.mode-false,
body.is-blurred .toolbar-MessageList .btn-icon.mode-toggle.mode-false,
body.is-blurred .toolbar-MessageListSidebar .btn-icon.mode-toggle.mode-false {
  background-color: #C9CBFF;
}
.toolbar-MessageList .btn-icon,
.toolbar-MessageListSidebar .btn-icon {
  margin-top: 5px;
  padding: 0 13px;
}
.toolbar-MessageList .btn-icon img.content-mask,
.toolbar-MessageListSidebar .btn-icon img.content-mask {
  -webkit-mask-position: -7px -15px;
  -webkit-mask-size: 48px;
  height: 32px;
  width: 32px;
}
