css:
body.platform-linux .sheet-toolbar-container[data-id="Preferences"] {
  background: #1E1E2E;
}
.sheet-toolbar .item-back {
  box-shadow: none;
  background: transparent;
  border: 1px solid transparent;
  border-radius: 3px;
  height: 26px;
  margin-left: 10px;
  margin-top: 9px;
  padding: 0px 13px 0px 5px;
}
.sheet-toolbar .item-back img.content-mask {
  zoom: 0.3 !important;
  background-color: #c9cbff;
}
.sheet-toolbar .item-back .item-back-title {
  margin: 1px 2px 0 3px;
  font-size: 14px;
  font-weight: bold;
  color: #c9cbff;
}
.sheet-toolbar .item-back:hover {
  background-color: #302d41;
  border-color: #1a1826;
}
.sheet-toolbar .item-back:active {
  background-color: #C9CBFF;
  border-color: #C9CBFF;
}
.sheet-toolbar .item-back:active img.content-mask {
  background-color: #1A1826;
}
.sheet-toolbar .item-back:active .item-back-title {
  color: #1A1826;
}
.preferences-wrap .container-preference-tabs {
  border-color: #161320;
}
.preferences-wrap .container-preference-tabs .preferences-tabs {
  background-color: #1E1E2E;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item {
  box-shadow: none;
  border: 1px solid transparent;
  border-radius: 1px 1px 0 0;
  margin: 0px -1px -1px 0px;
  padding-top: 0px;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item .tab-icon {
  display: none;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item .name {
  padding: 10px 11px 8px;
  font-size: 14px;
  font-weight: 500;
  color: rgba(217, 224, 238, 0.5);
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item:hover {
  background: #28283d;
  border-color: #161320;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item.active {
  box-shadow: none;
  background: #222234;
  border-color: #161320;
  border-bottom-color: #222234;
  border-radius: 1px 1px 0 0;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item.active .name {
  color: #D9E0EE;
}
.preferences-wrap {
  color: #D9E0EE;
}
.preferences-wrap .preferences-content {
  background-color: #222234;
}
.preferences-wrap h6 {
  font-size: 13px;
  font-weight: 700;
  text-transform: uppercase;
  color: rgba(217, 224, 238, 0.5);
  border-bottom: 1px solid rgba(217, 224, 238, 0.15);
  padding-bottom: 3px;
}
.preferences-wrap .container-keymaps .shortcut-section-title,
.preferences-wrap .account-section-title {
  font-size: 13px;
  font-weight: 700;
  text-transform: uppercase;
  color: rgba(217, 224, 238, 0.5);
  border-bottom: 1px solid rgba(217, 224, 238, 0.15);
  padding-bottom: 3px;
}
.preferences-wrap input[type='checkbox'] {
  -webkit-appearance: none;
  width: 16px;
  height: 16px;
  background-image: url(mailspring://Catppuccin/styles/images/controls/checkbox-unchecked-dark.png);
  background-color: transparent;
  background-size: 16px 16px;
  border: none;
  border-radius: 0;
  top: 3px;
}
.preferences-wrap input[type='checkbox']:checked {
  width: 16px;
  height: 16px;
  background-image: url(mailspring://Catppuccin/styles/images/controls/checkbox-checked-dark.png);
  background-color: transparent;
  background-size: 16px 16px;
  border: none;
  border-radius: 0;
}
.preferences-wrap input[type='radio'] {
  -webkit-appearance: none;
  width: 16px;
  height: 16px;
  background-image: url(mailspring://Catppuccin/styles/images/controls/radio-unchecked-dark.png);
  background-color: transparent;
  background-size: 16px 16px;
  border: none;
  border-radius: 0;
  top: -2px;
}
.preferences-wrap input[type='radio']:checked {
  width: 16px;
  height: 16px;
  background-image: url(mailspring://Catppuccin/styles/images/controls/radio-checked-dark.png);
  background-color: transparent;
  background-size: 16px 16px;
  border: none;
  border-radius: 0;
}
.preferences-wrap p {
  color: rgba(217, 224, 238, 0.5);
  font-size: 1em;
}
.preferences-wrap .platform-note {
  background-color: #2c2c44;
  border-left: 3px solid #C9CBFF;
  color: rgba(217, 224, 238, 0.8);
  font-size: 1em;
  margin: 10px 0;
}
.preferences-wrap .platform-note::before {
  color: #C9CBFF;
}
.preferences-wrap .preferences-content .container-dropdown {
  margin: 5px 0 8px;
}
.preferences-wrap .preferences-content .container-dropdown div:first-child {
  margin-top: 3px;
}
.preferences-wrap .container-general .local-data .btn {
  border: 1px solid #161320;
  border-radius: 3px;
}
.preferences-wrap .container-general .local-data .btn:hover {
  border: 1px solid #161320;
}
.preferences-wrap .container-general .local-data .btn:active {
  border: 1px solid #1A1826;
}
.preferences-wrap .container-accounts .accounts-content .account-list .account {
  border-bottom: 1px solid #161320;
}
.preferences-wrap .container-accounts .accounts-content .account-details {
  background-color: #222234;
  border-color: #222234;
  width: 420px;
}
.preferences-wrap .container-accounts .accounts-content .account-details > input {
  font-size: 1em;
}
.preferences-wrap .container-accounts .accounts-content .account-details select {
  margin-right: 6px;
}
.nylas-editable-list .items-wrapper {
  background-color: #1A1826;
  border-color: #161320;
  border-radius: 3px 3px 0 0;
}
.nylas-editable-list .items-wrapper .list-item:first-child {
  border-radius: 3px 3px 0 0;
}
.nylas-editable-list .items-wrapper .list-item:hover {
  background: #302D41;
}
.nylas-editable-list .items-wrapper .list-item.selected,
.nylas-editable-list .items-wrapper .list-item.editing {
  background-color: #C9CBFF;
  color: #1A1826;
}
.nylas-editable-list .items-wrapper .list-item.editable-item {
  border-bottom: 1px solid #161320;
}
.nylas-editable-list .items-wrapper .scrollbar-track {
  background: transparent;
  border-left: 1px solid transparent;
  border-right: 1px solid transparent;
  width: 12px;
  padding: 3px 0px 3px 6px;
}
.nylas-editable-list .items-wrapper .scrollbar-track .scrollbar-handle {
  background: #575268;
  border: none;
  border-radius: 3px;
  opacity: 0.4;
}
.nylas-editable-list .items-wrapper .scrollbar-track:hover,
.nylas-editable-list .items-wrapper .scrollbar-track.dragging {
  background: #302D41;
  border-left: 1px solid #161320;
  padding-left: 3px;
  padding-right: 2px;
}
.nylas-editable-list .items-wrapper .scrollbar-track:hover .scrollbar-handle,
.nylas-editable-list .items-wrapper .scrollbar-track.dragging .scrollbar-handle {
  opacity: 0.99;
}
.nylas-editable-list .items-wrapper .scrollbar-track:hover:not(.dragging) .scrollbar-handle {
  background: #6E6C7E;
}
.nylas-editable-list .items-wrapper .scrollbar-track:hover:not(.dragging) .scrollbar-handle:hover {
  background: #988BA2;
}
.nylas-editable-list .items-wrapper .scrollbar-track.dragging .scrollbar-handle {
  background: #C9CBFF;
}
.nylas-editable-list:focus .items-wrapper .list-item.selected {
  background-color: #C9CBFF;
  color: #1A1826;
}
.nylas-editable-list .buttons-wrapper {
  background-color: #222234;
  border-color: #161320;
  border-radius: 0 0 3px 3px;
}
.nylas-editable-list .buttons-wrapper .btn-editable-list {
  border-color: #161320;
  color: #C9CBFF;
}
.nylas-editable-list .buttons-wrapper .btn-editable-list:first-child {
  border-radius: 0 0 0 3px;
}
.nylas-editable-list .buttons-wrapper .btn-editable-list.btn-disabled,
.nylas-editable-list .buttons-wrapper .btn-editable-list.btn-disabled:active {
  opacity: 1;
  background-color: #222234;
  color: rgba(217, 224, 238, 0.45);
}
.nylas-editable-list .buttons-wrapper .btn-editable-list:hover:not(.btn-disabled) {
  background-color: #575268;
}
.nylas-editable-list .buttons-wrapper .btn-editable-list:active:not(.btn-disabled) {
  background-color: #C9CBFF;
  color: #1A1826;
}
.container-identity .identity-content-box {
  color: #D9E0EE;
}
.container-identity .identity-content-box .padded:last-child {
  border-top: 1px solid rgba(217, 224, 238, 0.15);
}
.container-identity .feature-explore-grid .feature {
  background-color: #2c2c44;
  border-left: 3px solid #C9CBFF;
}
.container-identity .feature-explore-grid .feature h3 {
  color: #D9E0EE;
}
.container-identity .feature-explore-grid .feature p {
  color: rgba(217, 224, 238, 0.8);
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode {
  background-color: #302D41;
  border: 1px solid #161320;
  border-radius: 3px;
  color: #C9CBFF;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode img.content-mask {
  background-color: #C9CBFF;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode:hover {
  background-color: #575268;
  border: 1px solid #161320;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode.active {
  background-color: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.preferences-wrap .container-appearance .appearance-mode-switch .appearance-mode.active img.content-mask {
  background-color: #1A1826;
}
.preferences-wrap .container-appearance .platform-linux-only .platform-note .btn {
  margin-top: -3px;
}
.preferences-wrap .container-appearance .appearance-scale-slider {
  margin: 16px 0;
}
.preferences-wrap .container-appearance .appearance-scale-slider .ruler .midpoint {
  border-right: 1px solid rgba(217, 224, 238, 0.5);
}
.preferences-wrap .container-appearance .appearance-scale-slider .ruler img {
  background-image: none;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-size: 48px;
  background-color: #D9E0EE;
  width: 48px;
  height: 48px;
  padding-top: 48px;
}
.preferences-wrap .container-appearance .appearance-scale-slider .ruler img[alt='appearance-scale-small.png'] {
  -webkit-mask-image: url(../static/images/preferences/appearance/appearance-scale-small@2x.png);
}
.preferences-wrap .container-appearance .appearance-scale-slider .ruler img[alt='appearance-scale-big.png'] {
  -webkit-mask-image: url(../static/images/preferences/appearance/appearance-scale-big@2x.png);
}
.preferences-wrap .container-appearance .appearance-scale-slider input[type='range'] {
  -webkit-appearance: none;
  background: transparent;
}
.preferences-wrap .container-appearance .appearance-scale-slider input[type='range']::-webkit-slider-runnable-track {
  background-color: #302D41;
  border-radius: 2.5px;
  height: 5px;
}
.preferences-wrap .container-appearance .appearance-scale-slider input[type='range']::-webkit-slider-thumb {
  -webkit-appearance: none;
  width: 15px;
  height: 15px;
  background: #6E6C7E;
  border: 1px solid #161320;
  border-radius: 50%;
  margin-top: -5px;
}
.preferences-wrap .container-appearance .appearance-scale-slider input[type='range']::-webkit-slider-thumb:hover {
  background: #575268;
  border-color: #161320;
}
.preferences-wrap .container-appearance .appearance-scale-slider input[type='range']::-webkit-slider-thumb:active {
  background: #C9CBFF;
  border-color: #C9CBFF;
}
.preferences-wrap .container-keymaps .shortcut {
  color: #D9E0EE;
}
.preferences-wrap .container-keymaps .shortcut .values {
  color: #D9E0EE;
}
.preferences-wrap .container-keymaps .shortcut:hover .values {
  background: #575268;
  outline: 1px solid #161320;
  color: #C9CBFF;
}
.preferences-wrap .container-keymaps .shortcut.editing .values {
  background: #C9CBFF;
  outline: 1px solid #1A1826;
  color: #1A1826;
}
.preferences-wrap .container-keymaps h2 {
  color: #D9E0EE;
}
.container-mail-rules .empty-list {
  background-color: #1A1826;
  border: 1px solid #161320;
  border-radius: 3px;
}
.container-mail-rules .empty-list h2 {
  color: rgba(217, 224, 238, 0.5);
}
.container-mail-rules .empty-list .icon-mail-rules {
  background-image: none;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url(../static/images/preferences/mail-rules/rules-big@2x.png);
  background-color: rgba(217, 224, 238, 0.8);
}
.container-mail-rules .rule-detail {
  background-color: #222234;
  border-color: #222234;
}
.container-mail-rules .rule-detail .well {
  background-color: #222234;
  border-color: #222234;
  font-size: 1em;
}
.container-mail-rules .rule-detail .well .well-row input:last-child {
  margin-left: 0px;
  margin-top: 8px;
}
.container-mail-rules .rule-detail .inner > select {
  margin-left: 6px;
  margin-right: 6px;
}
.preferences-wrap .category-mapper-container .role-section .col-left {
  color: #D9E0EE;
  font-size: 1em;
  padding-top: 4px;
}
.preferences-wrap .category-mapper-container .category-picker-dropdown .btn .category-item {
  font-size: 1em;
}
.preferences-wrap .category-mapper-container .category-picker-dropdown .btn .category-item img.content-mask {
  background-color: #C9CBFF;
}
.preferences-wrap .category-mapper-container .category-picker-dropdown .btn .category-item + img {
  width: 20px;
  height: 12px;
  background-image: none;
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-image: url(../static/images/dropdown-menu/dropdown-chevron@2x.png);
  -webkit-mask-size: 20px 12px;
  -webkit-mask-position: 0px 0px;
  background-color: #C9CBFF;
  padding-top: 12px;
}
.preferences-wrap .category-mapper-container .category-picker-dropdown:active .category-item img.content-mask {
  background-color: #1A1826;
}
.preferences-wrap .category-mapper-container .category-picker-dropdown:active .category-item + img {
  background-color: #1A1826;
}
.preferences-signatures-container .signature-wrap .account-picker {
  color: rgba(217, 224, 238, 0.5);
}
.preferences-signatures-container .signature-wrap .button-dropdown .primary-item,
.preferences-signatures-container .signature-wrap .button-dropdown .only-item {
  box-shadow: none;
  background: #302D41;
  border: 1px solid #161320;
  color: #C9CBFF;
  height: 2.1em;
  font-size: 1em;
  margin-left: 6px;
}
.preferences-signatures-container .signature-wrap .button-dropdown .primary-item img.content-mask,
.preferences-signatures-container .signature-wrap .button-dropdown .only-item img.content-mask {
  background-color: #C9CBFF;
}
.preferences-signatures-container .signature-wrap .button-dropdown .primary-item:hover,
.preferences-signatures-container .signature-wrap .button-dropdown .only-item:hover {
  background: #575268;
  border: 1px solid #161320;
}
.preferences-signatures-container .signature-wrap .button-dropdown .primary-item:active,
.preferences-signatures-container .signature-wrap .button-dropdown .only-item:active {
  background: #C9CBFF;
  border: 1px solid #1A1826;
  color: #1A1826;
}
.preferences-signatures-container .signature-wrap .button-dropdown .primary-item:active img.content-mask,
.preferences-signatures-container .signature-wrap .button-dropdown .only-item:active img.content-mask {
  background-color: #1A1826;
}
.preferences-signatures-container .signature-wrap .section-header {
  font-weight: 700;
  margin-top: 24px;
  margin-bottom: 12px;
}
.preferences-signatures-container .signature-wrap .section {
  background-color: #222234;
  border: 1px solid #161320;
}
.preferences-signatures-container .signature-wrap .section.preview {
  background-color: #1A1826;
  border-radius: 3px 3px 0 0;
  color: #D9E0EE;
}
.preferences-signatures-container .signature-wrap .section.preview .label {
  color: rgba(217, 224, 238, 0.5);
}
.preferences-signatures-container .signature-wrap .section.preview a {
  color: #89DCEB;
}
.preferences-signatures-container .signature-wrap .section.preview + .section {
  border-radius: 0 0 3px 3px;
  border-top-color: #222234;
}
.preferences-signatures-container .signature-wrap .section.information {
  border: none;
  padding: 0;
}
.preferences-signatures-container .signature-wrap .section.information .field label {
  color: rgba(217, 224, 238, 0.8);
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-well {
  border: 1px solid #161320;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-well:hover {
  border: 1px solid #C9CBFF;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .drop-note {
  color: rgba(217, 224, 238, 0.5);
  font-size: 0.9em;
}
.preferences-signatures-container .signature-wrap .section.information .field.photo-picker .photo-options a:hover {
  color: #C9CBFF;
}
.preferences-signatures-container .signature-wrap .signature-template-picker {
  background: #222234;
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option {
  background: #1A1826;
  border: 1px solid #161320;
  border-radius: 3px;
}
.preferences-signatures-container .signature-wrap .signature-template-picker .option.active {
  border: 1px solid #C9CBFF;
}
.preferences-signatures-container .signature-wrap textarea.raw-html {
  border-radius: 3px;
}
.preferences-signatures-container .signature-wrap textarea.raw-html:focus {
  border: 1px solid #C9CBFF;
  box-shadow: inset 1px 0 #C9CBFF, inset -1px 0 #C9CBFF, inset 0 1px #C9CBFF, inset 0 -1px #C9CBFF;
}
.preferences-templates-container a {
  cursor: pointer;
  color: #89DCEB;
  font-size: 1em !important;
  text-decoration: underline;
}
.preferences-templates-container .template-wrap .RichEditor-root {
  background: #1A1826;
  border: 1px solid #161320;
  border-radius: 3px;
}
.preferences-templates-container .template-wrap .RichEditor-root.focused {
  border: 1px solid #C9CBFF;
  box-shadow: inset 1px 0 #C9CBFF, inset -1px 0 #C9CBFF, inset 0 1px #C9CBFF, inset 0 -1px #C9CBFF;
}
.preferences-templates-container .template-wrap .RichEditor-root .RichEditor-content {
  color: #D9E0EE;
}
.preferences-templates-container .template-wrap .RichEditor-root .template-variable {
  background-color: rgba(201, 203, 255, 0.5);
  border-bottom: 1px solid #C9CBFF;
  color: #D9E0EE;
}
.preferences-templates-container .template-wrap .RichEditor-root .template-variable.selected {
  border: 1px solid #C9CBFF;
}
.preferences-templates-container .template-wrap .section.note {
  color: rgba(217, 224, 238, 0.5);
  font-size: 1em;
}
.column-Preferences .scrollbar-track {
  background: transparent;
  border-left: 1px solid transparent;
  border-right: 1px solid transparent;
  width: 12px;
  padding: 3px 0px 3px 6px;
}
.column-Preferences .scrollbar-track .scrollbar-handle {
  background: #575268;
  border: none;
  border-radius: 3px;
  opacity: 0.4;
}
.column-Preferences .scrollbar-track:hover,
.column-Preferences .scrollbar-track.dragging {
  background: #302D41;
  border-left: 1px solid #161320;
  padding-left: 3px;
  padding-right: 2px;
}
.column-Preferences .scrollbar-track:hover .scrollbar-handle,
.column-Preferences .scrollbar-track.dragging .scrollbar-handle {
  opacity: 0.99;
}
.column-Preferences .scrollbar-track:hover:not(.dragging) .scrollbar-handle {
  background: #6E6C7E;
}
.column-Preferences .scrollbar-track:hover:not(.dragging) .scrollbar-handle:hover {
  background: #988BA2;
}
.column-Preferences .scrollbar-track.dragging .scrollbar-handle {
  background: #C9CBFF;
}
