css:
.sheet-toolbar {
  border-bottom: 1px solid #2c2c2c;
  box-shadow: none;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  filter: invert(100%);
}
img.content-dark {
  filter: invert(100%);
}
img.content-light {
  filter: invert(100%);
}
.mail-label {
  filter: contrast(110%) brightness(85%);
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: 0 0 0.5px rgba(255, 255, 255, 0.28), 0 1px 1.5px rgba(255, 255, 255, 0.08);
}
.thread-list .list-item:not(.unread):not(.selected):not(.focused) {
  color: #aaa;
}
.thread-list .list-item.thread-list-reminder-item {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread {
  background-color: #312856;
}
.thread-list .list-item.thread-list-reminder-item.unread:not(.focused):not(.selected) {
  background-color: #312856;
}
.send-reminders-header {
  color: #7d5bef;
  background: #312856;
  border-bottom: 1px solid #4d3b8a;
}
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(44, 44, 44, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(44, 44, 44, 0.8);
}
.template-picker .content-container {
  height: 150px;
  width: 210px;
  overflow-y: scroll;
}
.template-picker .footer-container {
  border-top: 1px solid #2c2c2c;
}
.template-status-bar {
  background-color: #fcf4db;
  color: #695209;
  border: 1.5px solid #f8e6ac;
  border-radius: 2px;
  padding: 4px 10px 4px 10px;
  font-size: 13.02px;
  margin-bottom: 10px;
  text-align: center;
}
.RichEditor-root .template-variable {
  padding: 0;
  padding-left: 2px;
  padding-right: 2px;
  border: 1px solid transparent;
  border-bottom: 1px solid #c79b11;
  background-color: #fcf4db;
  color: #98760d;
}
.RichEditor-root .template-variable.selected {
  border: 1px solid #c79b11;
}
.preferences-templates-container {
  max-width: 800px;
  margin: 0 auto;
}
.preferences-templates-container .template-list {
  position: relative;
  height: inherit;
  width: inherit;
}
.preferences-templates-container .template-list .items-wrapper {
  min-width: 200px;
  height: 262px;
}
.preferences-templates-container .template-list .editable-item {
  padding: 8px 10px;
  border-bottom: none;
}
.preferences-templates-container .template-list .btn-editable-list {
  height: 37px;
  width: 37px;
  line-height: 37px;
  font-size: 1em;
}
.preferences-templates-container .template-wrap {
  display: flex;
  position: relative;
  margin-left: 12px;
  flex-direction: column;
  min-height: 200px;
  width: 100%;
}
.preferences-templates-container .template-wrap.empty {
  opacity: 0.5;
  pointer-events: none;
}
.preferences-templates-container .template-wrap .section {
  margin-bottom: 10px;
}
.preferences-templates-container .template-wrap .section.editor {
  display: flex;
  flex: 1;
  margin-bottom: 0;
}
.preferences-templates-container .template-wrap .section.note {
  font-size: 0.9em;
  margin-top: 8px;
  color: rgba(238, 238, 238, 0.6);
}
.preferences-templates-container .template-wrap textarea.raw-html {
  font-family: 'DejaVu Sans Mono', Menlo, Monaco, Consolas, 'Courier New', monospace;
  font-size: 0.9em;
  width: 100%;
  height: 240px;
  padding: 12px;
  flex: 1;
  background: #212121;
  border: 1px solid #2c2c2c;
}
.preferences-templates-container .template-wrap .RichEditor-root {
  flex: 1;
  background: #212121;
  padding: 10px;
  padding-top: 2px;
  border: 1px solid #2c2c2c;
}
