css:
/**
 * Colors
 */
/**
 * Borders
 */
#account-switcher .primary-item .name {
  color: #697278;
}
.account-sidebar-sections {
  background-color: #ffffff !important;
}
.account-sidebar-sections section:first-child .heading {
  padding-right: 40px;
}
.account-sidebar-sections section .heading {
  padding-bottom: 5px;
}
.account-sidebar-sections section .heading .text {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
}
.account-sidebar-sections section .item-container {
  margin: 0 10px 0 0 !important;
}
.account-sidebar-sections section .item-container .disclosure-triangle {
  display: flex;
  align-items: center;
  width: 15px;
}
.account-sidebar-sections section .item-container .disclosure-triangle div {
  border-left-color: #e6e6e6;
  border-top-width: 3px;
  border-bottom-width: 3px;
  border-left-width: 5px;
  transform-origin: 2px;
}
.account-sidebar-sections section .item-container .item {
  padding: 0 10px !important;
  color: #87939a !important;
  cursor: pointer !important;
}
.account-sidebar-sections section .item-container .item .item-count-box {
  background: transparent !important;
  color: #87939a !important;
  box-shadow: 0 0.5px 0 #87939a, 0 -0.5px 0 #87939a, 0.5px 0 0 #87939a, -0.5px 0 0 #87939a !important;
}
.account-sidebar-sections section .item-container .item.selected {
  background: #5dade1 !important;
  border-radius: 4px;
  color: #ffffff !important;
}
.account-sidebar-sections section .item-container .item.selected .item-count-box {
  background: transparent !important;
  color: #ffffff !important;
  box-shadow: 0 0.5px 0 #ffffff, 0 -0.5px 0 #ffffff, 0.5px 0 0 #ffffff, -0.5px 0 0 #ffffff !important;
}
.account-sidebar-sections section .item-container .item.selected .icon img {
  background: #ffffff !important;
}
