css:
/* The Onboarding window should never adopt theme styles. This re-assigns UI
variables and resets commonly overridden styles to ensure the onboarding window
always looks good. Previously we tried to make the theme just not load in the
window, but it uses a hot window which makes that difficult now. */
h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: 'Nylas-Pro', 'Helvetica', sans-serif;
  line-height: 1.1;
  color: #434648;
}
h1 small,
h2 small,
h3 small,
h4 small,
h5 small,
h6 small,
h1 .small,
h2 .small,
h3 .small,
h4 .small,
h5 .small,
h6 .small {
  line-height: 1;
}
h1 {
  font-size: 23.94px;
  font-weight: 600;
}
h2 {
  font-size: 23.94px;
  font-weight: 300;
}
h3 {
  font-size: 20.02px;
  font-weight: 300;
}
h4 {
  font-size: 18.06px;
}
h5 {
  font-size: 14px;
}
h6 {
  font-size: 12.04px;
}
h1,
h2,
h3 {
  margin-top: 21px;
  margin-bottom: 10.5px;
}
h1 small,
h2 small,
h3 small,
h1 .small,
h2 .small,
h3 .small {
  font-size: 65%;
}
h4,
h5,
h6 {
  margin-top: 10.5px;
  margin-bottom: 10.5px;
}
h4 small,
h5 small,
h6 small,
h4 .small,
h5 .small,
h6 .small {
  font-size: 75%;
}
.btn {
  padding: 0 0.8em;
  border-radius: 3px;
  border: 0;
  cursor: default;
  display: inline-block;
  color: #231f20;
  background: #ffffff;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 0.5px 1px rgba(0, 0, 0, 0.15);
  height: 1.9em;
  line-height: 1.9em;
  font-size: 13.02px;
}
.btn img.content-mask {
  background-color: #231f20;
}
.btn .text {
  margin-left: 6px;
}
.btn:active {
  cursor: default;
  background: #e7e7e7;
}
.btn:focus {
  outline: none;
}
.btn.btn-small {
  font-size: 12.04px;
}
.btn.btn-large {
  font-size: 14px;
  padding: 0 1.3em;
  line-height: 2.2em;
  height: 2.3em;
}
.btn.btn-larger {
  font-size: 15.96px;
  padding: 0 1.6em;
}
.btn.btn-disabled {
  color: rgba(35, 31, 32, 0.6);
  background: rgba(254, 254, 254, 0.85);
}
.btn.btn-disabled:active {
  background: rgba(254, 254, 254, 0.85);
}
.btn.btn-emphasis {
  position: relative;
  color: #ffffff;
  font-weight: 500;
  background: linear-gradient(to bottom, #6bb1f9 0%, #0a80ff 100%);
  box-shadow: none;
  border: 1px solid #3878fa;
}
.btn.btn-emphasis img.content-mask {
  background-color: #ffffff;
}
.btn.btn-emphasis.btn-disabled {
  opacity: 0.4;
}
.btn.btn-emphasis:before {
  content: ' ';
  width: calc(100% + 2px);
  height: calc(100% + 2px);
  border-radius: 4px;
  top: -1px;
  left: -1px;
  position: absolute;
  z-index: -1;
  background: linear-gradient(to bottom, #4ca2f9 0%, #015cff 100%);
}
.btn.btn-emphasis:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#296efa), to(#4783fb));
}
