css:
html,
body {
  margin: 0;
  height: 100%;
  width: 100%;
  overflow: hidden;
  -webkit-font-smoothing: antialiased;
}
.theme-option {
  position: absolute;
  top: 0;
  margin-top: 4px;
  margin-left: 5px;
  width: 100px;
  height: 60px;
  background-color: #f6f6f6;
  color: #231f20;
  border-radius: 5px;
  text-align: center;
  overflow: hidden;
}
.theme-option.active-true {
  border: 1px solid #3187e1;
  box-shadow: 0 0 4px #9ecaed;
}
.theme-option.active-false {
  border: 1px solid #dddddd;
}
.theme-option .theme-name {
  font-family: 'Nylas-Pro', 'Helvetica', sans-serif;
  font-size: 12px;
  font-weight: 600;
  margin-top: 7px;
  height: 18px;
  overflow: hidden;
}
.theme-option .swatches {
  padding-left: 27px;
  padding-right: 27px;
  display: flex;
  flex-direction: row;
}
.theme-option .swatches .swatch {
  flex: 1;
  height: 10px;
  width: 10px;
  margin: 4px 2px 4px 2px;
  border-radius: 2px;
  border: 1px solid rgba(0, 0, 0, 0.15);
  background-clip: border-box;
  background-origin: border-box;
}
.theme-option .swatches .swatch.font-color {
  background-color: #231f20;
}
.theme-option .swatches .swatch.active-color {
  background-color: #3187e1;
}
.theme-option .swatches .swatch.toolbar-color {
  background-color: #d2d2d2;
}
.theme-option .divider-black {
  position: absolute;
  bottom: 12px;
  height: 1px;
  width: 100%;
  background-color: black;
  opacity: 0.15;
}
.theme-option .divider-white {
  position: absolute;
  z-index: 10;
  bottom: 11px;
  height: 1px;
  width: 100%;
  background-color: white;
  opacity: 0.15;
}
.theme-option .strip {
  position: absolute;
  bottom: 0;
  height: 12px;
  width: 100%;
  background-color: #f6f7f8;
}
