css:
/*
Include the standard variables from Mailspring's base theme:
https://github.com/Foundry376/Mailspring/blob/master/app/static/base/ui-variables.less#L37:L40
*/
.autoload-images-header {
  background-color: #534a2f;
  border: 1px solid #776423;
  border-radius: 3px;
  color: rgba(253, 218, 86, 0.92);
  margin: 5px 0;
  padding: 5px 12px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.autoload-images-header .option {
  color: rgba(250, 236, 181, 0.7);
}
.autoload-images-header .option:hover {
  color: rgba(182, 171, 134, 0.88);
}
