css:
/*
Include the standard variables from Mailspring's base theme:
https://github.com/Foundry376/Mailspring/blob/master/app/static/base/ui-variables.less#L37:L40
*/
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(62, 65, 83, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(62, 65, 83, 0.8);
}
.event-wrapper {
  cursor: default;
  position: relative;
  font-size: 13.02px;
  margin-top: 14px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  width: 100%;
  display: inline-block;
  border-radius: 3px;
  border: 1px solid #3e4153;
}
.event-wrapper .event-header {
  border-bottom: 1px solid #3e4153;
  padding: 10px;
  overflow: hidden;
  text-overflow: ellipsis;
}
.event-wrapper .event-header img {
  margin-right: 20px;
}
.event-wrapper .event-header .event-title-text {
  color: rgba(248, 248, 242, 0.6);
}
.event-wrapper .event-header .event-title {
  color: #f8f8f2;
}
.event-wrapper .event-header .event-download {
  float: right;
  border-radius: 3px;
  border: 1px solid #3e4153;
  padding: 0 4px;
}
.event-wrapper .event-header .event-download img {
  background: rgba(248, 248, 242, 0.6);
  margin: 0;
}
.event-wrapper .event-body {
  padding: 10px;
  color: #f8f8f2;
}
.event-wrapper .event-body .event-location {
  user-select: text;
}
.event-wrapper .event-body .event-date {
  display: inline-block;
  width: 100%;
}
.event-wrapper .event-body .event-date .event-day {
  display: block;
  font-size: 15.96px;
  color: #e64d65;
}
.event-wrapper .event-body .event-date .event-time {
  display: inline-block;
  font-size: 20.02px;
  font-weight: 300;
}
.event-wrapper .event-body .event-actions {
  display: flex;
}
.event-wrapper .event-body .event-actions .btn-rsvp {
  margin-right: 10.5px;
  margin-top: 5px;
  min-width: 85px;
  text-align: center;
}
.event-wrapper .event-body .event-actions .btn-rsvp:hover {
  background: #32343f;
}
.event-wrapper .event-body .event-actions .btn-rsvp:active {
  background: #393a45;
}
.event-wrapper .event-body .event-actions .btn-rsvp.DECLINED {
  background: #d9534f;
  color: #282a36;
}
.event-wrapper .event-body .event-actions .btn-rsvp.ACCEPTED {
  background: #5cb346;
  color: #282a36;
}
.event-wrapper .event-body .event-actions .btn-rsvp.TENTATIVE {
  background: #a4a458;
  color: #282a36;
}
body.platform-win32 .event-wrapper {
  border-radius: 0;
}
body.platform-win32 .event-wrapper .event-body .event-actions .btn-rsvp {
  border-radius: 0;
}
