css:
/*
Include the standard variables from Mailspring's base theme:
https://github.com/Foundry376/Mailspring/blob/master/app/static/base/ui-variables.less#L37:L40
*/
.send-reminders-popover .section.send-reminders-footer .reminders-label {
  color: rgba(216, 222, 233, 0.6);
  font-size: 0.8em;
  min-height: 36px;
}
.send-reminders-popover .section.send-reminders-footer .reminders-label .reminder-date {
  font-weight: bold;
}
.send-reminders-popover .section.send-reminders-footer .btn-cancel {
  width: 100%;
  margin-top: 5px;
}
.send-reminders-toolbar-button {
  order: -102;
}
.thread-list .list-item.focused .timestamp.send-reminders-thread-timestamp {
  color: #fff;
}
.thread-list .list-item.focused .timestamp.send-reminders-thread-timestamp img {
  background-color: #fff;
}
.thread-list .timestamp.send-reminders-thread-timestamp {
  opacity: 0.82;
  color: #979797;
}
.thread-list .timestamp.send-reminders-thread-timestamp img {
  background-color: #979797;
  margin-top: -3px;
  padding-right: 6px;
}
.send-reminders-header {
  color: #5a31e1;
  background: linear-gradient(to bottom, #eee8ff 0%, #fff 25%);
  padding: 13px 0 10px 20px;
  margin: -19px -20px 15px -20px;
  border-bottom: 1px solid #e7e1fb;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  cursor: default;
}
.send-reminders-header img {
  background-color: #5a31e1;
  margin-top: -6px;
  margin-right: 10px;
}
.send-reminders-header ~ .header-toggle-control {
  top: 57px !important;
}
.message-list-headers .send-reminders-header {
  padding-left: 15px;
  margin-top: 0;
}
.message-list-headers .send-reminders-header .reminder-date {
  font-weight: bold;
}
.message-list-headers .send-reminders-header .clear-reminder {
  position: absolute;
  right: 18px;
  cursor: pointer;
  text-decoration: underline;
}
.thread-list .list-item.thread-list-reminder-item {
  background-color: #f2efff;
}
.thread-list .list-item.thread-list-reminder-item.unread {
  background-color: #f2efff;
}
.thread-list .list-item.thread-list-reminder-item.unread:not(.focused):not(.selected) {
  background-color: #f2efff;
}
.thread-list .list-item.thread-list-reminder-item.focused,
.thread-list .list-item.thread-list-reminder-item.selected {
  background: #6fa9a8;
}
.thread-list .list-item.thread-list-reminder-item .thread-icon-reminder-triggered {
  margin-top: 1px;
  background-image: url(../static/images/thread-list/icon-reminder@2x.png);
}
.thread-list .list-item .thread-icon-reminder-pending {
  margin-top: 1px;
  background-image: url(../static/images/thread-list/icon-reminder-outline@2x.png);
}
